INSERT INTO `prescript` VALUES (59974, '14672', '第一竹沥汤', '竹沥5升，甘草1两，秦艽1两，葛根1两，黄芩1两，麻黄1两，防已1两，细辛1两，桂心1两，干姜1两，防风1两半，升麻1两半，茯苓2两，附子2枚，杏仁50枚。', '小竹沥汤（《圣惠》卷四十五）、竹沥汤（《圣济总录》卷四十一）。《千金翼》本方有白术一两，无茯苓、杏仁。', '《千金》卷七', '脚气。两脚痹弱，或转筋，皮肉不仁，腹胀起如肿，按之不陷，心中恶不饮食，或患冷。', '-', '-', '升麻', '[\'竹沥\', \'甘草\', \'秦艽\', \'葛根\', \'黄芩\', \'麻黄\', \'两防\', \'细辛\', \'两桂心\', \'干姜\', \'防风\', \'两半\', \'升麻\', \'两半\', \'茯苓\', \'附子\', \'杏仁\']');
INSERT INTO `prescript` VALUES (59975, '14632', '金不换膏', '栀子1两，防风1两，良姜1两，海风藤1两，灵仙1两，牛膝1两，熟地1两，桃仁1两，柴胡1两，白鲜皮1两，全虫1两，枳壳1两，白芷1两，甘草1两，黄连1两，细辛1两，白芍1两，玄参1两，猪苓1两，前胡1两，麻黄1两，桔梗1两，僵蚕1两，升麻1两，地丁1两，大黄1两，木通1两，橘皮1两，川乌1两，生地1两，香附1两，双花1两，知母1两，薄荷1两，当归1两，杜仲1两，白术1两，泽泻1两，青皮1两，黄柏1两，杏仁1两，黄芩1两，穿山甲1两，蒺藜1两，天麻1两，苦参1两，乌药1两，羌活1两，半夏1两，茵陈1两，浙贝', '随证按穴摊贴之。', '《全国中药成药处方集》（沈阳方）', '舒筋通络，驱风散寒，调经止痛。主腰痛瘫痿，关节疼痛，麻痹不仁，心腹诸痛，男子遗精，女子带下，虚冷泄泻，月经崩漏，疟疾，疝气，偏正头痛，寒湿脚气。', '-', '-', '升麻', '[\'栀子\', \'防风\', \'两良\', \'海风藤\', \'两灵仙\', \'牛膝\', \'熟地\', \'桃仁\', \'柴胡\', \'白鲜皮\', \'两全\', \'枳壳\', \'白芷\', \'甘草\', \'黄连\', \'细辛\', \'白芍\', \'玄参\', \'猪苓\', \'两前\', \'胡两\', \'麻黄\', \'桔梗\', \'僵蚕\', \'升麻\', \'两地\', \'大黄\', \'木通\', \'橘皮\', \'川乌\', \'生地\', \'香附\', \'两双\', \'知母\', \'薄荷\', \'当归\', \'杜仲\', \'白术\', \'泽泻\', \'青皮\', \'黄柏\', \'杏仁\', \'黄芩\', \'穿山甲\', \'蒺藜\', \'天麻\', \'苦参\', \'乌药\', \'羌活\', \'半夏\', \'茵陈\', \'两浙\']');
INSERT INTO `prescript` VALUES (59976, '14611', '除风湿羌活汤', '羌活7分，防风5分，升麻5分，柴胡5分，藁本1钱，苍术1钱。', '除湿羌活汤（《回春》卷二）、除湿汤（《金鉴》卷三十九）', '《内外伤辨》卷中', '湿从外受，或一身尽痛，或头重如蒙，甚而昏冒。风湿相搏，湿因外中，得之于天阴淫雨，晴后湿蒸，早晨雾露，及久卧湿地，远行涉水，瘴气山岚，其证头身重痛，甚而昏冒，大便溏薄，皮肤浮肿。湿似疸（熏黄黑晦一症，若兼一身尽痛，乃是湿病，非疸，当从湿治），霍乱因湿，及因干湿四肢重着，骨节烦病，胸膈滞闷。风湿上冲，头重如裹，似有物蒙之，兼有热者。', '-', '-', '升麻', '[\'羌活\', \'防风\', \'升麻\', \'柴胡\', \'藁本\', \'苍术\']');
INSERT INTO `prescript` VALUES (59977, '14522', '柴胡汤', '柴胡（去苗）1两，升麻1两半，车前子1两，决明子（微炒）1两，栀子仁1两，黄芩（去黑心）1两，黄连（去须）1两，甘草（炙，锉）1两，防风（去叉）1两，羚羊角（镑）1两，马牙消1两。', '每服5钱匕，水1盏半，煎至8分，去滓，食后、临卧服。', '《圣济总录》卷一○四', '肝虚风热上冲，目暗赤痛。', '-', '-', '升麻', '[\'柴胡\', \'升麻\', \'两半\', \'车前子\', \'决明子\', \'栀子\', \'黄芩\', \'黑心\', \'黄连\', \'甘草\', \'防风\', \'羚羊角\', \'两马牙\', \'消两\']');
INSERT INTO `prescript` VALUES (59978, '14484', '凉血清肠散', '生地黄1钱5分，当归1钱5分，芍药1钱5分，防风1钱，升麻1钱，荆芥1钱，黄芩8分，黄连8分，香附8分，川芎8分，甘草5分。', '水煎服。', '《观聚方要补》卷六引《医学统旨》', '大肠热甚脱肛。肠风下血。', '-', '-', '升麻', '[\'生地黄\', \'钱分\', \'当归\', \'钱分\', \'芍药\', \'钱分\', \'防风\', \'升麻\', \'荆芥\', \'黄芩\', \'黄连\', \'香附\', \'川芎\', \'甘草\']');
INSERT INTO `prescript` VALUES (59979, '14483', '麻仁汤', '麻子仁2合半（炒），升麻3两，豉2两，射干1分半，大黄（锉，炒）1分半，甘草（炙，锉）1分半，陈橘皮（汤浸去白，炒）1两。', '每服5钱匕，以水1盏半，加生姜1枣大（拍破），煎至8分，去滓，下芒消末半钱匕，更煎1-2沸，空腹温服。', '《圣济总录》卷八十四', '脚气，大肠结涩不通。', '-', '-', '升麻', '[\'麻子\', \'仁合半\', \'升麻\', \'两豉\', \'射干\', \'分半\', \'大黄\', \'分半\', \'甘草\', \'分半\', \'橘皮\', \'汤浸\']');
INSERT INTO `prescript` VALUES (59980, '14474', '黄芩散', '黄芩1两，栀子仁1两，黄连（去须）1两，葳蕤1两，川升麻1两，蕤仁（汤浸，去赤皮）1两，甘草（炙微赤，锉）1两。', '黄芩汤（《圣济总录》卷一一○）。', '《圣惠》卷三十三', '斑痘疮入眼，口干心烦。', '-', '-', '升麻', '[\'黄芩\', \'栀子\', \'黄连\', \'葳蕤\', \'两川\', \'升麻\', \'蕤仁\', \'汤浸\', \'赤皮\', \'甘草\', \'炙微\']');
INSERT INTO `prescript` VALUES (59981, '14462', '大黄散', '川大黄1两（锉碎，微炒），黄连1两（去须），黄芩3分，川升麻3分，黄药3分，甘草半两（生，锉）。', '每服4钱，以水1中盏，加黑豆30粒，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十一', '伤寒心脾热，舌肿。', '-', '-', '升麻', '[\'大黄\', \'黄连\', \'两去\', \'黄芩\', \'分川\', \'升麻\', \'黄药分\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (59982, '14459', '黄连散', '黄连3分（去须），大青3分，川升麻3分，桑根白皮半两（锉），甘草半两（炒微赤，锉）。', '每服1钱，以水1小盏，煎至5分，去滓，放温，量儿大小，分减服之。若与奶母服，即加栀子、黄芩各半两，每服3钱，以水1中盏，煎至6分，去滓，食后温服。', '《圣惠》卷九十', '小儿口疮，心热烦闷。', '-', '-', '升麻', '[\'黄连\', \'分去\', \'大青\', \'分川\', \'升麻\', \'分桑根\', \'白皮\', \'半两\', \'甘草\', \'半两\', \'炒微\']');
INSERT INTO `prescript` VALUES (59983, '14438', '大黄丸', '川大黄半两（炙碎，微炒），栀子仁半两，黄耆半两（锉），川升麻半两，川朴消半两，黄连半两（去须），生干地黄半两，玄参半两，磁石1两（烧醋淬7遍，捣碎，细研，水飞过）。', '每服30丸，食后以温水送下。以利为度。', '《圣惠》卷三十六', '风热毒气攻耳，暴聋，由肾气实热所致。', '-', '-', '升麻', '[\'大黄\', \'半两\', \'炙碎\', \'栀子\', \'半两\', \'黄耆\', \'半两\', \'锉川\', \'升麻\', \'半两\', \'川朴消\', \'半两\', \'黄连\', \'半两\', \'须生\', \'地黄\', \'半两\', \'玄参\', \'半两\', \'磁石\', \'两烧\', \'淬遍\', \'捣碎\', \'细研水\', \'飞过\']');
INSERT INTO `prescript` VALUES (59984, '14378', '固经膏', '全当归3两，丹皮（酒炒）2两，柴胡2两，酒芍2两，生地2两，黄芩2两，知母2两，麦冬2两，地骨皮2两，川芎2两，贝母2两，黄连2两，羌活1两，防风1两，连翘1两，薄荷1两，蔓荆子1两，紫苏1两，独活1两，藁本1两，细辛1两，丹参1两，党参1两，黄耆1两，熟地1两，元参1两，白术1两，天冬1两，赤芍1两，白薇1两，苍术1两，萸肉1两，淮山药1两，枳壳1两，桔梗1两，麦芽1两，郁金1两，贯众1两，青皮1两，陈皮1两，半夏1两，胆南星1两，白芷1两，升麻1两，葛根1两，黄柏1两，黑山栀1两，生甘草1两，熟牛膝1', '外用，上贴心口，中贴脐眼，下贴丹田，或兼贴对脐两腰。', '《理瀹》', '举经固经，补阴清火。主妇人血虚有热，月经先期，或经行过多，先后不定，或经行不止，或崩中，或漏下，或湿热带下，或五旬后经行者。', '-', '-', '升麻', '[\'当归\', \'丹皮\', \'柴胡\', \'两酒\', \'生地\', \'黄芩\', \'知母\', \'麦冬\', \'地骨皮\', \'川芎\', \'贝母\', \'黄连\', \'羌活\', \'防风\', \'连翘\', \'薄荷\', \'蔓荆子\', \'紫苏\', \'独活\', \'藁本\', \'细辛\', \'丹参\', \'党参\', \'两黄耆\', \'熟地\', \'两元\', \'参两\', \'白术\', \'两天\', \'冬两\', \'赤芍\', \'两白薇\', \'苍术\', \'两萸\', \'两淮\', \'山药\', \'枳壳\', \'桔梗\', \'麦芽\', \'郁金\', \'贯众\', \'青皮\', \'陈皮\', \'半夏\', \'胆南星\', \'白芷\', \'升麻\', \'葛根\', \'黄柏\', \'黑山\', \'栀两\', \'生甘草\', \'两熟\', \'牛膝\']');
INSERT INTO `prescript` VALUES (59985, '14329', '茯苓散', '茯苓8分，麦门冬8分，通草8分，升麻8分，紫菀1钱，知母1钱，桂心4分，赤石脂、淡竹叶10片，大枣2个。', '水2盏，煎8分，温服。', '《医统》卷二十', '心经实热，口干烦渴，眠卧不安。', '-', '-', '升麻', '[\'茯苓\', \'麦门冬\', \'通草\', \'升麻\', \'紫菀\', \'知母\', \'钱桂心分\', \'赤石脂\', \'淡竹叶\', \'大枣\']');
INSERT INTO `prescript` VALUES (59986, '14239', '麻黄汤', '麻黄（去节）3两，升麻3两，大黄3两，黄芩3两，石膏3两，甘草1两（炙），栀子仁3合。', '以水9升，煮取3升，分服之。愈。', '《普济方》卷二六一', '乳石发，上冲头面及身体壮热，服升麻汤内解外不解者。', '-', '-', '升麻', '[\'麻黄\', \'去节\', \'升麻\', \'大黄\', \'黄芩\', \'石膏\', \'甘草\', \'栀子\', \'仁合\']');
INSERT INTO `prescript` VALUES (59987, '14237', '大青散', '大青半两，川大黄半两（锉，微炒）1分，牛黄半分（细研）1分，朱砂（细研）1分，甘草（炙微赤，锉）1分，犀角屑1分，玄参1分，川升麻1分，栀子仁1分。', '每服半钱，以沙糖水调下，不拘时候。', '《圣惠》卷八十三', '小儿气壅烦热，心躁目赤。', '-', '-', '升麻', '[\'大青\', \'半两\', \'大黄\', \'半两\', \'锉微\', \'炒分\', \'牛黄\', \'半分\', \'细研分\', \'朱砂\', \'细研分\', \'甘草\', \'炙微\', \'犀角\', \'玄参\', \'分川\', \'升麻\', \'栀子\', \'仁分\']');
INSERT INTO `prescript` VALUES (59988, '14235', '度瘴散', '麻黄（去节）1两，升麻1两，附子（炮，去皮）1两，白术1两，细辛2分，干姜2分，防己2分，防风2分，桂心2分，乌头（炮，去皮）2分，蜀椒（汗）2分，桔梗2分。', '山中所在有瘴气之处，旦空腹饮服1钱匕，覆取汗。病重稍加之。', '《千金翼》卷十', '瘴气。', '-', '-', '升麻', '[\'麻黄\', \'去节\', \'升麻\', \'附子\', \'去皮\', \'白术\', \'细辛\', \'干姜\', \'防己\', \'防风\', \'分桂心分\', \'乌头\', \'去皮\', \'分蜀椒\', \'汗分\', \'桔梗\']');
INSERT INTO `prescript` VALUES (59989, '14213', '浣肌散', '枫香（别研）1两，荆芥穗1两，大黄2两，苦参2两，当归2两，升麻2两，白蒺藜2两，枳壳（去瓤，炒）2两，射干1两半。', '方中枫香，《普济方》引作“槐花”。', '《杨氏家藏方》卷十二', '风热客博，皮肤瘙痒，隐疹(疒咅)(疒畾)，疮疡疥癣，抓之出水，浸淫不止，或风气游走暴肿。荨麻疹。', '-', '-', '升麻', '[\'枫香\', \'别研\', \'荆芥\', \'大黄\', \'苦参\', \'当归\', \'升麻\', \'两白\', \'蒺藜\', \'枳壳\', \'射干\', \'两半\']');
INSERT INTO `prescript` VALUES (59990, '14190', '草灵宝丹', '川芎4两，天麻（酒浸，去苗）4两，当归（洗）4两，白芍药4两，细辛（去土叶）4两，荆芥穗4两，川楝子肉（炒）4两，麻黄（去根节）4两，五加皮4两，白鲜皮4两，何首乌（酒浸）4两，自然铜（火烧7遍，醋浸7遍）4两，菊花4两，枳壳（炒，去瓤）4两，白术（炒）4两，薄荷叶4两，石斛（去根，炒）4两，威灵仙（去土）4两，枸杞子4两，木香4两，川乌头（炮，去皮脐尖）4两，甘草（炒）4两，附子（炮，去皮脐）4两，草乌头（炮，去皮脐尖）4两，香附子（炒）4两，车前子（酒浸）4两，金毛狗脊（去毛）4两，没药（别研）4两，', '每服半丸或1丸，热酒化下，不拘时候。', '《杨氏家藏方》卷一', '中风，及八风五痹，瘫痪亸曳，口眼斜，眉角牵引，项背拘强，牙关紧急，心中悸闷，神情如醉，遍身发热，骨节烦疼，肌肉麻木，腰膝沉重，皮肤瞤动，状若虫行；阳虚头痛，风寒入脑，目旋晕转，似在舟船，耳内蝉鸣，有如风雨之声应；风寒湿痹，脚气缓急，及打扑伤于筋骨，或遇天明，一身尽痛，不得睡卧。', '-', '-', '升麻', '[\'川芎\', \'天麻\', \'酒浸\', \'当归\', \'两白\', \'芍药\', \'细辛\', \'土叶\', \'荆芥\', \'川楝子\', \'麻黄\', \'根节\', \'五加皮\', \'白鲜皮\', \'何首乌\', \'酒浸\', \'自然铜\', \'火烧\', \'遍醋\', \'浸遍\', \'菊花\', \'枳壳\', \'白术\', \'薄荷叶\', \'石斛\', \'威灵仙\', \'枸杞子\', \'木香\', \'两川\', \'乌头\', \'去皮\', \'脐尖\', \'甘草\', \'附子\', \'去皮\', \'两草\', \'乌头\', \'去皮\', \'脐尖\', \'香附子\', \'车前子\', \'酒浸\', \'金毛狗\', \'脊去\', \'没药\', \'别研\']');
INSERT INTO `prescript` VALUES (59991, '14153', '和气平胃散', '厚朴5钱，黄连5钱，猪苓5钱，泽泻5钱，地榆5钱，苍术3钱，升麻1钱5分，豆蔻1钱5分，白芍药3钱，陈皮4钱，柴胡1钱5分，甘草1钱。', '《女科旨要》有白术。', '《女科万金方》', '安胎和气。主妇人胎前8-9个月，胎儿长发以致胎母脾胃虚弱而不调和，湿热相攻，五脏六腑不和，或变痢疾、杂患之病。', '-', '-', '升麻', '[\'厚朴\', \'黄连\', \'猪苓\', \'泽泻\', \'钱地\', \'榆钱\', \'苍术\', \'升麻\', \'钱分\', \'豆蔻\', \'分白\', \'芍药\', \'陈皮\', \'柴胡\', \'钱分\', \'甘草\']');
INSERT INTO `prescript` VALUES (59992, '14140', '柴胡散', '柴胡1两（去苗），葛根半两（锉），赤茯苓半分，麦门冬3分（去心），石膏2两，葳蕤3分，甘草半两（炙微赤，锉），玄参3分（去芦头），川升麻3分，黄芩半两，犀角屑1两，川芒消1两。', '每服4钱，以水1中盏，加生姜半分，豉100粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十五', '因服乳石，至脏腑壅滞，及脚气欲发，或憎寒壮热，头痛心烦，眼目昏闷，头旋欲吐，不纳饮食。', '-', '-', '升麻', '[\'柴胡\', \'两去\', \'葛根\', \'半两\', \'茯苓\', \'半分\', \'麦门冬\', \'分去\', \'石膏\', \'葳蕤\', \'甘草\', \'半两\', \'炙微\', \'玄参\', \'分去\', \'芦头川\', \'升麻\', \'黄芩\', \'半两\', \'犀角\', \'两川芒\', \'消两\']');
INSERT INTO `prescript` VALUES (59993, '13975', '羚羊角饮', '羚羊角3分（镑），菊花3分，羌活（去芦头）3分，漏芦半两（去芦头），胡黄连半两，玄参半两，升麻半两。', '每服5钱匕，以水1盏，煎至8分，去滓，食后温服，每日2次。', '《圣济总录》卷一○八', '眼不见物。', '-', '-', '升麻', '[\'羚羊角\', \'分镑\', \'菊花\', \'羌活\', \'芦头分\', \'漏芦\', \'半两\', \'芦头\', \'胡黄连\', \'半两\', \'玄参\', \'半两\', \'升麻\', \'半两\']');
INSERT INTO `prescript` VALUES (59994, '13952', '救生散', '山豆根1两，北大黄半两，川升麻半两，朴消（生）半两。', '每1粒以薄绵包，少痛，便含咽液。', '《普济方》卷六十二', '时疾头面肿，咽喉肿塞，气息难通。', '-', '-', '升麻', '[\'山豆根\', \'北大\', \'半两\', \'升麻\', \'半两\', \'朴消生\', \'半两\']');
INSERT INTO `prescript` VALUES (59995, '13916', '钩藤子芩汤', '钩藤3分，黄芩3分，沙参3分，知母（焙）1两，升麻1两，犀角（镑）1两，蚱蝉2枚（炙，去翅头足），蛇蜕皮3寸（炙），柴胡（去苗）半两，甘草（炙，锉）半两，白术半两。', '以水2升，煎取6合，去滓，入蜜2合，竹沥3合，再炼如饧，每服1钱匕，微微与服。', '《圣济总录》卷一七一', '小儿惊痫体虚者。', '-', '-', '升麻', '[\'钩藤\', \'黄芩\', \'沙参\', \'知母\', \'升麻\', \'犀角\', \'蚱蝉\', \'头足\', \'蜕皮\', \'柴胡\', \'去苗\', \'半两\', \'甘草\', \'半两\', \'白术\', \'半两\']');
INSERT INTO `prescript` VALUES (59996, '13835', '大黄散', '川大黄1两（锉碎，微炒），栀子仁1两，川升麻1两，黄芩1两，甘草1两（生，锉），玄参1两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '方出《圣惠》卷六十二，名见《圣济总录》卷一三一', '发背初欲作肿。', '-', '-', '升麻', '[\'大黄\', \'栀子\', \'仁两川\', \'升麻\', \'黄芩\', \'甘草\', \'两生\', \'玄参\']');
INSERT INTO `prescript` VALUES (59997, '13770', '钩藤皮汤', '钩藤皮2分，麻黄（去节）2分，龙齿6分（研，绵裹），银1斤，寒水石12分，栀子（劈）12分，知母12分，石膏（碎，绵裹）12分，杏仁（去两仁皮尖，研）12分，升麻10分，子芩14分，蛇蜕皮7寸（炙），蚱蝉4枚（去足翅，炙），柴胡10分，芍药8分，沙参8分，生葛汁5分，蜜7合，牡牛黄（如大豆粒）10枚（煎成研下之）。', '钩藤饮（原书卷三七七）若大便涩者，加大黄10分。', '《普济方》卷一○○', '救急疗痫。主小儿癫痫。', '慎热面、炙肉、鱼、蒜、粘食、油腻、冷水。', '-', '升麻', '[\'钩藤\', \'皮分\', \'麻黄\', \'节分\', \'龙齿\', \'分研\', \'绵裹\', \'银斤\', \'寒水石\', \'栀子\', \'劈分\', \'知母\', \'石膏\', \'碎绵裹\', \'杏仁\', \'两仁\', \'皮尖\', \'研分\', \'升麻\', \'分子\', \'分蛇\', \'蜕皮\', \'蚱蝉\', \'柴胡\', \'芍药\', \'沙参\', \'分生\', \'葛汁\', \'分蜜合\', \'牡牛\', \'黄如大\', \'豆粒\', \'煎成\', \'研下\']');
INSERT INTO `prescript` VALUES (59998, '13765', '凉血益气汤', '人参1钱，黄耆1钱，生地黄8分，川芎8分，升麻5分，条黄芩5分，枳壳5分，槐角5分，当归8分，黄连3分。', '上（口父）咀。水煎熟，空心服。', '《杏苑》卷八', '凉血益气。主气血虚而痔疾疼痛。', '-', '-', '升麻', '[\'人参\', \'黄耆\', \'生地黄\', \'川芎\', \'升麻\', \'分条\', \'黄芩\', \'枳壳\', \'槐角\', \'当归\', \'黄连\']');
INSERT INTO `prescript` VALUES (59999, '13691', '柴胡汤', '柴胡（去苗）1两，枳壳（去瓤，麸炒）1两，升麻1两，黄连（去须）1两，麻黄（去根节）1两半，甘草（炙，锉）3分，知母（切，焙）3分，栀子仁3分。', '此证先烙上脘穴，次烙关元，及灸十八壮，若不愈，再灸七壮，次烙背心下廉及天聪，如不愈者宜服本方。', '《圣济总录》卷六十一', '心黄。面赤口张，气喘多惊，饶睡，手脚烦疼，舌上疮生，心下急闷，不欲饮食，舌缩口干，7-8日内必发狂走。', '-', '-', '升麻', '[\'柴胡\', \'枳壳\', \'升麻\', \'黄连\', \'麻黄\', \'根节\', \'两半\', \'甘草\', \'知母\', \'切焙分\', \'栀子\', \'仁分\']');
INSERT INTO `prescript` VALUES (60000, '13670', '柴胡清肝散', '柴胡1钱，黄芩（炒）1钱，黄连（炒）7分，山栀（炒）7分，当归1钱，川芎6分，生地黄1钱，牡丹皮1钱，升麻8分，甘草3分。', '柴胡清肝饮（《证治汇补》卷四）。脾胃弱，去芩、连，加芩、术。', '《明医杂着》卷六', '肝气郁结，风热外侵，颈项结核，肿痛不消，或有寒热；肝热犯脾，口唇肿裂；妇人肝火内盛，月经光期而量多者。', '-', '-', '升麻', '[\'柴胡\', \'黄芩\', \'黄连\', \'分山\', \'炒分\', \'当归\', \'川芎\', \'生地黄\', \'牡丹皮\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (60001, '13523', '胡麻散', '胡麻子（赤色扁者佳，另研）5两，白芷2两，何首乌2两，防风2两，蔓荆子1两5钱，甘菊花1两，升麻2两，威灵仙2两，苦参（酒炒）3两，川当归2两，川芎2两（酒炒），牛蒡子2两（微炒，另研），白蒺藜3两，荆芥穗2两，薄荷叶2两，片黄芩2两，白芍（酒炒）2两，黄连（酒炮1日炒）2两。', '每服3钱，食远服。秋分后至春分，白酒调服；春分后至秋分，清茶调服。或用米糊细为丸，食远白汤下亦可。', '《寿世保元》卷九', '紫白癜风并癣，及面上酒渣，又名粉渣面刺。', '-', '-', '升麻', '[\'胡麻\', \'赤色\', \'扁者\', \'研两\', \'白芷\', \'何首乌\', \'防风\', \'蔓荆子\', \'甘菊花\', \'升麻\', \'威灵仙\', \'苦参\', \'两川\', \'当归\', \'川芎\', \'两酒\', \'牛蒡子\', \'两微\', \'研白\', \'蒺藜\', \'荆芥\', \'薄荷叶\', \'两片\', \'黄芩\', \'白芍\', \'黄连\', \'酒炮\']');
INSERT INTO `prescript` VALUES (60002, '13520', '枇杷叶散', '枇杷叶1分（拭去毛，炙微黄），川升麻半两，人参半两（去芦头），茅根1两（锉），竹茹3分，贝母半两（煨微黄）。', '每服1钱，以水1小盏，加大枣1个（擘），生姜少许，煎至5分，去滓温服，不拘时候。', '《圣惠》卷八十四', '小儿伤寒壮热，咳嗽呕吐。', '-', '-', '升麻', '[\'枇杷叶\', \'拭去\', \'微黄\', \'升麻\', \'半两\', \'人参\', \'半两\', \'芦头\', \'茅根\', \'竹茹\', \'贝母\', \'半两\', \'微黄\']');
INSERT INTO `prescript` VALUES (60003, '13464', '茯苓汤', '白茯苓（去黑皮）3两，紫苏叶3两，杏仁（去皮尖双仁，炒）3两，升麻3两，陈橘皮（汤浸，去白，焙）3两，柴胡（去苗）3两，槟榔（锉）12枚，犀角（镑）3两，栀子仁3两。', '每服3钱匕，水1盏，加生姜1枣大（拍碎），同煎至6分，去滓，空心温服，晚食前再服。', '《圣济总录》卷八十三', '脚气肿满，气急上气，心闷烦热，呕逆不下食。', '-', '-', '升麻', '[\'白茯苓\', \'黑皮\', \'紫苏叶\', \'杏仁\', \'皮尖\', \'双仁\', \'升麻\', \'两陈\', \'橘皮\', \'汤浸\', \'柴胡\', \'槟榔\', \'犀角\', \'栀子\']');
INSERT INTO `prescript` VALUES (60004, '13453', '黄连汤', '甘草（炙）1两，升麻1两，黄连3两，豉5合，栀子仁14枚。', '以水3升，煮取1升，分温服', '《外台》卷三十八', '解散除热止痢。主乳石发后变下痢。', '-', '-', '升麻', '[\'甘草\', \'升麻\', \'黄连\', \'两豉\', \'栀子\', \'仁枚\']');
INSERT INTO `prescript` VALUES (60005, '13435', '钩藤煎', '钩藤1分，子芩半两，知母半两，寒水石3分，川升麻半两，沙参1两（去芦头），蚱蝉2枚（去翅足，微炒），蜣螂2枚（去翅足，炒微黄），甘草半两（炙微赤，锉）。', '每以熟水调1杏仁大服。日3次。', '《圣惠》卷八十五', '小儿惊痫，体热羸瘦。', '-', '-', '升麻', '[\'钩藤\', \'分子\', \'半两\', \'知母\', \'半两\', \'寒水石\', \'分川\', \'升麻\', \'半两\', \'沙参\', \'两去\', \'芦头\', \'蚱蝉\', \'足微\', \'蜣螂\', \'微黄\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60006, '13412', '柴胡散', '柴胡1两（去苗），川升麻3分，黄芩3分，知母1两，赤芍药1两，大青3分，石膏2两，栀子仁1两，川大黄1两（锉碎，微炒），杏仁3分（汤浸，去皮尖双仁，麸炒微黄）。', '每服4钱，以水1中盏，加生姜半分，豉50粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷九', '伤寒6日，头痛壮热，百节疼痛。', '-', '-', '升麻', '[\'柴胡\', \'两去\', \'苗川\', \'升麻\', \'黄芩\', \'知母\', \'两赤\', \'芍药\', \'两大青分\', \'石膏\', \'栀子\', \'仁两川\', \'大黄\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (60007, '13407', '救土通肠汤', '玄参2两，当归1两，生地1两，知母1钱，厚朴1钱，升麻5分，大麻子30粒。', '水煎服。2剂大便必通，减去大麻子、知母，再用4剂。', '《辨证录》卷九', '脾火作祟，大便闭结，口干唇裂，食不能消，腹痛难忍，按之益痛，小便短涩。', '-', '-', '升麻', '[\'玄参\', \'当归\', \'生地\', \'知母\', \'厚朴\', \'升麻\', \'大麻子\']');
INSERT INTO `prescript` VALUES (60008, '13385', '黄连散', '黄连1分（去须），大青2分，川升麻1分，赤茯苓1分，人参1分（去芦头），甘草1分（炙微赤，锉），麦门冬半两（去心，焙），黄芩1分，地骨皮1分，犀角屑半分。', '每服1钱，以水1盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷八十四', '小儿伤寒，得汗利后，余热不除，心神烦躁，夜卧不安。', '-', '-', '升麻', '[\'黄连\', \'分去\', \'大青\', \'分川\', \'升麻\', \'分赤\', \'茯苓\', \'人参\', \'分去\', \'芦头\', \'甘草\', \'分炙微\', \'麦门冬\', \'半两\', \'黄芩\', \'地骨皮\', \'犀角\', \'半分\']');
INSERT INTO `prescript` VALUES (60009, '13380', '钩藤汤', '钩藤1两，使君子（去皮）1分，干蝎（炒）1分，人参1分，子芩1分，川大黄（锉碎，微炒）1分，犀角屑1分，蚱蝉3枚（微炙），甘草半两（炙微赤，锉），川升麻半两，石膏半两。', '每服1钱匕，以水1小盏，煎至5分，去滓，加竹沥半合，牛黄末1字服之。', '《圣济总录》卷一六八', '小儿风热，惊疳潮热。', '-', '-', '升麻', '[\'钩藤\', \'使君子\', \'去皮\', \'分干\', \'炒分\', \'人参\', \'分子\', \'分川\', \'大黄\', \'碎微\', \'炒分\', \'犀角\', \'蚱蝉\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'升麻\', \'半两\', \'石膏\', \'半两\']');
INSERT INTO `prescript` VALUES (60010, '13372', '羚羊角豉汤', '豉1升半，犀角屑1两，羚羊角屑1两，芍药3两，升麻4两，杏仁1两（去皮尖），栀子7枚，甘草（炙）1两。', '以水7升，煮取1升半，去滓，分3次服。', '《外台》卷二十三引《古今录验》', '喉痛肿结，毒气冲心胸。', '忌海藻、菘菜。', '-', '升麻', '[\'升半\', \'犀角\', \'羚羊角\', \'芍药\', \'升麻\', \'杏仁\', \'两去\', \'皮尖\', \'栀子\', \'甘草\']');
INSERT INTO `prescript` VALUES (60011, '13335', '柴胡汤', '柴胡（去苗）1两，地骨皮1两，犀角（镑）1两，麦门冬（去心，焙）1两，葛根（锉）1两，黄连（去须）1两，赤芍药1两，黄芩（去黑心）1两，升麻1两，甘草（炙，锉）半两。', '每服3钱匕，水1盏，煎至7分，去滓，食后温服。', '《圣济总录》卷四十三', '心脏实热，上焦壅滞，口舌生疮，或多烦渴。', '-', '-', '升麻', '[\'柴胡\', \'地骨皮\', \'犀角\', \'麦门冬\', \'葛根\', \'黄连\', \'芍药\', \'黄芩\', \'黑心\', \'升麻\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60012, '13320', '柴胡泽泻汤', '柴胡2两，泽泻2两，橘皮（一方用桔梗）2两，黄芩2两，枳实2两，旋覆花2两，升麻2两，芒消2两，生地黄（切）1升。', '上(口父)咀。以水1斗，煮取3升，去滓，下芒消，分3服。', '《千金》卷十四', '小肠热胀，口疮。', '-', '-', '升麻', '[\'柴胡\', \'泽泻\', \'橘皮\', \'一方\', \'桔梗\', \'黄芩\', \'枳实\', \'旋覆花\', \'升麻\', \'两芒\', \'消两\', \'生地黄\', \'切升\']');
INSERT INTO `prescript` VALUES (60013, '13312', '黑膏', '乌头、川芎、雄黄、胡粉、木防己、升麻、黄连、雌黄、藜芦、明矾5钱，杏仁（去皮尖）40粒，巴豆40枚，黄柏1钱8分，松脂1团，乳发1团。', '方中乌头以下九味药用量原缺。', '《理瀹》', '遍体生疮，脓血溃坏。', '-', '-', '升麻', '[\'乌头\', \'川芎\', \'雄黄\', \'胡粉\', \'防己\', \'升麻\', \'黄连\', \'雌黄\', \'藜芦\', \'明矾\', \'杏仁\', \'皮尖\', \'巴豆\', \'黄柏\', \'钱分\', \'松脂\', \'团乳\', \'发团\']');
INSERT INTO `prescript` VALUES (60014, '13296', '黄耆汤', '生地黄8两，竹叶（切）3升，小麦2升，黄耆3两，黄芩3两，前胡3两，大黄3两，瓜篓4两，通草2两，芍药2两，升麻2两，茯苓2两，甘草2两，知母2两，人参1两，当归1两。', '上先以水2斗，煮竹叶及小麦，取1斗2升，去滓，复煮诸药，取4升，分4次服，日3夜1。小便利，除茯苓、通草，加麦门冬；腹满，加石膏3两；热盛，去人参、当归。', '《鬼遗》卷三', '痈疽内虚，热，渴甚。', '-', '-', '升麻', '[\'生地黄\', \'竹叶\', \'切升\', \'小麦\', \'黄耆\', \'黄芩\', \'两前\', \'胡两\', \'大黄\', \'两瓜篓\', \'通草\', \'芍药\', \'升麻\', \'茯苓\', \'甘草\', \'知母\', \'人参\', \'当归\']');
INSERT INTO `prescript` VALUES (60015, '13258', '黄柏汤', '黄柏（去粗皮，蜜炙）半两，大青半两，龙胆半两，玄参（坚者）半两，生干地黄半两，升麻1两，射干3分（去毛）。', '每服5钱匕，水1盏半，加竹叶3-7片，煎至8分，去滓，入蜜1合搅匀，食后含化咽津。', '《圣济总录》卷三十', '伤寒后心脏热，舌裂口生疮。', '-', '-', '升麻', '[\'黄柏\', \'粗皮蜜\', \'半两\', \'大青\', \'半两\', \'龙胆\', \'半两\', \'玄参\', \'坚者\', \'半两\', \'生干\', \'地黄\', \'半两\', \'升麻\', \'射干\', \'分去\']');
INSERT INTO `prescript` VALUES (60016, '13243', '钩藤散', '钩藤1分，蚱蝉2枚（微炒，去翅足），川升麻半两，子芩半两，麦门冬半两（去心，焙），蛇蜕皮5寸（烧灰），川大黄半两（锉碎，微炒），石膏2两，甘草半两（炙微赤，锉）。', '每服1钱，以水1小盏，煎至5分，去滓，入竹沥半合服之。', '《圣惠》卷八十五', '小儿风壅气盛，心胸痰滞，壮热发痫。', '-', '-', '升麻', '[\'钩藤\', \'蚱蝉\', \'足川\', \'升麻\', \'半两\', \'子芩\', \'半两\', \'麦门冬\', \'半两\', \'蜕皮\', \'烧灰\', \'大黄\', \'半两\', \'石膏\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60017, '13241', '葛根散', '葛根1两（锉），甘草半两（生锉），黄耆1两（锉），川升麻1两，栝楼根1两，麦门冬1两（去心），赤芍药1两，黄芩3分，栀子仁1两，生干地黄1两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十一', '痈肿。热盛口干，烦渴，或时干呕。', '-', '-', '升麻', '[\'葛根\', \'甘草\', \'半两\', \'黄耆\', \'锉川\', \'升麻\', \'栝楼\', \'根两\', \'麦门冬\', \'两去\', \'芍药\', \'黄芩\', \'栀子\', \'仁两生\', \'地黄\']');
INSERT INTO `prescript` VALUES (60018, '13157', '柴胡汤', '柴胡（去苗）1两，麻黄（去根节）1两半，升麻1两，桂（去粗皮）3分，大黄（锉，炒）1两，甘草（炙）3分，鳖甲（醋炙，去裙襕）1两1分，枳实（去瓤，麸炒）3分，知母（焙）3分，栀子仁1分。', '每服5钱匕，水1盏半，加生姜3片，煎至7分，去滓温服。', '《圣济总录》卷二十二', '时行疫疠，数日未得汗，浑身壮热，呕逆不下食。', '-', '-', '升麻', '[\'柴胡\', \'麻黄\', \'根节\', \'两半\', \'升麻\', \'两桂去\', \'粗皮分\', \'大黄\', \'甘草\', \'炙分\', \'鳖甲\', \'两分\', \'枳实\', \'炒分\', \'知母\', \'焙分\', \'栀子\', \'仁分\']');
INSERT INTO `prescript` VALUES (60019, '13135', '调元解毒汤', '人参8分，生耆1钱，当归1钱，连翘1钱，牛蒡1钱，防风5分，川芎5分，升麻5分，黄芩5分，黄连5分，前胡1钱，木通8分，炙草3分，虫退3分。', '煎服。', '《种痘新书》卷四', '补中兼解毒。主痘疮大热灼人，心中迷闷，元气虚而毒气盛者。', '-', '-', '升麻', '[\'人参\', \'分生\', \'当归\', \'连翘\', \'牛蒡\', \'防风\', \'川芎\', \'升麻\', \'黄芩\', \'黄连\', \'分前\', \'胡钱\', \'木通\', \'分炙草\', \'分虫\', \'退分\']');
INSERT INTO `prescript` VALUES (60020, '13090', '大青散', '大青1两，知母1两，黄芩（去黑心）1两，大黄（煨）1两，山栀子仁1两，升麻1两，黄连（去须）1两，甘草（炙，锉）半两。', '每服3钱匕，入朴消1钱匕，用蜜水调下。', '《圣济总录》卷二十一', '阳盛发狂有斑，大小便秘涩。', '-', '-', '升麻', '[\'大青\', \'知母\', \'黄芩\', \'黑心\', \'大黄\', \'两山\', \'栀子\', \'升麻\', \'黄连\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60021, '13087', '柴胡洗眼汤', '柴胡（去苗）1两，蕤仁（研）1两，黄连（去须）1两，川升麻1两，玄参1两。', '以水3大盏，煎取1大盏半，滤去滓，微热淋洗，不勒度数，冷即重暖用之。', '《圣惠》卷三十二', '丹石毒上攻，眼目赤痛，微肿，眦烂。', '-', '-', '升麻', '[\'柴胡\', \'蕤仁\', \'研两\', \'黄连\', \'两川\', \'升麻\', \'玄参\']');
INSERT INTO `prescript` VALUES (60022, '13069', '大垂云膏', '当归1两，附子（去皮脐，生用）1两，芎1两，防风1两，川升麻1两，槐子1两，细辛（去苗）1两，侧柏叶1两，桃仁（汤浸，去皮尖双仁）1分，杏仁（汤浸，去皮尖双仁）1分，甘草1分，桑根白皮1分，白及1分，黄耆1分，白僵蚕1分，垂柳1握（煎了不在吊），黄丹7两，雄黄半两，朱砂1分（细研），硫黄2分（细研），麝香1钱（细研），白芷1分，没药1分，麒麟竭1分（细研），龙脑1分（细研），黄蜡4两（细研），油1斤半。', '发背疮，热酒调1钱服，外贴之。余症外贴，虎豹咬着，用甘草水洗后贴之。', '《圣惠》卷六十三', '一切恶疮焮肿，发背，疽疮，风肿，肠痈，乳痈，瘰疬，疥癣，发鬓，牙痈，发脑，肾痈，马坠磕破骨损、及一切虫蛇毒物咬伤。', '-', '-', '升麻', '[\'当归\', \'附子\', \'去皮\', \'脐生用\', \'两芎\', \'防风\', \'两川\', \'升麻\', \'两槐子\', \'细辛\', \'侧柏叶\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁分\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁分\', \'甘草\', \'分桑根\', \'白皮\', \'分白及\', \'黄耆\', \'白僵蚕\', \'垂柳\', \'黄丹\', \'雄黄\', \'半两\', \'朱砂\', \'分细研\', \'硫黄\', \'分细研\', \'麝香\', \'钱细研\', \'白芷\', \'没药\', \'麒麟\', \'竭分\', \'细研\', \'龙脑\', \'分细研\', \'黄蜡\', \'两细\', \'研油\', \'斤半\']');
INSERT INTO `prescript` VALUES (60023, '13011', '麻风膏', '麻黄5钱，羌活2两，升麻2钱，白檀香1钱，白及1钱，防风1钱，归身1钱。', '抹擦。', '《内外科百病验方大全》', '面上或身上风热浮肿，痒如虫行，肌肤干燥，时起白屑，时发极痒，抓破时硫黄水，或破烂见血，痛楚难堪。', '-', '-', '升麻', '[\'麻黄\', \'羌活\', \'升麻\', \'钱白\', \'檀香\', \'钱白及\', \'防风\', \'钱归身\']');
INSERT INTO `prescript` VALUES (60024, '12998', '大黄散', '川大黄（锉碎，微炒）1两，赤芍药1两，川升麻1两，鬼箭羽1两，鬼臼（去根）1两，桂心1两，桔梗（去芦头）1两，柴胡（去苗）1两，川朴消2两。', '每服3钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十三', '中恶心痛，腹胀闷乱。', '-', '-', '升麻', '[\'大黄\', \'芍药\', \'两川\', \'升麻\', \'鬼箭羽\', \'鬼臼\', \'两桂心\', \'桔梗\', \'芦头\', \'柴胡\', \'去苗\', \'两川朴\', \'消两\']');
INSERT INTO `prescript` VALUES (60025, '12960', '柴胡饮', '柴胡（去苗）1两，桑上寄生1两，知母（切，焙）1两，百合（洗）1两，麦门冬（去心，焙）1两，升麻1两，甜竹茹（新竹刮用）3两。', '每服5钱匕，水1盏半，加生姜3片，同煎至1盏，去滓，食后温服。', '《圣济总录》卷一五六', '妊娠虚烦懊热，胎气不宁，手足烦倦。', '-', '-', '升麻', '[\'柴胡\', \'去苗\', \'两桑上\', \'寄生\', \'知母\', \'切焙\', \'百合\', \'麦门冬\', \'升麻\', \'两甜\', \'竹茹\', \'新竹\', \'刮用\']');
INSERT INTO `prescript` VALUES (60026, '12925', '黄连散', '黄连（去须）半两，川升麻半两，黄芩半两，犀角屑半两，川大黄（锉碎，微炒）半两，麦门冬（去心，焙）半两，甘草（炙微赤，锉）半两，茯神3分。', '每服半钱，以竹沥调下1日3-4次。', '《圣惠》卷八十三', '小儿心热，夜卧狂语，烦渴。', '-', '-', '升麻', '[\'黄连\', \'半两\', \'升麻\', \'半两\', \'黄芩\', \'半两\', \'犀角\', \'半两\', \'大黄\', \'半两\', \'麦门冬\', \'半两\', \'甘草\', \'炙微\', \'半两\', \'茯神\']');
INSERT INTO `prescript` VALUES (60027, '12816', '柴胡汤', '柴胡（去苗）半两，芍药半两，知母（焙）半两，桃仁（汤浸，去皮尖双仁，炒黄）半两，木香半两，山栀子仁半两，升麻半两，大青半两，白芷半两，黄芩（去黑心）半两，细辛（去苗叶）1分，甘草（炙，锉）1分，石膏1两。', '每服5钱匕，水1盏半，加生姜半分（拍碎），同煎至7分，去滓，食后温服，日3次。', '《圣济总录》卷三十一', '伤寒后百节疼痛，壮热头疼。', '-', '-', '升麻', '[\'柴胡\', \'去苗\', \'半两\', \'芍药\', \'半两\', \'知母\', \'半两\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁\', \'半两\', \'木香\', \'半两\', \'栀子\', \'半两\', \'升麻\', \'半两\', \'大青\', \'半两\', \'白芷\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'细辛\', \'苗叶分\', \'甘草\', \'石膏\']');
INSERT INTO `prescript` VALUES (60028, '12800', '济阳逐火汤', '胆草2钱，升麻2钱，柴胡1钱，甘草2钱，生石膏2钱（引）。', '此方服后，头痛甚者，切不可畏，火燥则炀，随服必平，须记之。', '《点点经》卷一', '头脑作痛，不拘左右；胃火牙痛。', '-', '-', '升麻', '[\'胆草\', \'升麻\', \'柴胡\', \'甘草\', \'生石膏\', \'钱引\']');
INSERT INTO `prescript` VALUES (60029, '12774', '麻子小豆汤', '麻子5升，赤小豆5升，生商陆2升，升麻4两，附子2两，射干3两。', '每服5合，日2次，夜1次，并食豆。当利小便为度，肿退即愈。', '《千金》卷二十二', '毒肿无定处，毒气深重，或赤色恶寒，或心腹刺痛烦闷。', '-', '-', '升麻', '[\'麻子\', \'赤小豆\', \'升生\', \'商陆\', \'升麻\', \'附子\', \'射干\']');
INSERT INTO `prescript` VALUES (60030, '12745', '柴胡汤', '柴胡（去苗）1两，升麻3分，芍药3分，麦门冬（去心，焙）3分，甘草3分，黄芩（去黑心）3分，知母（焙）5钱，黄连5钱。', '方中知母、黄连用量原缺，据《普济方》补。', '《圣济总录》卷三十', '伤寒热病后，乍寒乍热，骨节痛，口舌生疮。', '-', '-', '升麻', '[\'柴胡\', \'升麻\', \'芍药\', \'麦门冬\', \'心焙分\', \'甘草\', \'黄芩\', \'黑心\', \'知母\', \'黄连\']');
INSERT INTO `prescript` VALUES (60031, '12731', '柴胡散', '柴胡（去苗）2两，赤芍药2两，知母2两，栀子仁2两，川升麻3分，黄芩3分，大青3分，杏仁（汤浸，去皮尖双仁，麸炒微黄）3分，甘草（炙微赤，锉）3分，石膏4两，川大黄3分（锉碎，微炒）。', '每服4钱，以水1中盏，加豉50粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒谵语，头痛壮热，百骨节疼痛。', '-', '-', '升麻', '[\'柴胡\', \'芍药\', \'知母\', \'栀子\', \'仁两川\', \'升麻\', \'黄芩\', \'分大青分\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'甘草\', \'炙微\', \'石膏\', \'两川\', \'大黄\']');
INSERT INTO `prescript` VALUES (60032, '12704', '黄耆散', '黄耆（去芦，蒸出，擘破，于槐砧上碎锉）1两，甘草半两（炙），柴胡1两（去芦，以布拭去土净，锉，勿犯铁器），人参1两，秦艽1两（须是于脚下左交裂者，以布拭却毛），川升麻半两，山栀子1两（如雀脑者，去皮，以甘草水浸1宿，焙用），黄芩1两，地骨皮半两，茯苓（赤者，以水中澄去浮者，炒用）1两。', '每服3钱，水1盏，同煎至6分，去滓，食后温服。', '《博济》卷二', '肺脏壅塞咳嗽，涕唾稠粘，咽喉不利。', '-', '-', '升麻', '[\'黄耆\', \'芦蒸\', \'出擘\', \'破于\', \'甘草\', \'半两\', \'柴胡\', \'两去\', \'芦以布\', \'拭去\', \'土净\', \'铁器\', \'人参\', \'秦艽\', \'两须\', \'脚下\', \'左交裂者\', \'以布\', \'毛川\', \'升麻\', \'半两\', \'栀子\', \'两如雀\', \'脑者\', \'去皮\', \'甘草\', \'水浸\', \'宿焙用\', \'黄芩\', \'地骨皮\', \'半两\', \'茯苓\', \'赤者\', \'水中\', \'澄去\', \'浮者\', \'炒用\']');
INSERT INTO `prescript` VALUES (60033, '12688', '麻黄汤', '前胡5钱，柴胡（去毛）5钱，石膏5钱，苍术（锉，炒）5钱，藁本5钱，赤芍药5钱，白芷5钱，土芎5钱，干葛5钱，升麻5钱，麻黄3钱。', '每服4钱，加生姜3片，连须葱2根，水煎服，不拘时候。春加黄芩，夏用正方，秋加麻黄，冬加豆豉。', '《得效》卷十五', '发散四时伤寒。主四时伤寒，潮热头痛，及时疫。', '-', '-', '升麻', '[\'胡钱\', \'柴胡\', \'毛钱\', \'石膏\', \'苍术\', \'藁本\', \'芍药\', \'白芷\', \'钱土\', \'干葛钱\', \'升麻\', \'麻黄\']');
INSERT INTO `prescript` VALUES (60034, '12583', '柴葛安胎饮', '柴胡8分，葛根8分，青黛8分，石膏1钱5分，升麻5分，栀子1钱，知母7分，葱白3根。', '水煎服。有痰，加竹沥1小杯，姜汁2匙。', '《胎产心法》卷上', '孕妇热病，骨节疼痛。', '-', '-', '升麻', '[\'柴胡\', \'葛根\', \'分青黛分\', \'石膏\', \'钱分\', \'升麻\', \'栀子\', \'知母\', \'葱白\']');
INSERT INTO `prescript` VALUES (60035, '12569', '独活防风汤', '麻黄（去节）1两，防风1两，独活1两，桂心半两，羚羊角屑半两，升麻半两，甘草半两，酸枣仁半两，秦艽半两，川芎7分，当归7分，杏仁（制）7分。', '上（口父）咀。每服4钱，加生姜4片，竹沥1合，水煎服。', '《云岐子保命集》卷十三', '妊娠中风，角弓反张，口噤语涩，谓之风痉，亦名子痫。', '-', '-', '升麻', '[\'麻黄\', \'去节\', \'防风\', \'独活\', \'两桂心\', \'半两\', \'羚羊角\', \'半两\', \'升麻\', \'半两\', \'甘草\', \'半两\', \'酸枣仁\', \'半两\', \'秦艽\', \'半两\', \'川芎\', \'当归\', \'杏仁\', \'制分\']');
INSERT INTO `prescript` VALUES (60036, '12550', '淋浇黄连汤', '黄连1两，地骨皮1两，羌活1两，防风1两（去芦头），木通1两，甘草1两，白芷1两，川大黄1两，狼牙1两，川升麻1两，莽草1两，藁本1两，黄耆1两（锉），赤芍药1两，细辛1两，桑根白皮1两，黄芩1两，白矾1两，葱白1两，麻黄1两。', '黄连汤（《普济方》卷二八九）。', '《圣惠》卷六十一', '发背痈疽，穿穴时久，坏烂，恶气不可近，出骨露筋，余毒未解，攻刺疼痛不可忍。', '-', '-', '升麻', '[\'黄连\', \'地骨皮\', \'羌活\', \'防风\', \'两去\', \'芦头\', \'木通\', \'甘草\', \'白芷\', \'两川\', \'大黄\', \'两狼牙\', \'两川\', \'升麻\', \'莽草\', \'藁本\', \'两黄耆\', \'芍药\', \'细辛\', \'两桑根\', \'白皮\', \'黄芩\', \'白矾\', \'葱白\', \'麻黄\']');
INSERT INTO `prescript` VALUES (60037, '12464', '茯苓桂枝参甘耆附麻黄汤', '人参3钱，茯苓3钱，黄耆3钱，紫苏3钱，桂枝2钱，附子2钱，甘草1钱，升麻1钱。', '流水煎，温服。', '《医学金针》卷八', '痘疹痒塌黑陷者。', '-', '-', '升麻', '[\'人参\', \'茯苓\', \'黄耆\', \'紫苏\', \'桂枝\', \'附子\', \'甘草\', \'升麻\']');
INSERT INTO `prescript` VALUES (60038, '12460', '羚羊角汤', '羚羊角（镑）1两，射干3分，络石3分（碎），大黄3分（锉），升麻3分，木通1两半（锉），芍药1两半，生地黄（切，焙）2两。', '每服5钱匕，以水1盏半，煎至8分，去滓，下芒消末1钱匕，搅匀温服。得利即愈。', '《圣济总录》卷一二二', '脾热，喉中肿痛，热塞不通。', '-', '-', '升麻', '[\'羚羊角\', \'射干\', \'络石\', \'分碎\', \'大黄\', \'升麻\', \'木通\', \'两半\', \'芍药\', \'两半\', \'生地黄\', \'切焙\']');
INSERT INTO `prescript` VALUES (60039, '12440', '常山散', '常山1两，鳖甲（涂醋，炙令黄，去裙襕）1两，升麻1两，赤茯苓（去黑皮）1两，栀子仁1两，人参1两。', '每服2钱匕，温水调服，不拘时候。', '《圣济总录》卷三十七', '疟病。身黄发热，小便不利。', '-', '-', '升麻', '[\'常山\', \'鳖甲\', \'涂醋\', \'炙令\', \'黄去\', \'升麻\', \'两赤\', \'茯苓\', \'黑皮\', \'栀子\', \'人参\']');
INSERT INTO `prescript` VALUES (60040, '12419', '羚羊角散', '木通半两（锉），防风半两（去芦头），川升麻半两，羚羊角屑半两，桂心半两，甘草（炙）1分。', '每服1钱，以水1小盏，煎至5分，去滓，入竹沥少许，更煎1-2沸，加减服之，不拘时候。', '《普济方》卷三七八', '小儿痫愈，不能语。', '-', '-', '升麻', '[\'木通\', \'半两\', \'防风\', \'半两\', \'芦头川\', \'升麻\', \'半两\', \'羚羊角\', \'半两\', \'桂心\', \'半两\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (60041, '12342', '茯神散', '茯神半两，川升麻半两，犀角屑半两，代赭（细研）1分，钩藤1分，川大黄（锉碎，微炒）1分。', '每服1钱，以水1小盏，煎至4分，去滓放温，渐渐服之。', '《普济方》卷三八三', '小儿疳积针后，宣腹压惊。主', '-', '-', '升麻', '[\'茯神\', \'半两\', \'升麻\', \'半两\', \'犀角\', \'两代\', \'赭细\', \'研分\', \'钩藤\', \'分川\', \'大黄\', \'碎微\', \'炒分\']');
INSERT INTO `prescript` VALUES (60042, '12319', '固齿擦牙散', '青盐、寒水石，白蒺藜8两，羊胫骨，地骨皮4两，香附8两，熟地黄4两，生地4两，骨碎补8两，蕲艾茸、石燕、升麻，皂角4两，槐树头49个，桑树头49个，杨柳头（清明者）49个。', '方中青盐、寒水石、羊胫骨、蕲艾茸、石燕、升麻用量原缺。', '《何氏济生论》卷六', '固齿。主', '-', '-', '升麻', '[\'青盐\', \'寒水石\', \'蒺藜\', \'两羊\', \'胫骨\', \'地骨皮\', \'香附\', \'熟地黄\', \'生地\', \'骨碎补\', \'艾茸\', \'石燕\', \'升麻\', \'皂角\', \'槐树\', \'头个\', \'桑树\', \'头个\', \'杨柳\', \'清明\']');
INSERT INTO `prescript` VALUES (60043, '12297', '大黄汤', '大黄（锉，炒）1两，柴胡（去苗）3分，升麻3分，黄芩（去黑心）3分，知母（焙）3分，芍药3分，栀子仁3分，枳实（去瓤，麸炒黄）半两，细辛（去苗叶）1分。', '每服2钱匕，水1盏，加竹叶10片，同煎至6分，去滓，分温3服，早、晚各1次。', '《圣济总录》卷一七七', '小儿8岁以上热结痰实，不能食。', '-', '-', '升麻', '[\'大黄\', \'柴胡\', \'苗分\', \'升麻\', \'黄芩\', \'黑心\', \'知母\', \'焙分\', \'芍药\', \'栀子\', \'仁分\', \'枳实\', \'半两\', \'细辛\', \'苗叶分\']');
INSERT INTO `prescript` VALUES (60044, '12273', '大麻子赤小豆汤', '大麻子（熬）5升，赤小豆5升，生商陆2升（薄切之），升麻4两，附子（炮）3两，射干3两。', '以水4斗，煮诸药，取2斗5升，去滓，研麻子令破，以麻子汁煮豆，令极熟，去滓，可得6-7升，1服1升，1日1夜令尽。', '《千金翼》卷二十四', '疮痈毒气深重，毒肿无定处，或涩恶寒，或心腹刺痛、烦闷者。', '服药后，小便当利，即毒除肿减。食兼此豆益佳，如汤沃雪。凡用麻子，皆不得用郁悒者，可拣择用之。', '-', '升麻', '[\'大麻子\', \'熬升\', \'赤小豆\', \'升生\', \'商陆\', \'升薄切\', \'升麻\', \'附子\', \'射干\']');
INSERT INTO `prescript` VALUES (60045, '12255', '荜茇散', '荜茇1两，苦参1两，防风（去叉）1两，升麻1两，藁本（去苗土）1分。', '每用3钱匕，水1盏，煎6-7沸，热漱冷吐。', '《圣济总录》卷一二○', '诸阳气虚，风攻牙齿疼痛，不任寒热，嚼物隐痛。', '-', '-', '升麻', '[\'苦参\', \'防风\', \'升麻\', \'藁本\', \'苗土\']');
INSERT INTO `prescript` VALUES (60046, '12207', '大黄汤', '大黄（锉，炒）1两，地肤草1两，猪苓（去黑皮）1两，知母（微炒）1两，芍药1两，枳实（去瓤，麸炒）1两，升麻1两，木通（锉）1两，甘草（炙）1两，黄芩（去黑心）半两。', '每服3钱匕，以水1盏，煎至7分，去滓温服，1日2次。', '《圣济总录》卷一五七', '妊娠子淋，小便不通。', '-', '-', '升麻', '[\'大黄\', \'地肤\', \'猪苓\', \'黑皮\', \'知母\', \'芍药\', \'枳实\', \'升麻\', \'木通\', \'甘草\', \'黄芩\', \'黑心\', \'半两\']');
INSERT INTO `prescript` VALUES (60047, '12203', '黄耆萆薢大黄汤', '黄耆5两，萆薢5两（即土茯苓），当归3两，芎3两，桂枝3两，防已3两，升麻3两，鲮甲2两，熟大黄1两，附子1两，甘草1两，营实2两（若无，以忍冬花代之）。', '加生姜，水煎服。', '《霉疮证治》卷下', '霉疮。发头面手足腹背，似杨梅子紫赤色，有脓泡或无脓泡，或痒或痛，而其人实者；乃结毒坚硬如石，小便常涩痛，难消散，又难溃脓。', '-', '-', '升麻', '[\'黄耆\', \'两即\', \'土茯苓\', \'当归\', \'两芎\', \'桂枝\', \'两防\', \'升麻\', \'两鲮甲\', \'两熟\', \'大黄\', \'附子\', \'甘草\', \'两营实\', \'两若\', \'无以\', \'忍冬花\', \'代之\']');
INSERT INTO `prescript` VALUES (60048, '12170', '大神丸', '熟川乌（黑豆水煮）5钱，制草乌（姜汁、甘草水煮）5钱，大风子（去油壳，净）5钱，白僵蚕3钱，北全蝎（姜汁炒）5钱，北蝉蜕5钱，川山甲（土炒成珠）5钱，明雄黄2钱，台乌药5钱，漂苍术（童便浸1宿）5钱，北防风4钱，荆芥穗4钱，苏薄荷4钱，绿升麻2钱半，全当归5钱，大川芎4钱，大秦艽（酒洗）5钱，炙甘草（去皮）5钱，羌活7钱，生地黄4钱半。', '服小神丸后，尚未除根，服此以断根。倘服药欲呕，煮乌豆一勺食之。倘毒气再发，外复施针灸熏洗各法治之。若全愈，明年再服小神丸一料，连服二三年，病根永除。', '《疯门全书》', '麻疯。', '忌铁。', '-', '升麻', '[\'川乌\', \'黑豆\', \'水煮\', \'钱制\', \'草乌\', \'姜汁\', \'甘草\', \'水煮\', \'大风子\', \'油壳\', \'白僵蚕\', \'钱北\', \'全蝎\', \'姜汁\', \'炒钱北\', \'蝉蜕\', \'川山甲\', \'成珠\', \'钱明\', \'雄黄\', \'钱台\', \'乌药\', \'苍术\', \'童便\', \'浸宿\', \'钱北\', \'防风\', \'荆芥\', \'薄荷\', \'升麻\', \'钱半全\', \'当归\', \'钱大\', \'川芎\', \'钱大\', \'秦艽\', \'洗钱\', \'甘草\', \'去皮\', \'羌活\', \'生地黄\', \'钱半\']');
INSERT INTO `prescript` VALUES (60049, '12128', '苦参汤', '苦参2两，龙胆2两，升麻3两，栀子3两。', '苦酒5升，煮取2升，分2服。当大吐乃愈。', '方出《肘后方》卷一，名见《外台》卷七', '暴得心腹痛如刺。', '-', '-', '升麻', '[\'苦参\', \'龙胆\', \'升麻\', \'栀子\']');
INSERT INTO `prescript` VALUES (60050, '12098', '凉血地黄汤', '黄芩1分，荆芥穗1分，蔓荆子1分，黄柏2分，知母2分，藁本2分，细辛2分，川芎2分，黄连3分，羌活3分，柴胡3分，升麻3分，防风3分，生地黄5分，当归5分，甘草1钱，红花少许。', '生地黄汤（《杏苑》卷八）、地黄汤（《女科秘要》卷四）。', '《兰室秘藏》卷中', '妇人肾水阴虚，不能镇守包络相火，而致血崩。', '-', '-', '升麻', '[\'黄芩\', \'荆芥\', \'穗分\', \'蔓荆子\', \'黄柏\', \'知母\', \'藁本\', \'细辛\', \'川芎\', \'黄连\', \'羌活\', \'柴胡\', \'升麻\', \'防风\', \'生地黄\', \'当归\', \'甘草\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (60051, '12040', '茯苓饮', '茯苓3两，紫苏叶3两，杏仁3两，橘皮3两，升麻3两，柴胡3两，生姜4两，犀角2两（屑），槟榔12枚（并皮子，碎）。', '茯苓饮子（《鸡峰》卷四）。', '《外台》卷十八引《延年秘录》', '脚气肿，气急上气，心闷热烦，呕逆不下食。', '忌醋物。', '-', '升麻', '[\'茯苓\', \'紫苏叶\', \'杏仁\', \'橘皮\', \'升麻\', \'柴胡\', \'生姜\', \'犀角\', \'两屑\', \'槟榔\', \'皮子\']');
INSERT INTO `prescript` VALUES (60052, '12019', '麻黄防风汤', '麻黄（去根节，先煎，掠去沫，焙干）3分，防风（去叉）3分，芍药3分，防己半两，桂（去粗皮）半两，芎半两，黄芩（去黑心）半两，甘草（炙）半两，白术半两，人参半两，附子（炮裂，去皮脐）1两半，独活（去芦头）半两，竹沥（旋入）、升麻半两，石膏1两，羚羊角（镑屑）1两。', '每用药15钱，以水4盏，加生姜1分（切），煎至2盏，去滓，入竹沥2合，更煎3沸，分3次温服。', '《圣济总录》卷九', '中风半身不遂。', '-', '-', '升麻', '[\'麻黄\', \'根节\', \'掠去\', \'焙干\', \'防风\', \'去叉分\', \'芍药\', \'防己\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'甘草\', \'半两\', \'白术\', \'半两\', \'人参\', \'半两\', \'附子\', \'炮裂\', \'去皮\', \'两半\', \'独活\', \'芦头\', \'半两\', \'竹沥\', \'旋入\', \'升麻\', \'半两\', \'石膏\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (60053, '11975', '大温丸', '大附子（童便煮1炷香），人参（去芦）3分，桔梗1钱，生地黄1钱，蛤粉5分，玄参7分，升麻4分。', '方中附子用量原缺。', '《痰火点雪》卷二', '口舌生疮，服诸凉药不效者。', '-', '-', '升麻', '[\'附子\', \'童便\', \'人参\', \'芦分\', \'桔梗\', \'生地黄\', \'蛤粉\', \'玄参\', \'升麻\']');
INSERT INTO `prescript` VALUES (60054, '11965', '柴胡散', '柴胡1两（去苗），麻黄1两半（去根节），川升麻1两，人参1两（去芦头），麦门冬1两（去心），甘草3分（炙微赤，锉），枳实3分（麸炒微黄），知母3分，栀子仁3分，鳖甲3两（涂醋炙令黄，去裙襕）。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病数日，未得汗，遍身壮热，呕逆，不下食。', '-', '-', '升麻', '[\'柴胡\', \'两去\', \'麻黄\', \'两半\', \'根节川\', \'升麻\', \'人参\', \'两去\', \'芦头\', \'麦门冬\', \'两去\', \'甘草\', \'分炙微\', \'枳实\', \'分麸\', \'微黄\', \'知母\', \'栀子\', \'仁分\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\']');
INSERT INTO `prescript` VALUES (60055, '11938', '葛根散', '葛根1两（锉），麻黄1两（去根节），赤芍药1两，防风1两（去芦头），黄芩1两，汉防己1两，桂心1两，白术1两，人参1两（去芦头），独活1两，芎1两，川升麻1两，牛膝1两（去苗），石膏2两，陈橘皮1两（汤浸，去白瓤，焙），五加皮1两，羚羊角屑1两。', '每服4钱，以水1中盏，入生姜半分，煎至5分，去滓，入淡竹沥1合，更煎1-2沸，不拘时候温服。', '《圣惠》卷十九', '风痱。言语不转，四肢缓弱，上焦烦壅，心气不利。', '-', '-', '升麻', '[\'葛根\', \'麻黄\', \'两去\', \'根节\', \'芍药\', \'防风\', \'两去\', \'芦头\', \'黄芩\', \'两汉\', \'防己\', \'两桂心\', \'白术\', \'人参\', \'两去\', \'芦头\', \'独活\', \'两芎\', \'两川\', \'升麻\', \'牛膝\', \'两去\', \'石膏\', \'两陈\', \'橘皮\', \'两汤\', \'浸去\', \'五加皮\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (60056, '11872', '柴胡汤', '柴胡（去苗）1两半，黄芩（去黑心）1两半，泽泻1两半，葛根（炙，锉）1两半，升麻1两半，玄参3两，生干地黄（切，焙）2两。', '每用5钱匕，水1盏半，加竹叶7片，煎至1盏，去滓，下芒消1钱匕，分为2服，空心、食后各1次。', '《圣济总录》卷八十六', '调气下热。主肝劳。关格不通，精神不守，气逆上冲，胸中烦闷。', '-', '-', '升麻', '[\'柴胡\', \'去苗\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'泽泻\', \'两半\', \'葛根\', \'两半\', \'升麻\', \'两半\', \'玄参\', \'两生\', \'地黄\', \'切焙\']');
INSERT INTO `prescript` VALUES (60057, '11846', '麻黄汤', '麻黄（去根节）1两半，防风（去叉）1两半，桂（去粗皮）3分，当归（切，焙）1两，白槟榔（切，焙）1两，黄芩（去黑心）1两半，升麻1两半，犀角（镑）1两半，赤茯苓（去黑皮）1两半。', '每服5钱匕，以水1盏半，加大枣2枚（擘破），煎至1盏，去滓，空腹温服。', '《圣济总录》卷八十一', '风毒脚气。', '-', '-', '升麻', '[\'麻黄\', \'根节\', \'两半\', \'防风\', \'两半\', \'桂去\', \'粗皮分\', \'当归\', \'切焙\', \'两白\', \'槟榔\', \'切焙\', \'黄芩\', \'黑心\', \'两半\', \'升麻\', \'两半\', \'犀角\', \'两半\', \'茯苓\', \'黑皮\', \'两半\']');
INSERT INTO `prescript` VALUES (60058, '11831', '除根丸', '羌活6两，独活6两，两头尖3两，寸香5钱，草乌2两，胡麻半斤，荆芥7两，苍术7两，灵仙6两（酒洗），石菖蒲6两（酒洗），木通3两，当归8两，虎骨2两半（炙），苦参1斤，藁本6两，僵蚕2两，蝉退半斤（酒洗），白芷6两，杜仲5两（盐水炒），连翘6两，蒺藜半斤（去刺），朱砂7钱7分，黄芩6两，生地6两，枫子肉2两（炒），升麻4两，风藤5两，首乌6两，天麻5两，牛膝半斤，全蝎6两，细辛4两，川芎3两，山栀半斤，蔓荆子6两，苍耳子半斤（取肉）。', '每服2钱，好酒送下，白汤亦可，日3次。', '《秘传大麻疯方》', '三十六种疯疾。', '-', '-', '升麻', '[\'羌活\', \'独活\', \'两头尖\', \'两寸\', \'香钱\', \'草乌\', \'胡麻\', \'半斤\', \'荆芥\', \'苍术\', \'两灵仙\', \'两酒\', \'石菖蒲\', \'两酒\', \'木通\', \'当归\', \'虎骨\', \'两半\', \'苦参\', \'藁本\', \'僵蚕\', \'两蝉\', \'半斤\', \'酒洗\', \'白芷\', \'杜仲\', \'盐水\', \'连翘\', \'蒺藜\', \'半斤\', \'朱砂\', \'钱分\', \'黄芩\', \'生地\', \'两枫子\', \'升麻\', \'两风藤\', \'首乌\', \'天麻\', \'牛膝\', \'半斤\', \'全蝎\', \'细辛\', \'川芎\', \'两山\', \'半斤\', \'蔓荆子\', \'苍耳子\', \'半斤\', \'取肉\']');
