INSERT INTO `prescript` VALUES (60060, '11796', '黄芩汤', '黄芩（去黑心）半两，栀子仁半两，玄参半两，升麻半两，大黄（锉，炒）半两，黄耆（锉）半两，连翘蓝叶半两，甘草半两，木香半两，芎半两，犀角屑半两。', '每服1钱匕，水半盏，煎3分，去滓温服。', '《圣济总录》卷一八二', '小儿痈疮，烦热疼痛。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'半两\', \'栀子\', \'半两\', \'玄参\', \'半两\', \'升麻\', \'半两\', \'大黄\', \'半两\', \'黄耆\', \'半两\', \'连翘\', \'蓝叶\', \'半两\', \'甘草\', \'半两\', \'木香\', \'半两\', \'半两\', \'犀角\', \'半两\']');
INSERT INTO `prescript` VALUES (60061, '11754', '调元渗湿汤', '羌活8分，白术8分，防风8分，独活8分，升麻4分，苍术1钱，猪苓1钱，柴胡1钱，茯苓1钱，泽泻6分，干葛6分，甘草6分，人参6分，黄柏6分，神曲6分。', '水煎，空心服。', '《玉案》卷三', '肾疸。目与浑身金色，小便赤涩。', '-', '-', '升麻', '[\'羌活\', \'白术\', \'防风\', \'独活\', \'升麻\', \'苍术\', \'猪苓\', \'柴胡\', \'茯苓\', \'泽泻\', \'分干葛分\', \'甘草\', \'人参\', \'黄柏\', \'神曲\']');
INSERT INTO `prescript` VALUES (60062, '11753', '黄芩汤', '黄芩（去黑心）3两，石膏（碎）5两，甘草（炙，锉）2两，升麻2两。', '每服5钱匕，水1盏半，煎至8分，去滓放冷，用漱口，1日10次；喉咽有疮，稍稍咽之。', '《圣济总录》卷一八三', '食饮失度，乳石发，口中发疮。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'石膏\', \'甘草\', \'升麻\']');
