INSERT INTO `prescript` VALUES (60064, '11602', '大鳖甲汤', '鳖甲2两，防风1两，麻黄1两，白术1两，石膏1两，知母1两，升麻1两，茯苓1两，橘皮1两，芎1两，杏仁1两，人参1两，半夏1两，当归1两，芍药1两，萎蕤1两，甘草1两，麦门冬1两，羚羊角6铢，大黄1两半，犀角半两，青木香半两，雄黄半两，大枣10枚，贝齿7枚，乌头7枚，生姜3两，薤白14枚，麝香3铢，赤小豆3合，吴茱萸5合。', '上(口父)咀。以水2斗，煮取4升，分6服。相去10里久，得下止。', '《千金》卷七', '脚弱风毒，挛痹气上；及伤寒恶风，温毒，山水瘴气热毒，四肢痹弱。', '-', '-', '升麻', '[\'鳖甲\', \'防风\', \'麻黄\', \'白术\', \'石膏\', \'知母\', \'升麻\', \'茯苓\', \'橘皮\', \'两芎\', \'杏仁\', \'人参\', \'半夏\', \'当归\', \'芍药\', \'萎蕤\', \'甘草\', \'麦门冬\', \'羚羊角\', \'大黄\', \'两半\', \'犀角\', \'半两\', \'青木香\', \'半两\', \'雄黄\', \'半两\', \'大枣\', \'贝齿\', \'乌头\', \'生姜\', \'薤白\', \'麝香\', \'赤小豆\', \'吴茱萸\']');
INSERT INTO `prescript` VALUES (60065, '11584', '菊花清燥汤', '甘菊花2钱，当归1钱，生地1钱，白芍1钱（酒炒），川芎1钱，知母1钱，贝母1钱（去心，研），地骨皮1钱，麦冬（去心）1钱，柴胡5分，黄芩5分，升麻5分，犀角5分（镑），甘草（生）5分。', '加竹叶20片，灯心20寸，水2钟，煎8分，食后温服。', '《金鉴》卷六十八', '石榴疽，焮肿。', '-', '-', '升麻', '[\'甘菊花\', \'当归\', \'生地\', \'白芍\', \'钱酒\', \'川芎\', \'知母\', \'贝母\', \'心研\', \'地骨皮\', \'麦冬\', \'心钱\', \'柴胡\', \'黄芩\', \'升麻\', \'犀角\', \'分镑\', \'甘草\', \'生分\']');
INSERT INTO `prescript` VALUES (60066, '11573', '断红肠澼丸', '生地炭6斤，当归6斤，黄芩6斤，地榆炭5斤4两，生栀子5斤4两，生白芍4斤8两，升麻3两，炒槐花3斤15两，侧柏炭4斤，乌梅4两，生阿胶4斤，芥穗4斤，黄连1斤8两。', '每服1丸，白开水送下。', '《全国中药成药处方集》（天津方）', '清热、除湿、止血。主痔疮漏疮，肛门肿痛，大便出血。', '-', '-', '升麻', '[\'生地\', \'炭斤\', \'当归\', \'黄芩\', \'地榆\', \'斤两\', \'栀子\', \'斤两\', \'白芍\', \'斤两\', \'升麻\', \'槐花\', \'斤两\', \'侧柏\', \'炭斤\', \'乌梅\', \'两生\', \'阿胶\', \'芥穗斤\', \'黄连\', \'斤两\']');
INSERT INTO `prescript` VALUES (60067, '11535', '黄耆丸', '黄耆1两半（炙，锉），犀角屑1两半，黄连半两（去须），茯神半两（去木），当归半两（切，焙），防风半两（去叉），芍药半两，升麻半两，赤茯苓半两（去黑皮），黄芩半两（去黑心），甘草（炙）半两，木通（锉）1两，麝香（研）半分。', '每服20丸，生姜汤送下。未效，加至30丸。', '《圣济总录》卷一八三', '乳石发动，痈疽发背，一切热毒及恶疮。', '-', '-', '升麻', '[\'黄耆\', \'两半\', \'犀角\', \'两半\', \'黄连\', \'半两\', \'茯神\', \'半两\', \'当归\', \'半两\', \'切焙\', \'防风\', \'半两\', \'芍药\', \'半两\', \'升麻\', \'半两\', \'茯苓\', \'半两\', \'黑皮\', \'黄芩\', \'半两\', \'黑心\', \'甘草\', \'半两\', \'木通\', \'麝香\', \'半分\']');
INSERT INTO `prescript` VALUES (60068, '11525', '柴胡散', '柴胡（去苗）1两，茵陈1两，川大黄（锉碎，微炒）1两，黄芩1两，木通（锉）1两，川升麻1两，栀子仁1两，川芒消1两，茅根（锉）1两。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。以得通利为度。', '《圣惠》卷十六', '时气恶寒壮热，头痛，小便不通。', '-', '-', '升麻', '[\'柴胡\', \'茵陈\', \'两川\', \'大黄\', \'黄芩\', \'木通\', \'两川\', \'升麻\', \'栀子\', \'两川芒\', \'消两茅根\']');
INSERT INTO `prescript` VALUES (60069, '11492', '大泽泻汤', '柴胡1两，茯神（《外台》作茯苓）1两，黄芩1两，泽泻1两，升麻1两，杏仁1两，磁石4两（碎），羚羊角1两，地黄3两，大青3两，芒消3两，淡竹叶（切）1升。', '上(口父)咀。以水1斗，煮取3升，去滓，下芒消，分3服。', '方出《千金》卷十九，名见《圣济总录》卷五十一', '肾热。好怒好忘，耳听无闻，四肢满急，腰背转动强直。', '-', '-', '升麻', '[\'柴胡\', \'茯神\', \'外台作\', \'茯苓\', \'黄芩\', \'泽泻\', \'升麻\', \'杏仁\', \'磁石\', \'两碎\', \'羚羊角\', \'两地\', \'黄两大青\', \'两芒\', \'消两\', \'淡竹叶\', \'切升\']');
