INSERT INTO `prescript` VALUES (60071, '11443', '保婴丹', '缠豆藤1两5钱（8月内收取毛豆梗上缠绕红黄大者，阴干），生地黄1两，山楂肉1两，牛蒡子1两，辰砂1两（水飞，研细），黑豆30粒，新升麻7钱5分，独活2钱，甘草5钱，当归（酒浸）5钱，赤芍药5钱，黄连5钱，桔梗5钱，连翘7钱，苦丝瓜2条（5寸长者，来年经霜为妙，烧灰存性用）。', '每服1丸，浓煎甘草汤化下。前项药须取精好者，遇春、秋2分或正月十五日修合。', '《医统》卷九十一', '小儿痘疮未出，每遇春分、秋分时服此，其痘毒能渐消散。若服3年6次，其毒尽消，痘出必稀，可保无虞。主', '-', '-', '升麻', '[\'豆藤\', \'收取\', \'毛豆\', \'缠绕\', \'黄大者\', \'阴干\', \'生地黄\', \'山楂\', \'牛蒡子\', \'辰砂\', \'两水飞\', \'研细\', \'豆粒\', \'升麻\', \'钱分\', \'独活\', \'甘草\', \'当归\', \'酒浸\', \'芍药\', \'黄连\', \'桔梗\', \'连翘\', \'钱苦\', \'丝瓜\', \'条寸\', \'长者\', \'经霜为\', \'烧灰\', \'存性\']');
INSERT INTO `prescript` VALUES (60072, '11441', '大黄丸', '木香半两，大黄半两，黑豆1两，升麻3分。', '每服3钱，加乌梅2个，新汲水煎服。', '《直指》卷四', '脚气，风热烦闷发渴，大便不通。', '-', '-', '升麻', '[\'木香\', \'半两\', \'大黄\', \'半两\', \'黑豆\', \'升麻\']');
INSERT INTO `prescript` VALUES (60073, '11416', '黄耆白术汤', '细辛3分，吴茱萸5分，川芎5分，柴胡1钱，升麻1钱，当归身1钱5分，黄柏2钱（酒洗），炙甘草2钱，羌活2钱，五味子3钱，白术5钱，人参5钱，黄耆1两。', '黄耆白术散（《普济方》卷三二八）。如腹中痛不快，加炙甘草1钱；汗出不止，加黄柏1钱。', '《兰室秘藏》卷中', '妇人四肢沉重，自汗，上至头，际头而还，恶风，头痛躁热。', '-', '-', '升麻', '[\'细辛\', \'吴茱萸\', \'川芎\', \'柴胡\', \'升麻\', \'当归\', \'身钱分\', \'黄柏\', \'钱酒\', \'甘草\', \'羌活\', \'五味子\', \'白术\', \'人参\', \'黄耆\']');
