INSERT INTO `prescript` VALUES (60075, '11361', '黄连散', '黄连半两（去须），木通半两（锉），犀角屑3分，川升麻2分，黄芩半两，大青半两，茯神半两，甘草半两（炙微赤，锉），百合3分。', '每服5钱，以水1大盏，加生姜半分，竹叶2-7片，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十三', '伤寒毒气未散，欲变入狐惑证，目赤，面色斑斑如锦纹。', '-', '-', '升麻', '[\'黄连\', \'半两\', \'木通\', \'半两\', \'犀角\', \'分川\', \'升麻\', \'黄芩\', \'半两\', \'大青\', \'半两\', \'茯神\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'百合\']');
INSERT INTO `prescript` VALUES (60076, '11354', '黄芩散', '黄芩1分（去黑心），升麻1分，黄连1分（去须），大青1分，虾蟆1分（烧灰），角蒿（灰）1分，黄柏（去粗皮）半两。', '每用1字匕，贴齿龈上，有涎即吐。如患干湿癣，以口脂和，涂疮上；或腊月猪脂和亦得。', '《圣济总录》卷一七二', '小儿口齿疳。唇口痒痛，齿龈肿黑，宣露摇动；及干湿癣。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'升麻\', \'黄连\', \'分去\', \'大青分\', \'虾蟆\', \'烧灰\', \'角蒿\', \'灰分\', \'黄柏\', \'粗皮\', \'半两\']');
INSERT INTO `prescript` VALUES (60077, '11338', '黄耆丸', '黄耆1两（锉），防风1两（去叉），地骨皮1两，枳实（去瓤，麸炒）1两，羌活半两（去芦头），苦参半两，当归半两（切，炒），升麻半两，大黄半两（锉，炒），甘草（炙，锉）半两。', '方中黄耆、防风、地骨皮用量原缺，据《普济方》补。', '《圣济总录》卷十二', '风气有热，烦惋，头面生疮。', '-', '-', '升麻', '[\'黄耆\', \'防风\', \'两去\', \'地骨皮\', \'枳实\', \'羌活\', \'半两\', \'芦头\', \'苦参\', \'半两\', \'当归\', \'半两\', \'升麻\', \'半两\', \'大黄\', \'半两\', \'甘草\', \'半两\']');
