INSERT INTO `prescript` VALUES (60079, '11293', '马蔺根散', '马蔺根2两，川升麻1两，射干1两半，犀角屑2两，玄参2两半，木通1两（锉），（艹遽）麦1两，甘草半两（生，锉）。', '每服3钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十五', '咽喉卒肿痛，热毒在胸膈。', '-', '-', '升麻', '[\'马蔺\', \'根两川\', \'升麻\', \'射干\', \'两半\', \'犀角\', \'玄参\', \'两半\', \'木通\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60080, '11272', '凉膈散', '连翘3钱，栀子3钱，大黄4钱（酒蒸），芒消1钱，黄芩3钱，薄荷8分，知母1钱5分，升麻4分，石膏3钱，黄连6分，甘草8分。', '水煎，频服。', '《寿世保元》卷六', '胃有实热，齿痛，或上牙痛尤甚者。', '-', '-', '升麻', '[\'连翘\', \'栀子\', \'大黄\', \'钱酒\', \'蒸芒\', \'消钱\', \'黄芩\', \'薄荷\', \'知母\', \'钱分\', \'升麻\', \'石膏\', \'黄连\', \'甘草\']');
INSERT INTO `prescript` VALUES (60081, '11256', '调中益气汤', '黄耆1钱，人参（去芦）半钱，甘草（炙）半钱，陈皮2分，五味子7粒，芍药3分，白术5分，当归5分，升麻2分，柴胡2分。', '上（口父）咀，作1服。水2盏，煎至1盏，去滓，食前温服。如下元阴火蒸蒸发，燥热者，加生地黄2分；如咳嗽，加5味子10粒；腹中气不转运者，更加陈皮3分、木香2分；身体沉重，虽小便数多，加茯苓2钱、苍术1钱、泽泻半钱，黄柏3分；如胃气不和，加汤洗姜制半夏5分；痰厥头疼，加半夏；如夏月，须加白芍药3分，以补肺气不足；如春夏腹疼，尤宜加芍药；恶热燥渴而腹疼者，更加白芍药半钱；严寒腹疼，加中桂2钱；如冬月腹疼，不可用芍药，以太寒故也，只加干姜2分，或加半夏4分（姜制）。', '《东垣试效方》卷一', '因饥饱劳役，损伤脾胃，元气不足。其脉弦或洪缓而沉，按之无力，中之下，时得一涩。其证身体沉重，四肢倦懒，百节烦疼，胸满短气，膈咽不通，心烦不安，耳鸣耳聋，目有瘀肉，热壅如火，视物昏花，口中沃沫，饮食失味，怠堕嗜卧，忽肥忽瘦，溺色变，或清利而数，或上饮下便，或夏月飧泄，腹中虚痛，不思饮食。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'芦半钱\', \'甘草\', \'炙半钱\', \'陈皮分\', \'五味子\', \'芍药\', \'白术\', \'当归\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (60082, '11244', '羚羊角汤', '羚羊角1两（镑），决明子1两，人参1两，升麻1两，玄参1两，车前子1两，羌活1两半（去芦头），防风（去叉）1两半，细辛（去苗叶）半两。', '每服5钱匕，以水1盏半，煎至8分，去滓温服，不拘时候。', '《圣济总录》卷一○九', '眼见黑花，或头旋目暗，欲变青盲，眼瞳微开。', '-', '-', '升麻', '[\'羚羊角\', \'两镑\', \'决明子\', \'人参\', \'升麻\', \'玄参\', \'车前子\', \'羌活\', \'两半\', \'芦头\', \'防风\', \'两半\', \'细辛\', \'苗叶\', \'半两\']');
INSERT INTO `prescript` VALUES (60083, '11233', '黄耆汤', '黄耆4两，升麻3两，桂心（冷用）2分，黄芩1两，竹叶（切）1升，茯苓2两，生姜（切）2两，甘草2两（炙）。', '上（口父）咀。以水2斗，煮竹叶，减5升，去之，澄取9升，纳诸药，煮取3升，去滓，分3服，日3次。', '《千金翼》卷二十三', '痈肿虚弱。', '-', '-', '升麻', '[\'黄耆\', \'升麻\', \'两桂心\', \'冷用\', \'黄芩\', \'竹叶\', \'切升\', \'茯苓\', \'生姜\', \'甘草\']');
INSERT INTO `prescript` VALUES (60084, '11189', '荜茇丸', '荜茇1两半，麻黄（去根节煎，去沫，焙）1两半，独活（去芦头）1两半，升麻1两半，吴茱萸（汤浸3度，焙炒）1两半，木香3分，陈橘皮（汤浸，去白，焙）3分，射干1两，白茯苓（去黑皮）1两，干姜（炮）1两，昆布（洗去咸味，暴干）2两，羚羊角（镑）半两，杏仁（去皮尖双仁，炒）1两1分。', '每服15丸，空心米饮送下。以利为度。若利多者，减至7丸。若食不消化，不能食者，加白术1两1分，陈曲2两半（炒令微黄）；若大便涩者，加大黄1两。', '《圣济总录》卷八十三', '脚气毒闷，呕逆吐沫，心烦气急，不下食。', '-', '-', '升麻', '[\'两半\', \'麻黄\', \'根节\', \'两半\', \'独活\', \'芦头\', \'两半\', \'升麻\', \'两半\', \'吴茱萸\', \'浸度\', \'两半\', \'木香\', \'分陈\', \'橘皮\', \'汤浸\', \'白焙分\', \'射干\', \'白茯苓\', \'黑皮\', \'干姜\', \'昆布\', \'咸味\', \'暴干\', \'羚羊角\', \'半两\', \'杏仁\', \'皮尖\', \'双仁\', \'两分\']');
