INSERT INTO `prescript` VALUES (60094, '10944', '柴胡散', '柴胡（去苗）1两，川升麻1两，黄芩1两，黄连（去须）1两，栀子仁1两，车前子1两，决明子1两，防风（去芦头）1两，羚羊角屑1两，马牙消1两，甘草（炙微赤，锉）1两，玄参1两。', '每服3钱，以水1中盏，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '肝脏风热，眼中生疮肿痛。', '-', '-', '升麻', '[\'柴胡\', \'去苗\', \'两川\', \'升麻\', \'黄芩\', \'黄连\', \'栀子\', \'车前子\', \'决明子\', \'防风\', \'芦头\', \'羚羊角\', \'两马牙\', \'消两\', \'甘草\', \'炙微\', \'玄参\']');
INSERT INTO `prescript` VALUES (60095, '10938', '羚羊角散', '羚羊角屑3分，川升麻3分，秦艽3分（去苗），木通3分（锉），白鲜皮3分，槟榔1两，黄芩3分，麦门冬1两（去心，焙），川大黄1两（锉碎，微炒），甘草半两（炙微赤，锉）。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气六日，烦躁头痛，小便赤涩，壅热不退。', '-', '-', '升麻', '[\'羚羊角\', \'分川\', \'升麻\', \'秦艽\', \'苗木\', \'通分\', \'白鲜皮\', \'槟榔\', \'黄芩\', \'麦门冬\', \'两去\', \'心焙川\', \'大黄\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60096, '10911', '揩齿白芷散', '白芷1分，升麻3分，藁本（去苗土）1分，细辛（去苗叶）1分，沉香（锉）1分，丁香1分，石膏（研）1分，贝齿（研）1分，麝香（研）1分，猪牙皂荚（烧存性）、凝水石（研）1两。', '本方方名，原书（文瑞楼本）作白芷散。', '《圣济总录》（人卫本）卷一二一', '益牙齿，去恶气。主', '-', '-', '升麻', '[\'白芷\', \'升麻\', \'藁本\', \'苗土\', \'细辛\', \'苗叶分\', \'沉香\', \'锉分\', \'丁香\', \'石膏\', \'分贝\', \'研分\', \'麝香\', \'研分\', \'猪牙皂\', \'存性\', \'凝水\', \'石研\']');
INSERT INTO `prescript` VALUES (60097, '10908', '救苦化坚汤', '黄耆1钱，人参3分，炙甘草5分，真漏芦1钱，升麻1钱，葛根5分，连翘1钱，牡丹皮3分，当归身3分，生地黄3分，熟地黄3分，白芍药3分，肉桂2分，柴胡8分，黍粘子3分，羌活1钱，独活5分，防风5分，昆布2分，京三棱（煨）2分，广荗（煨）3分，益智仁2分，大麦芽面1钱，神曲末（炒黄色）2分，黄连（去须）3分，黄柏（炒）3分，厚朴3钱2分（姜制）。', '救苦胜灵汤（《准绳·疡医》卷三）、消瘰化坚汤（《医林篡要》卷十）。本方方名，《济生拔萃》本作“救苦胜灵丹”；改为丸剂，名“救苦化坚丸”（见《杏苑》）。如气短不调及喘者，加人参剂量；如夏月，倍白芍药，冬寒则不可用；如有烦躁者，去肉桂；如疮不在少阳经，去柴胡；无肿者，不用黍粘子；如疮不坚硬，不用京三棱、广荗；如无唾多，吐沫，吐食，去益智仁；如有热，或腿脚无力，或躁烦欲去衣，宜加用黄柏，无则不用；如无腹胀，不用厚朴；如气不顺，加橘皮，甚者加木香少许；如只在阳明分为瘰疬者，去柴胡、黍粘子；如在少阳分，为马刀扶瘿者', '《兰室秘藏》（人卫本）卷下', '瘰疬，马刀挟瘿。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'甘草\', \'分真\', \'漏芦\', \'升麻\', \'葛根\', \'连翘\', \'牡丹皮\', \'当归\', \'身分\', \'生地黄\', \'熟地黄\', \'分白\', \'芍药\', \'肉桂\', \'柴胡\', \'粘子分\', \'羌活\', \'独活\', \'防风\', \'昆布\', \'分京\', \'三棱\', \'分广\', \'益智仁\', \'大麦芽\', \'面钱\', \'神曲\', \'黄色\', \'黄连\', \'黄柏\', \'炒分\', \'厚朴\', \'钱分\', \'姜制\']');
INSERT INTO `prescript` VALUES (60098, '10902', '揩齿石膏散', '石膏（研）1两，凝水石（研）2两，丹砂（研）1分，升麻半两，白芷1两，细辛（去苗叶）半两，藁本（去苗土）半两，沉香1两（锉）。', '石膏散（《普济方》卷七十）。去恶气，入麝香少许甚佳。', '《圣济总录》卷一二一', '令齿鲜净，去除恶气。主牙齿黄黑，及口臭。', '-', '-', '升麻', '[\'石膏\', \'研两凝水\', \'石研\', \'丹砂\', \'研分\', \'升麻\', \'半两\', \'白芷\', \'细辛\', \'苗叶\', \'半两\', \'藁本\', \'苗土\', \'半两\', \'沉香\']');
INSERT INTO `prescript` VALUES (60099, '10875', '桔梗散', '桔梗1两（去芦头），犀角屑1两，羚羊角屑1两，赤芍药1两，川升麻2两，栀子仁1两，杏仁1两（汤浸，去皮尖双仁，麸炒微黄），甘草1两（炙微赤，锉）。', '每服用4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十五', '咽喉肿痛，结毒气冲其心胸。', '-', '-', '升麻', '[\'桔梗\', \'两去\', \'芦头\', \'犀角\', \'羚羊角\', \'芍药\', \'两川\', \'升麻\', \'栀子\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (60100, '10872', '瞿麦散', '瞿麦3分，车前根3分，木通1两（锉），栀子仁1两，川大黄1两（锉碎，微炒），黄芩1两，川升麻1两，牵牛子3分（微炒），滑石半两，川朴消1两，甘草半两（炙微赤，锉）。', '每服5钱，以水1中盏，加葱白2茎，灯心半束，煎至6分，去滓温服，不拘时候，以通利为度。', '《圣惠》卷十三', '伤寒，小便不通，尿血涩痛。', '-', '-', '升麻', '[\'瞿麦\', \'分车\', \'根分\', \'木通\', \'栀子\', \'仁两川\', \'大黄\', \'黄芩\', \'两川\', \'升麻\', \'牵牛子\', \'分微\', \'滑石\', \'半两\', \'川朴\', \'消两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60101, '10829', '茯神散', '茯神3分，龙齿半两，寒水石1两，川升麻3分，石膏1两，麦门冬1两（去心，焙），甘草半两（炙微赤，锉）。', '每服1钱，以水1小盏，煎至5分，去滓，入竹沥半合，更煎1-2沸。', '《圣惠》卷八十三', '小儿心热，惊悸烦乱。', '-', '-', '升麻', '[\'茯神\', \'龙齿\', \'半两\', \'寒水石\', \'两川\', \'升麻\', \'石膏\', \'麦门冬\', \'两去\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60102, '10801', '柴胡散', '柴胡1两，人参1两，茯苓1两，桔梗1两，芍药1两，当归（酒浸）1两，麦门冬1两，青皮1两，桑白皮1两，川芎1两，白术1两，升麻1两，甘草（炙）1两。', '每服2钱，水1盏，煎7分，通口连滓服。', '《医统》卷四十六', '虚劳。', '-', '-', '升麻', '[\'柴胡\', \'人参\', \'茯苓\', \'桔梗\', \'芍药\', \'当归\', \'酒浸\', \'麦门冬\', \'青皮\', \'桑白皮\', \'川芎\', \'白术\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (60103, '10780', '黄耆膏', '黄耆1分（锉），升麻1分，大黄1分（生锉），芍药1分，细辛（去苗叶）半两。', '每用少许，滴耳中，日3次。', '《圣济总录》卷一一五', '耳内窒塞，如有物点。', '-', '-', '升麻', '[\'黄耆\', \'升麻\', \'大黄\', \'分生\', \'芍药\', \'细辛\', \'苗叶\', \'半两\']');
INSERT INTO `prescript` VALUES (60104, '10753', '漏芦散', '漏芦1两，白蔹1两，黄芩1两，麻黄1两（去根节），知母1两，枳实2两（麸炒微黄），川升麻1两，犀角屑1两，赤芍药1两，川大黄2两（锉碎，微炒），甘草3分（生，锉）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十二', '除烦热，解毒。主发背及一切疮毒，攻冲寒热，大肠秘涩。', '-', '-', '升麻', '[\'漏芦\', \'白蔹\', \'黄芩\', \'麻黄\', \'两去\', \'根节\', \'知母\', \'枳实\', \'两麸\', \'微黄\', \'升麻\', \'犀角\', \'芍药\', \'两川\', \'大黄\', \'甘草\', \'分生\']');
INSERT INTO `prescript` VALUES (60105, '10741', '柴胡散', '柴胡半两（去芦头），大青3分，葛根半两（锉），石膏1两，知母半两，栀子仁半两，川升麻3分，黄芩3分，甘草半两（炙微赤，锉）。', '《袖珍》有茯苓，无黄芩。', '《圣惠》卷七十四', '妊娠热病，发即背酸疼，头痛壮热。若不急疗，热势不止，多致损落。', '-', '-', '升麻', '[\'柴胡\', \'半两\', \'芦头\', \'大青分\', \'葛根\', \'半两\', \'石膏\', \'知母\', \'半两\', \'栀子\', \'半两\', \'升麻\', \'黄芩\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60106, '10734', '大黄散', '川大黄1两（锉，微炒），犀角屑半两，川升麻半两，当归1分，赤芍药1分，红雪1两，甘草1分（炙微赤，锉）。', '每服1钱，以水1小盏，煎至6分，去滓，3-4岁温服1合，每日3-4次。以利为度。', '《圣惠》卷九十二', '小儿脏腑壅热，心神烦躁，大便不通。', '-', '-', '升麻', '[\'大黄\', \'犀角\', \'半两\', \'升麻\', \'半两\', \'当归\', \'分赤\', \'芍药\', \'分红\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (60107, '10724', '点眼黄连膏', '黄连（去须）1两，黄柏（去粗皮，蜜炙）1两，升麻1两，蕤仁（去皮）1两，细辛（去苗叶）3分，石胆末半钱（研极细），龙脑（研细）1两，蜜1两。', '本方方名，《普济方》引作“黄连膏”。', '《圣济总录》卷一○九', '风赤眼胬肉痒痛。', '-', '-', '升麻', '[\'黄连\', \'黄柏\', \'粗皮蜜\', \'升麻\', \'蕤仁\', \'去皮\', \'细辛\', \'苗叶分\', \'石胆末\', \'半钱\', \'研极细\', \'龙脑\', \'研细\', \'两蜜\']');
INSERT INTO `prescript` VALUES (60108, '10723', '大黄汤', '木香半两，大黄半两，黑豆1两，升麻3分。', '每服3钱，加乌梅2个，新水煎服。', '《直指》卷四', '脚气风热，烦闷发渴，大便不通。', '-', '-', '升麻', '[\'木香\', \'半两\', \'大黄\', \'半两\', \'黑豆\', \'升麻\']');
INSERT INTO `prescript` VALUES (60109, '10667', '羚羊角散', '羚羊角10两（镑），黄芩10两，升麻10两，甘草10两（炙），车前子10两，栀子仁5两，草龙胆5两，决明子20两。', '每服1钱，食后温热水调下，每日3次。小儿可服半钱。', '《局方》卷七', '大人、小儿一切风热毒，上攻眼目，暴发赤肿，或生疮疼痛，隐涩羞明。', '-', '-', '升麻', '[\'羚羊角\', \'两镑\', \'黄芩\', \'升麻\', \'甘草\', \'车前子\', \'栀子\', \'仁两草\', \'龙胆\', \'决明子\']');
INSERT INTO `prescript` VALUES (60110, '10663', '大青丸', '大青1两，大黄（锉，炒）1两，栀子（去皮）1两，黄耆（锉）1两，升麻1两，黄连（去须）1两，朴消2两。', '每服30丸，空心温水送下。', '《圣济总录》卷一一五', '脑热，脑脂流下，塞耳成聋。', '-', '-', '升麻', '[\'大青\', \'大黄\', \'栀子\', \'去皮\', \'两黄耆\', \'升麻\', \'黄连\', \'两朴\', \'消两\']');
INSERT INTO `prescript` VALUES (60111, '10641', '黄连汤', '黄连（去须）1钱，黄芩1钱，赤茯苓（去皮）1钱，麦门冬（去心）1钱，升麻1钱。', '每服3钱，水1盏，煎至7分，去滓，食后温服。', '《普济方》卷三十四引《护命》', '胆热口苦，神昏多睡，左手关脉实大。', '-', '-', '升麻', '[\'黄连\', \'黄芩\', \'茯苓\', \'去皮\', \'麦门冬\', \'心钱\', \'升麻\']');
INSERT INTO `prescript` VALUES (60112, '10621', '兜肚方', '白檀香1两，零陵香5钱，马蹄香5钱，香白芷5钱，马兜铃5钱，木鳖子8钱，羚羊角1两，甘松5钱，升麻5钱，丁皮7钱，血竭5钱，麝香9分。', '初服者，用3日后1解，至第5日复服，至1月后常服。', '《摄生众妙方》卷十一', '痞积，遗精，白浊，妇人赤白带下，及妇人经脉不调，久不受孕。', '有孕妇人不可服。', '-', '升麻', '[\'檀香\', \'零陵\', \'香钱\', \'马蹄\', \'香钱\', \'白芷\', \'马兜铃\', \'木鳖子\', \'羚羊角\', \'两甘松\', \'升麻\', \'钱丁皮\', \'血竭\', \'麝香\']');
INSERT INTO `prescript` VALUES (60113, '10604', '揩齿丹砂散', '丹砂（研）1两，麝香（研）少许，白檀香半两，丁香皮1两，藿香叶1两，茅香1两，甘松（去土）1两，白芷1两，升麻1两，莎草根（炒，去毛）1两，铅丹1两，石膏（末）4两，凝水石（末）1斤，零陵香半两，猪牙皂荚（烧存性）2两。', '丹砂散（《普济方》卷七十）。', '《圣济总录》卷一二一', '涤除腐气，令牙齿坚牢，齿槽固密，诸疾不生。主', '-', '-', '升麻', '[\'丹砂\', \'研两\', \'麝香\', \'少许\', \'檀香\', \'半两\', \'丁香\', \'藿香\', \'两茅香\', \'两甘松\', \'白芷\', \'升麻\', \'莎草\', \'铅丹\', \'石膏\', \'两凝水\', \'石末斤\', \'零陵\', \'半两\', \'猪牙皂\', \'存性\']');
INSERT INTO `prescript` VALUES (60114, '10593', '解肌汤', '川升麻1钱，甘草（生用）1钱，干姜1钱，黄芩1钱，赤芍1钱5分，麻黄（去节）5分。', '上(口父)咀。每服2-3钱，桃、柳枝各7根，水1钟，煎6分，不拘时候温服。', '《万氏家抄方》卷五', '小儿遍身赤肿，不能睡卧。', '-', '-', '升麻', '[\'升麻\', \'甘草\', \'用钱\', \'干姜\', \'黄芩\', \'赤芍\', \'钱分\', \'麻黄\', \'节分\']');
INSERT INTO `prescript` VALUES (60115, '10584', '调和饮', '白芍3钱，当归1钱，川芎2钱，黄连2钱，黄芩2钱，桃仁1钱，升麻5分。', '水煎，空心服。如红痢依本方；如白痢，用吴茱萸1钱，芩、连用酒炒；赤白痢，加白术，茯苓，陈皮，香附各1钱。', '《回春》卷三', '下痢稍久者。', '-', '-', '升麻', '[\'白芍\', \'当归\', \'川芎\', \'黄连\', \'黄芩\', \'桃仁\', \'升麻\']');
