INSERT INTO `prescript` VALUES (60144, '9796', '桂枝葛根汤', '桂枝1钱，葛根1钱，赤芍药1钱，升麻1钱，防风1钱，甘草1钱。', '加生姜3片、淡豆豉1钱，水1盏，煎7分，去滓温服，不拘时候。', '《准绳·幼科》卷四', '小儿癍疹初发，如时大寒，则腠理闭密，气血凝涩，防其发泄得迟，有毒气壅遏之变。', '-', '-', '升麻', '[\'桂枝\', \'葛根\', \'芍药\', \'升麻\', \'防风\', \'甘草\']');
