INSERT INTO `prescript` VALUES (60148, '9708', '羚羊角散', '羚羊角屑1两，地骨皮3分，黄芩3分，麦门冬1两半（去心，焙），秦艽半两（去苗），柴胡半两（去苗），栀子仁半两，车前子3分，葳蕤半两，川升麻半两，甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十二', '丹石毒上攻眼目，涩痛瞳赤，心神烦躁，唇口干燥。', '-', '-', '升麻', '[\'羚羊角\', \'地骨皮\', \'黄芩\', \'麦门冬\', \'两半\', \'秦艽\', \'半两\', \'去苗\', \'柴胡\', \'半两\', \'去苗\', \'栀子\', \'半两\', \'车前子\', \'葳蕤\', \'半两\', \'升麻\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60149, '9683', '大黄汤', '大黄3两，栀子50个，升麻2两，黄芩3两，芒消1两。', '五利汤（《千金》卷二十二）、五利大黄汤（《圣济总录》卷一三○）。', '《鬼遗》卷三', '痈疽，发背，时毒焮肿赤痛，烦渴便秘，脉实数；年四十已，还强壮，常大患热痈无定处，大小便不通', '-', '-', '升麻', '[\'大黄\', \'栀子\', \'升麻\', \'黄芩\', \'两芒\', \'消两\']');
INSERT INTO `prescript` VALUES (60150, '9661', '茯神丸', '茯神1两，牛黄1两（细研），虎肢1对（酒浸1宿微黄），石膏2两（细研），川升麻1两，麦门冬1两半（去心，焙），玄参1两，铁粉2两（细研），生干地黄1两，龙齿2两，金箔50片（细研），银箔50片（细研）。', '《普济方》有虎睛、樟脑，无虎肢、龙齿。', '《圣惠》卷二十', '风惊恍惚，心神不安。', '-', '-', '升麻', '[\'茯神\', \'牛黄\', \'两细\', \'研虎肢\', \'浸宿\', \'微黄\', \'石膏\', \'两细\', \'研川\', \'升麻\', \'麦门冬\', \'两半\', \'玄参\', \'铁粉\', \'两细\', \'研生\', \'地黄\', \'龙齿\', \'金箔\', \'片细\', \'研银\', \'箔片\', \'细研\']');
