INSERT INTO `prescript` VALUES (60156, '9562', '黄耆散', '黄耆1两（锉），犀角屑2分，地榆2分（微炙），玄参3分，赤茯苓3分，当归3分，川芒消1两半，木香半两，连翘半分，甘草3分（生，锉），枳壳3分（麸炒微黄，去部），栀子仁3分，川升麻1两，川大黄2两（锉碎，微炒），黄芩1两。', '每服4钱，以水1中盏，入竹叶3-7片，煎至6分，去滓温服，日3-4服。', '《圣惠》卷三十八', '乳石发动，生痈肿，烦疼壮热，口干心燥，大小便涩滞。', '-', '-', '升麻', '[\'黄耆\', \'犀角\', \'地榆\', \'分微\', \'玄参\', \'分赤\', \'茯苓\', \'当归\', \'分川芒\', \'两半\', \'木香\', \'半两\', \'连翘\', \'半分\', \'甘草\', \'分生\', \'枳壳\', \'分麸\', \'微黄\', \'去部\', \'栀子\', \'仁分川\', \'升麻\', \'两川\', \'大黄\', \'黄芩\']');
