INSERT INTO `prescript` VALUES (60160, '9511', '黄芩汤', '黄芩半两，川升麻半两，甘草（炙微赤，锉）半两，葳蕤1分，玄参1分，犀角屑1分。', '每服1钱，以水1小盏，煎至5分，去滓，温温分为2服，每日3-4次。', '方出《圣惠》卷八十九，名见《圣济总录》卷一八一', '5岁以下小儿肝脏热毒，目生丁翳。', '-', '-', '升麻', '[\'黄芩\', \'半两\', \'升麻\', \'半两\', \'甘草\', \'炙微\', \'半两\', \'葳蕤\', \'玄参\', \'犀角\']');
INSERT INTO `prescript` VALUES (60161, '9474', '丹参牛膝煮散', '丹参4两，牛膝4两，桑白皮4两，杏仁4两，升麻4两，猪苓4两，茯苓4两，犀角3两，黄芩3两，橘皮3两，防己3两，白前3两，泽泻3两，桂心3两，秦艽3两，生姜2两，李根白皮2两，大麻仁1升。', '以水1升半，纳散方寸匕，煮取7合，轻绢滤去滓，顿服，1日2次。夏月热，不得服丸散，此煮散顷年常用。', '《千金》卷七', '脚痹弱，气满身微肿。', '-', '-', '升麻', '[\'丹参\', \'牛膝\', \'桑白皮\', \'杏仁\', \'升麻\', \'猪苓\', \'茯苓\', \'犀角\', \'黄芩\', \'橘皮\', \'防己\', \'两白前\', \'泽泻\', \'两桂心\', \'秦艽\', \'生姜\', \'李根\', \'白皮\', \'两大\', \'麻仁\']');
INSERT INTO `prescript` VALUES (60162, '9345', '敛肺汤', '知母（焙）1两，百部1两，百合1两，白前1两，芍药1两，黄耆（锉）1两，款冬花1两，马兜铃1两，贝母（去心）1两，五味子1两，前胡（去芦头）1两，青橘皮（汤浸去白，焙）1两，防葵1两，大黄（生，锉）1两，麻黄（去根节）1两，桃仁（去皮尖双仁，炒黄）1两，白术（锉，炒）1两，升麻1两，紫菀（去苗土）大枣（去核，焙）1两，槟榔（麸炒）1两，甘草（炙）1两，葛根1两，防已1两。', '每服3钱，加水1大盏，煎至7分，去滓温服，不拘时候。', '《普济方》卷二十七', '肺脏壅热，咳嗽多痰，面色赤，口干，气急烦满，大肠不利。', '-', '-', '升麻', '[\'知母\', \'百部\', \'百合\', \'两白前\', \'芍药\', \'两黄耆\', \'款冬花\', \'马兜铃\', \'贝母\', \'五味子\', \'两前\', \'胡去\', \'芦头\', \'两青\', \'橘皮\', \'汤浸\', \'两防葵\', \'大黄\', \'麻黄\', \'根节\', \'桃仁\', \'皮尖\', \'双仁\', \'白术\', \'升麻\', \'紫菀\', \'苗土\', \'大枣\', \'去核\', \'槟榔\', \'甘草\', \'葛根\', \'两防\']');
INSERT INTO `prescript` VALUES (60163, '9339', '黄连救苦汤', '黄连1钱，升麻1钱，葛根1钱，柴胡1钱，赤芍1钱，川芎1钱，归尾1钱，连翘1钱，桔梗1钱，黄芩1钱，羌活1钱，防风1钱，金银花1钱，甘草节1钱。', '水2碗，煎8分，临服入酒1杯，食后服。', '《外科正宗》卷三', '脑疽、发鬓、发颐及天行时毒，初起憎寒壮热，头面耳项俱肿。对口疽，初起寒热发肿。', '-', '-', '升麻', '[\'黄连\', \'升麻\', \'葛根\', \'柴胡\', \'赤芍\', \'川芎\', \'钱归尾\', \'连翘\', \'桔梗\', \'黄芩\', \'羌活\', \'防风\', \'金银\', \'花钱\', \'甘草\', \'节钱\']');
INSERT INTO `prescript` VALUES (60164, '9321', '黄耆肉桂柴胡酒煎汤', '黄耆2钱，当归梢2钱，柴胡1钱5分，黍粘子1钱（炒），连翘1钱，肉桂1钱，升麻7分，炙甘草5分，黄柏5分。', '内托黄耆酒煎汤（《东垣试效方》卷三）、内托酒煎汤（《医学入门》卷八）、黄耆柴胡汤（《杏苑》卷八）、内托酒煎散（《外科大成》卷二）。本方方名，《医学正传》引作“托里黄耆汤”；《疡科选粹》引作“柴胡黍粘子汤”。', '《兰室秘藏》卷下', '附骨痈，坚硬漫肿，不辨肉色，行步作痛，按之大痛。', '-', '-', '升麻', '[\'黄耆\', \'当归\', \'柴胡\', \'钱分\', \'粘子\', \'连翘\', \'肉桂\', \'升麻\', \'甘草\', \'黄柏\']');
INSERT INTO `prescript` VALUES (60165, '9318', '大金牙散', '金牙3分，曾青（研，飞）3分，雄黄（研）3分，大黄（锉，炒）3分，丹砂（研，飞）3分，牛黄（研）3分，凝水石（煅，研）3分，野葛皮3分，龙骨3分，朴消（研）3分，犀角（镑）3分，獭肝（切片，炙干）3分，狸骨（酯，炙）3分，鹳骨（炙黄）3分，升麻1两半，附子（去皮脐，生用）半两，桂（去粗皮）半两，鬼臼半两，鬼督邮半两，黄环半两，青木香半两，常山半两，牡蛎（煅，研）半两，人参半两，知母（切、焙）半两，徐长卿半两，鬼箭羽1分，桔梗（炒）1分，代赭石1分，莽草1分，蜀漆1分，当归（切，焙）1分，白薇1分，巴豆（去', '如传尸骨蒸，注癖、冷气积聚，每服半钱匕，热汤调下，1日2次，病愈即止。仍以绛囊盛方寸匕带之，辟诸鬼气了如有诸邪病，狂言妄见者，带2、3钱；若中恶心痛、注忤喘满、鼓胀闷乱诸疾，每服4钱匕，米饮调下，吐利为应；若蛇虫毒疮，以津唾调敷之；若温疫瘴疠，服如前法；若虫毒，吐出即愈；若卒死中恶，汤调1钱匕，灌下即愈；卒中邪毒，眼戴上视，狂言者，亦用汤化3钱匕，未愈更服。若忽有恶疮肿，以汤和敷之，日1易，愈即止。', '《圣济总录》卷九十三', '传尸骨蒸、肺痿；及诸蛊注忤，虫蛇蜂蝎等毒；或恶疮肿瘤，中恶卒心痛，大小便不通，心腹鼓胀，疫疡瘴毒，积癖邪气。', '-', '-', '升麻', '[\'金牙\', \'分曾\', \'青研\', \'飞分\', \'雄黄\', \'研分\', \'大黄\', \'炒分\', \'丹砂\', \'研飞分\', \'牛黄\', \'研分\', \'凝水石\', \'研分\', \'野葛\', \'皮分\', \'龙骨\', \'分朴\', \'消研分\', \'犀角\', \'分獭\', \'切片\', \'炙干\', \'分狸\', \'骨酯\', \'分鹳\', \'黄分\', \'升麻\', \'两半\', \'附子\', \'去皮\', \'脐生用\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'鬼臼\', \'半两\', \'鬼督邮\', \'半两\', \'黄环\', \'半两\', \'青木香\', \'半两\', \'常山\', \'半两\', \'牡蛎\', \'煅研\', \'半两\', \'人参\', \'半两\', \'知母\', \'切焙\', \'半两\', \'徐长卿\', \'半两\', \'鬼箭羽\', \'桔梗\', \'炒分\', \'代赭石\', \'莽草\', \'分蜀\', \'漆分\', \'当归\', \'切焙分\', \'白薇分\', \'巴豆\']');
