INSERT INTO `prescript` VALUES (60169, '9088', '常山散', '恒山1两，鳖甲1两（涂醋，炙令黄，去裙襕），川升麻1两，栀子仁半两。', '空腹以温水调2钱服。以微吐为度，未吐再服。', '方出《圣惠》卷五十二，名见《圣济总录》卷三十四', '间日疟，发作无时，寒热不止。', '-', '-', '升麻', '[\'恒山\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'升麻\', \'栀子\', \'半两\']');
INSERT INTO `prescript` VALUES (60170, '9006', '麻黄羌活散', '麻黄（去根）1分，羌活1分，牡丹皮（去心）1分，独活1分，山栀1分，柴胡（去苗）1分，桔梗（去芦）1分，升麻1分，荆芥穗1分，大黄1分，知母1分，黄芩1分，半夏4铢，牵牛半两。', '每服3钱，水1盏，生姜2片，同煎至7分，临发壮热时，和滓吃。须是初发热时服，仍是食后吃，发了已寒，即更不要吃，待第二发热时吃也。', '《续易简方》卷三', '温疟，初发浑身大热，头痛不可忍，临醒时即寒栗战动，逡巡便醒。', '-', '-', '升麻', '[\'麻黄\', \'根分\', \'羌活\', \'牡丹皮\', \'心分\', \'独活\', \'分山\', \'栀分\', \'柴胡\', \'苗分\', \'桔梗\', \'芦分\', \'升麻\', \'荆芥\', \'穗分\', \'大黄\', \'知母\', \'黄芩\', \'半夏\', \'牵牛\', \'半两\']');
INSERT INTO `prescript` VALUES (60171, '8997', '黄耆散', '黄耆1两半（锉），赤茯苓1两，地骨皮1两，麦门冬1两（去心），生干地黄1两，黄芩1两，川升麻1两，射干1两，赤芍药1两，玄参1两，甘草1两（生锉）。', '每服4钱，用水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十二', '发脑。肿痛烦热不可忍。', '-', '-', '升麻', '[\'黄耆\', \'两半\', \'茯苓\', \'地骨皮\', \'麦门冬\', \'两去\', \'心生\', \'地黄\', \'黄芩\', \'两川\', \'升麻\', \'射干\', \'两赤\', \'芍药\', \'玄参\', \'甘草\', \'两生\']');
INSERT INTO `prescript` VALUES (60172, '8883', '大青散', '大青3分，犀角屑3分，玄参3分，黄芩3分，麦门冬3分（去心），川升麻3分，荠苨3分，甘草半两（炙微赤，锉），知母3分，石膏1两，枳壳1两（麸炒微黄，去瓤），川大黄1两（锉碎，微炒），红雪1两，吴蓝3分，葛根1两（锉）。', '本方方名，《普济方》引作“大青龙散”。', '《圣惠》卷四十五', '服乳石，补养过度，饮酒、食肉热面大多，致脚气发盛攻心，烦热，躁渴，闷乱，神思恍惚。', '-', '-', '升麻', '[\'大青分\', \'犀角\', \'玄参\', \'黄芩\', \'麦门冬\', \'分去\', \'心川\', \'升麻\', \'甘草\', \'半两\', \'炙微\', \'知母\', \'石膏\', \'枳壳\', \'两麸\', \'微黄\', \'大黄\', \'红雪\', \'吴蓝分\', \'葛根\']');
INSERT INTO `prescript` VALUES (60173, '8869', '黄耆汤', '黄耆（锉，炒）1两，升麻半两，犀角（镑）半两，紫葛3分，木通（锉）3分。', '每服5钱匕，以水1盏半，煎至1盏，下芒消半钱匕，滤去滓，空心温服。取利2-3行为度，未利再服。', '《圣济总录》卷一二八', '痈疽恶疮久远，脓水不尽，变为瘘。', '-', '-', '升麻', '[\'黄耆\', \'升麻\', \'半两\', \'犀角\', \'半两\', \'紫葛分\', \'木通\', \'锉分\']');
INSERT INTO `prescript` VALUES (60174, '8868', '大麻仁丸', '大麻仁2两（锉，研如膏），防风1两（去芦头），枳壳1两（麸炒微黄，去瓤），旋覆花1两，川大黄3两（锉碎，微炒），木香1两，槟榔1两，川升麻1两，杏仁1两（汤浸，去皮尖双仁，麸炒微黄）。', '本方方名，《普济方》引作“火麻仁丸”。', '《圣惠》卷六', '肺脏风毒，皮肤结硬，及遍身瘙痒生疮，大肠不利。', '-', '-', '升麻', '[\'麻仁\', \'研如膏\', \'防风\', \'两去\', \'芦头\', \'枳壳\', \'两麸\', \'微黄\', \'旋覆花\', \'两川\', \'大黄\', \'木香\', \'槟榔\', \'两川\', \'升麻\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (60175, '8842', '黄连汤', '黄连（去须）1两，黄芩（去黑心）1两，升麻1两，甘草（炙）3分，朴消（研）半两。', '每服3钱匕，水1盏，加竹叶3-7片，煎至6分，去滓温服，早、晚食后各1次。', '《圣济总录》卷三十二', '伤寒后，毒气上攻，眼生浮翳赤痛．', '-', '-', '升麻', '[\'黄连\', \'黄芩\', \'黑心\', \'升麻\', \'甘草\', \'分朴\', \'消研\', \'半两\']');
INSERT INTO `prescript` VALUES (60176, '8835', '独活散', '独活1两，羌活1两，川升麻（锉细）1两，酸枣仁（拣净）1两，人参（去芦头）1两，琥珀半两，川大黄（锉细，微炒）半两。', '每服1钱，以水1小盏，入金、银、薄荷各少许，放温服，不拘时候。', '《幼幼新书》卷十一引张涣方', '祛风截痫。主', '-', '-', '升麻', '[\'独活\', \'羌活\', \'两川\', \'升麻\', \'锉细\', \'酸枣仁\', \'人参\', \'芦头\', \'琥珀\', \'半两\', \'大黄\', \'细微\', \'半两\']');
