INSERT INTO `prescript` VALUES (60178, '8816', '葱豉汤', '葱白8茎，香豉2合，蓝叶3分，柴胡1两（去苗），川升麻半两，犀角屑半两，赤芍药半两，甘草半两（炙微赤）。', '以水5大盏，煎至2盏半，去滓，分5次温服。未效，再合服之。', '《圣惠》卷三十八', '五石忽发动，体热烦疼，心躁闷乱。', '-', '-', '升麻', '[\'葱白\', \'香豉\', \'合蓝叶分\', \'柴胡\', \'两去\', \'苗川\', \'升麻\', \'半两\', \'犀角\', \'半两\', \'芍药\', \'半两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60179, '8806', '不龟手膏', '猪脂油4两，白蜡2两（溶化，离火加），白芷1钱，升麻1钱，猪牙皂1钱，丁香5分，麝香2分。', '先用葱汤洗手净，拭干，烘手热，取前膏1块于手心内搓之，令手掌油润，去药，则只手于火上烘之、搓之，以油干为度。', '《外科大成》卷四', '滋润肌肤。主冬月手背裂痛。', '-', '-', '升麻', '[\'脂油\', \'白蜡\', \'溶化\', \'离火加\', \'白芷\', \'升麻\', \'猪牙皂\', \'丁香\', \'麝香\']');
