INSERT INTO `prescript` VALUES (60185, '8690', '麻黄汤', '麻黄（去根节，制过）1钱，升麻1钱，牛蒡子（炒）1钱，蝉壳（洗净，去足翅）1钱，甘草1钱。', '加腊茶叶1钱，以水1盏，煎至7分，去滓服。烦渴，加石膏末4钱。', '《准绳·幼科》卷六', '托里发表。主发热42日以后，明是疹子，却不见出，此皮肤坚厚，腠理闭密，又或为风寒袭之，曾有吐利，故伏而不出。', '-', '-', '升麻', '[\'麻黄\', \'节制\', \'过钱\', \'升麻\', \'牛蒡子\', \'洗净\', \'甘草\']');
