INSERT INTO `prescript` VALUES (60188, '8648', '黄芩散', '黄芩1两，栀子仁1两，黄连（去须）1两，葳蕤1两，川升麻1两，蕤仁（汤浸，去赤皮）1两，甘草（炙微赤，锉）1两，犀角屑半两。', '萎蕤犀角散（《圣济总录》卷一○六）。', '《圣惠》卷三十三', '眼生蟹目，黑睛疼痛。', '-', '-', '升麻', '[\'黄芩\', \'栀子\', \'黄连\', \'葳蕤\', \'两川\', \'升麻\', \'蕤仁\', \'汤浸\', \'赤皮\', \'甘草\', \'炙微\', \'犀角\', \'半两\']');
INSERT INTO `prescript` VALUES (60189, '8619', '麻黄汤', '麻黄（去根节，先煎，掠去沫，焙）3两，桂（去粗皮）半两，独活（去芦头）3分，羚羊角（镑）3分，萎蕤（切，焙）1两，葛根（锉）3两，升麻1两半，防风（去叉）1两半，石膏（碎）6两，甘草（炙，锉）3分。', '每服5钱匕，以水1盏半，煎至8分，去滓温服，如人行5里再服。用热生姜稀粥投之汗出，慎外风。', '《圣济总录》卷五', '中风肢体弛缓，言语謇涩，精神惛愦。', '-', '-', '升麻', '[\'麻黄\', \'根节\', \'掠去\', \'两桂去\', \'粗皮\', \'半两\', \'独活\', \'芦头分\', \'羚羊角\', \'分萎\', \'蕤切\', \'葛根\', \'升麻\', \'两半\', \'防风\', \'两半\', \'石膏\', \'甘草\']');
INSERT INTO `prescript` VALUES (60190, '8603', '解苦散', '玄参5钱，生地5钱，羌活1钱，黄柏2钱，白茯苓3钱，升麻5分，丹皮3钱。', '水煎服。4剂自散。', '《洞天奥旨》卷十一', '天火丹。', '-', '-', '升麻', '[\'玄参\', \'生地\', \'羌活\', \'黄柏\', \'白茯苓\', \'升麻\', \'丹皮\']');
INSERT INTO `prescript` VALUES (60191, '8560', '麻黄汤', '麻黄（去根节，煎，掠去沫，焙）1两半，芎1两半，升麻1两，防风（去叉）1两，防己1两，桂（去粗皮）1两，羚羊角（镑）1两。', '每用5钱匕，以水1盏半，煎取1盏，去滓，入竹沥半合，再煎3-4沸，去滓，分2次温服。', '《圣济总录》卷一五○', '妇人中风，口面斜。', '-', '-', '升麻', '[\'麻黄\', \'根节\', \'掠去\', \'两半\', \'两半\', \'升麻\', \'防风\', \'防己\', \'两桂去\', \'粗皮\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (60192, '8540', '川升麻散', '川升麻1两，半夏半两（汤洗7遍去滑），赤茯苓3分，枳壳1两（麸炒微黄，去瓤），黄芩1两，杏仁半两（汤浸，去皮尖双仁，麸炒微黄），细辛半两，羚羊角屑半两，生干地黄1两，甘草3分（炙微赤，锉）。', '每服3钱，以水1中盏，加生姜半分，苦竹叶2-7片，煎至6分，去滓，食后温服。', '《圣惠》卷三十三', '脏腑中痰气，热毒冲眼。', '-', '-', '升麻', '[\'升麻\', \'半夏\', \'半两\', \'洗遍\', \'茯苓\', \'枳壳\', \'两麸\', \'微黄\', \'黄芩\', \'杏仁\', \'半两\', \'汤浸\', \'皮尖\', \'双仁麸\', \'微黄\', \'细辛\', \'半两\', \'羚羊角\', \'半两\', \'生干\', \'地黄\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (60193, '8509', '柴胡汤', '柴胡（去苗）1两半，大黄（锉，炒）1两半，决明子（炒）1两，泽泻1两，升麻1两，芍药1两，白茯苓（去黑皮）1两，枳壳（去瓤，麸炒）1两，栀子仁1两，黄芩（去黑心）1两，黄连（去须）1两，细辛（去苗叶）1两，杏仁（汤浸，去皮尖双仁）1两，甘草（炙，锉）2两。', '每服5钱匕，水1盏半，苦竹叶10片，煎至1盏，去滓，投芒消末1钱匕，食后临卧温服。', '《圣济总录》卷一○九', '眼赤息肉，生翳膜，漠不见物。', '-', '-', '升麻', '[\'柴胡\', \'去苗\', \'两半\', \'大黄\', \'两半\', \'决明子\', \'泽泻\', \'升麻\', \'芍药\', \'白茯苓\', \'黑皮\', \'枳壳\', \'栀子\', \'黄芩\', \'黑心\', \'黄连\', \'细辛\', \'苗叶\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'甘草\']');
INSERT INTO `prescript` VALUES (60194, '8495', '黄连膏', '黄连1两（去须），黄柏半两，川升麻半两，蕤仁1两（去赤皮，研），细辛1两，石胆1豆许（研）。', '每日点少许于两目眦头。', '《圣惠》卷三十二', '眼赤痛不开。眼赤涩，疼痛不开，兼飞血赤痛。', '-', '-', '升麻', '[\'黄连\', \'两去\', \'黄柏\', \'半两\', \'升麻\', \'半两\', \'蕤仁\', \'两去\', \'赤皮研\', \'细辛\', \'两石\', \'胆豆\', \'许研\']');
INSERT INTO `prescript` VALUES (60195, '8477', '麻黄解肌汤', '麻黄1两，甘草1两，升麻1两，芍药1两，石膏1两，杏仁2枚，贝齿3枚。', '以水3升，煮取1升，顿服。覆取汗出。后食豉粥补虚。', '《肘后方》卷二', '解肌。主时行疫疠1-2日，头痛壮热烦躁。', '-', '-', '升麻', '[\'麻黄\', \'甘草\', \'升麻\', \'芍药\', \'石膏\', \'杏仁\', \'贝齿\']');
INSERT INTO `prescript` VALUES (60196, '8460', '麻黄汤', '麻黄1两，大枣20枚，茯苓3两，杏仁30枚，防风2两，白术2两，当归2两，升麻2两，芎2两，芍药2两，黄芩2两，桂心2两，麦门冬2两，甘草2两。', '上（口父）咀。以水9升，入清酒2升合煮，取2升半，分为4服，日3次，夜1次。覆令小汗，粉之，莫令见风。', '《千金》卷七', '恶风毒气冲心，脚弱无力，顽痹四肢不仁，失音不能言。', '忌海藻、菘菜、生葱、桃李、雀肉、酢物。', '-', '升麻', '[\'麻黄\', \'大枣\', \'茯苓\', \'杏仁\', \'防风\', \'白术\', \'当归\', \'升麻\', \'两芎\', \'芍药\', \'黄芩\', \'两桂心\', \'麦门冬\', \'甘草\']');
INSERT INTO `prescript` VALUES (60197, '8361', '黄芩汤', '黄芩（去黑心）1两半，升麻1两，木通（锉）1两，芍药1两，枳实（去瓤，麸炒）1两半，柴胡（去苗）1两，羚羊角（镑）1两，石膏（碎）2两，杏仁（汤浸，去皮尖双仁，炒）1两。', '每服3钱匕，以水1盏，煎至5分，去滓温服。', '《圣济总录》卷一二二', '风热客于肺经，上搏咽喉，气壅肿痛，语声不出。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'两半\', \'升麻\', \'木通\', \'芍药\', \'枳实\', \'两半\', \'柴胡\', \'羚羊角\', \'石膏\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\']');
INSERT INTO `prescript` VALUES (60198, '8347', '荆沥汤', '荆沥3升，麻黄4两，白术4两，芎藭4两，防风2两，桂心2两，升麻2两，茯苓2两，远志2两，人参2两，羌活2两，当归2两，母姜1升（切，取汁），防己2两，甘草2两。', '上（口父）咀。以水1斗5升，煎麻黄2沸，去沫，次下诸药，煮取3升，去滓，下荆沥、姜汁，煎取4升，分4次服，日3夜1。', '《千金》卷八', '心虚寒。阴气伤寒损心，惊掣悸，语声宽急混浊，口，冒昧，好自笑，厉风伤心者。', '-', '-', '升麻', '[\'荆沥升\', \'麻黄\', \'白术\', \'防风\', \'两桂心\', \'升麻\', \'茯苓\', \'远志\', \'人参\', \'羌活\', \'当归\', \'两母\', \'姜升切\', \'取汁\', \'防己\', \'甘草\']');
INSERT INTO `prescript` VALUES (60199, '8346', '羚羊角散', '羚羊角屑1两，防风1两（去芦头），川升麻1两，茯神1两，蕤仁1两（汤浸，去赤皮），麦门冬1两（去心，焙），地骨皮1两，决明子1两，甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓，食后温服。', '《医方类聚》卷六十六引《圣惠》', '眼生丁翳，风热上攻，泪出赤涩。', '-', '-', '升麻', '[\'羚羊角\', \'防风\', \'两去\', \'芦头川\', \'升麻\', \'茯神\', \'蕤仁\', \'两汤\', \'浸去\', \'赤皮\', \'麦门冬\', \'两去\', \'地骨皮\', \'决明子\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60200, '8335', '黄连汤', '黄连1两（去须），川升麻1两，麦门冬1两（去心），黄芩1两，栝楼根1两，知母1两，茯神半两，栀子仁1两，甘草1两（炙微赤，锉），石膏2两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '方出《圣惠》卷五十三，名见《普济方》卷一七九', '脾胃中热烦渴，身渐消瘦。', '-', '-', '升麻', '[\'黄连\', \'两去\', \'须川\', \'升麻\', \'麦门冬\', \'两去\', \'黄芩\', \'栝楼\', \'根两\', \'知母\', \'茯神\', \'半两\', \'栀子\', \'甘草\', \'两炙微\', \'石膏\']');
INSERT INTO `prescript` VALUES (60201, '8331', '黄连解毒汤', '黄连1钱半，黄柏1钱半，栀子1钱半，木香3分，犀角1钱（无，以升麻代之）。', '水1盏半，煎7分服。', '《证治要诀类方》卷一', '伤寒，因饮食复剧，烦闷干呕，口噪呻吟，错语不得眠。', '-', '-', '升麻', '[\'黄连\', \'钱半\', \'黄柏\', \'钱半\', \'栀子\', \'钱半\', \'木香\', \'犀角\', \'无以\', \'升麻\', \'代之\']');
