INSERT INTO `prescript` VALUES (60203, '8249', '黄耆散', '黄耆1两（锉），赤茯苓1两，人参1两（去芦头），麦门冬1两（去心），枳壳1两（麸炒，微黄，去瓤），川升麻1两，前胡1两（去芦头），百合1两，赤芍药1两，紫菀1两（洗去苗土），甘草1两（炙微赤，锉），沙参1两，知母1两。', '每服3钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六', '肺脏壅热，心胸不利，吃食全少，四肢烦疼。', '-', '-', '升麻', '[\'黄耆\', \'茯苓\', \'人参\', \'两去\', \'芦头\', \'麦门冬\', \'两去\', \'枳壳\', \'两麸\', \'微黄\', \'升麻\', \'两前\', \'胡两去\', \'芦头\', \'百合\', \'两赤\', \'芍药\', \'紫菀\', \'两洗\', \'苗土\', \'甘草\', \'两炙微\', \'沙参\', \'知母\']');
INSERT INTO `prescript` VALUES (60204, '8232', '柴胡汤', '柴胡（去苗）半两，升麻半两，黄芩（去黑心）半两，甘草（炙赤）半两，大黄（锉，炒令香）半两，石膏（碎）3分，钩藤1分，蚱蝉（去翅足，微炙）1枚，蛇蜕皮2寸（炙令黄色）。', '每服1钱匕，水1小盏，煎至半盏，去滓，加竹沥半合，更煎1-2沸，半月至1月儿，斟酌与之；稍大，以意增加。', '《圣济总录》卷一七一', '未满月及出月儿壮热发痫。', '-', '-', '升麻', '[\'柴胡\', \'去苗\', \'半两\', \'升麻\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'甘草\', \'半两\', \'大黄\', \'令香\', \'半两\', \'石膏\', \'碎分\', \'钩藤\', \'蚱蝉\', \'微炙枚\', \'蜕皮\', \'寸炙令\', \'黄色\']');
INSERT INTO `prescript` VALUES (60205, '8214', '黄连膏', '黄连（去须）1两，升麻1两，槐白皮1两，大青1两，苦竹叶1两。', '以水2升，煎至半升，去滓取汁，入龙脑、蜜，搅令匀，煎成膏。涂疮上，日3次。', '《圣济总录》卷一一七', '久患口疮。', '-', '-', '升麻', '[\'黄连\', \'升麻\', \'两槐\', \'白皮\', \'两大青\', \'苦竹叶\']');
INSERT INTO `prescript` VALUES (60206, '8197', '柴胡丸', '柴胡（去苗）1两半，胡黄连1两半，龙胆1两半，桃胶（干者）1两半，升麻1两半，茯神（去木）1两半，黄芩（去黑心）1两半，地骨皮1两半，生干地黄（焙）1两半，芍药1两半，大黄（锉，焙）1两半，知母（锉，焙）1两半，麦门冬（去心，焙）1两半，甘草（炙，锉）1两半，龙齿1两半，犀角（镑）1两半，玄参1两半，山栀子（去皮）1两半，桔梗（炒）1两半，丹砂2两（别研入药）。', '每服30丸，空心以熟水送下。', '《圣济总录》卷九十三', '骨蒸热劳，心烦闷，手足背膊酸疼，四肢沉重，食不作肌肤，日渐黄瘦。', '-', '-', '升麻', '[\'柴胡\', \'去苗\', \'两半\', \'胡黄连\', \'两半\', \'龙胆\', \'两半\', \'桃胶\', \'干者\', \'两半\', \'升麻\', \'两半\', \'茯神\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'地骨皮\', \'半生\', \'地黄\', \'两半\', \'芍药\', \'两半\', \'大黄\', \'两半\', \'知母\', \'两半\', \'麦门冬\', \'两半\', \'甘草\', \'两半\', \'龙齿\', \'两半\', \'犀角\', \'两半\', \'玄参\', \'半山\', \'栀子\', \'去皮\', \'两半\', \'桔梗\', \'两半\', \'丹砂\', \'两别研\', \'入药\']');
INSERT INTO `prescript` VALUES (60207, '8189', '柴胡散', '柴胡2两（去苗），川升麻2两，栀子仁2两，赤芍药2两，木通2两（锉），黄芩1两半，大青1两半，杏仁1两半（汤浸，去皮尖双仁），石膏3两。', '每服5钱，以水1大盏，加生姜半分，煎至5分，食后温服。', '《圣惠》卷三十六', '腹中虚热，舌本强直，口吻两边痛，舌上有疮，咽食不得。', '-', '-', '升麻', '[\'柴胡\', \'两去\', \'苗川\', \'升麻\', \'栀子\', \'芍药\', \'木通\', \'黄芩\', \'两半\', \'大青\', \'两半\', \'杏仁\', \'两半\', \'汤浸\', \'皮尖\', \'双仁\', \'石膏\']');
INSERT INTO `prescript` VALUES (60208, '8051', '黄芩汤', '黄芩（去黑心）3分，杏仁（去皮尖双仁，炒）3分，麻黄（去根节，汤煮，掠去沫，焙）3分，羌活（去芦）3分，人参3分，升麻3分，桔梗（炒）3分，黄连（去须）半分，蛤蚧（酥炙）半两。', '每服3钱，水1盏，煎5沸，去滓，先宜吃解上焦散子，食后、临卧服；未愈，更服葶苈丸。', '《普济方》卷二十七引《护命》', '上焦壅热，久患肺与喘急，喉中作声，不能起动。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'杏仁\', \'皮尖\', \'双仁\', \'炒分\', \'麻黄\', \'根节\', \'掠去\', \'焙分\', \'羌活\', \'芦分\', \'人参\', \'升麻\', \'桔梗\', \'炒分\', \'黄连\', \'半分\', \'蛤蚧\', \'半两\']');
INSERT INTO `prescript` VALUES (60209, '8034', '柴胡下热汤', '柴胡3两，黄芩3两，泽泻3两，升麻3两，芒消3两，玄参6两，淡竹叶（切）1升，生地黄（切）1升，干姜2两。', '以水9升，煮取3升，去滓，下芒消，平旦分3服。', '《外台》卷十六引《删繁方》', '肝劳。热闷，关格不通，精神不守，气逆上胸，热炎炎不止。', '忌芜荑。', '-', '升麻', '[\'柴胡\', \'黄芩\', \'泽泻\', \'升麻\', \'两芒\', \'消两\', \'玄参\', \'淡竹叶\', \'切升\', \'生地黄\', \'切升\', \'干姜\']');
INSERT INTO `prescript` VALUES (60210, '8027', '羚羊角散', '羚羊角屑1两，槟榔1两，木通1两（锉），枳壳1两（麸炒微黄，去瓤），红雪1两，甘草半两（炙微赤，锉），川升麻3分。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，每日3-4次。', '《圣惠》卷三十八', '乳石发动，三焦气壅，心神烦闷，大小肠壅滞不通。', '-', '-', '升麻', '[\'羚羊角\', \'槟榔\', \'木通\', \'枳壳\', \'两麸\', \'微黄\', \'红雪\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'升麻\']');
INSERT INTO `prescript` VALUES (60211, '8022', '柴胡石膏散', '赤芍药50两，柴胡（去苗）50两，前胡（去苗）50两，石膏（煅）50两，干葛50两，升麻25两，黄芩37两，桑白皮37两，荆芥穗（去土）37两。', '柴胡升麻汤（原书同卷宝庆新增方）、柴胡石膏汤（《奇效良方》卷十）。', '《局方》卷二', '外感风热、少阳、阳明合病，壮热恶风，头痛身疼，鼻塞咽干，口渴心烦，咳嗽自汗，脉浮缓。时行瘟疫，心胸如满，寒热往来，痰实咳嗽，涕唾稠粘。伤风，及阳气郁遏，元气下陷。春夏感冒，声重声哑，甚至痰壅气喘，咳嗽咽干，自汗。', '-', '-', '升麻', '[\'芍药\', \'柴胡\', \'去苗\', \'两前\', \'胡去\', \'石膏\', \'两干葛\', \'升麻\', \'黄芩\', \'桑白皮\', \'荆芥\']');
INSERT INTO `prescript` VALUES (60212, '8011', '调中益气汤', '黄耆1钱，人参5分，苍术5分，陈皮5分，升麻3分，炙甘草3分，柴胡3分，黄柏3分，木香3分，当归5分，白术5分，白芍5分。', '-', '《嵩崖尊生》卷六', '病后耳鸣耳聋。', '-', '-', '升麻', '[\'黄耆\', \'人参\', \'苍术\', \'陈皮分\', \'升麻\', \'甘草\', \'柴胡\', \'黄柏\', \'木香\', \'当归\', \'白术\', \'白芍\']');
INSERT INTO `prescript` VALUES (60213, '7986', '救急解毒丸', '甘草2两，桔梗2两，荆芥1两，防风1两，连翘1两，酒芩1两，酒连1两，薄荷1两，升麻1两，酒大黄1两，僵蚕5钱，蒲黄5钱，青黛5钱，盆消5钱，射干5钱。', '噙化；煎汤亦可。', '《伤暑全书》卷下', '时行疫气，咽喉肿痛，项筋粗大，舌强声哑，鼻塞气闷，水浆难进。兼治头面浮肿，疙瘩坚硬，浸淫湿疮，耳内流脓，眼弦赤肿，口内糜烂；', '-', '-', '升麻', '[\'甘草\', \'桔梗\', \'荆芥\', \'防风\', \'连翘\', \'两酒\', \'两酒\', \'薄荷\', \'升麻\', \'两酒\', \'大黄\', \'僵蚕\', \'黄钱\', \'青黛钱\', \'消钱\', \'射干\']');
INSERT INTO `prescript` VALUES (60214, '7970', '羚羊角汤', '羚羊角1两（镑），决明子1两（炒），犀角1两（镑），石膏1两，地骨皮1两，玄参半两，细辛半两（去苗叶），黄芩（去黑心）半两，防风3分（去叉），芎3分，柴胡3分（去苗），升麻3分。', '每服5钱匕，以水1盏半，加竹叶10片，煎至7分，去滓，入芒消末半钱匕，食后、临卧温服，每日2次。', '《圣济总录》卷一○四', '风毒目赤肿痛，肝肺风热。', '-', '-', '升麻', '[\'羚羊角\', \'两镑\', \'决明子\', \'犀角\', \'两镑\', \'石膏\', \'地骨皮\', \'玄参\', \'半两\', \'细辛\', \'半两\', \'苗叶\', \'黄芩\', \'黑心\', \'半两\', \'防风\', \'分去\', \'柴胡\', \'分去\', \'升麻\']');
