INSERT INTO `prescript` VALUES (60217, '7945', '琥珀膏', '归须1两，川芎1两，黄耆梢1两，蜂房1两，细辛1两，皂角1两，升麻1两，甘草梢1两，蓖麻子1两，大鳖子1两，芍药1两，白蔹1两，独活1两，川椒1两，藁本1两，防风梢1两，枸杞子1两，菖蒲1两，降真香1两，官桂1两，瓜蒌1两，苏木1两，白芷1两，杏仁1两，黄连1两，槐枝1两，琥珀2钱半，沉香2钱半，木香2钱半，丁香2钱半，藿香2钱半，零陵香2钱半，云母石2钱半，乳香2钱半，雄黄2钱半，朱砂2钱半，安息香2钱半，甘松2钱半，轻粉1钱，麝香1钱，发灰5钱，白杋（枯）1两（以上16味为极细末），羊肾脂4两，蟾酥2', '如用，于纸上摊之，量疮口大小。', '《医学纲目》卷十九引朱丹溪方', '五发恶疮，疔肿，瘰疬，远年冷疳、痔漏，一切无名恶疮，蛇伤、蝎啮、犬咬。', '-', '-', '升麻', '[\'归须\', \'川芎\', \'两黄耆\', \'蜂房\', \'细辛\', \'两皂角\', \'升麻\', \'甘草\', \'蓖麻子\', \'两大鳖子\', \'芍药\', \'白蔹\', \'独活\', \'两川椒\', \'藁本\', \'防风\', \'枸杞子\', \'菖蒲\', \'两降\', \'真香\', \'两官桂\', \'两瓜\', \'苏木\', \'白芷\', \'杏仁\', \'黄连\', \'两槐枝\', \'琥珀\', \'钱半\', \'沉香\', \'钱半\', \'木香\', \'钱半\', \'丁香\', \'钱半\', \'藿香\', \'钱半\', \'零陵\', \'香钱\', \'云母石\', \'钱半\', \'乳香\', \'钱半\', \'雄黄\', \'钱半\', \'朱砂\', \'钱半\', \'安息香\', \'钱半甘松\', \'轻粉\', \'麝香\', \'发灰钱\', \'以上\', \'味为\', \'极细末\', \'肾脂\', \'蟾酥\']');
INSERT INTO `prescript` VALUES (60218, '7929', '菊花散', '甘菊花半两，人参半两（去芦头），赤茯苓半两，麦门冬半两（去心），犀角屑半两，甘草半两（炙微赤，锉），防风半两（去芦头），羌活半两，地骨皮半两，羚羊角屑半两，蔓荆子半两，川升麻半两。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十七', '上焦壅滞，头面风热。', '-', '-', '升麻', '[\'甘菊花\', \'半两\', \'人参\', \'半两\', \'芦头\', \'茯苓\', \'半两\', \'麦门冬\', \'半两\', \'去心\', \'犀角\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'防风\', \'半两\', \'芦头\', \'羌活\', \'半两\', \'地骨皮\', \'半两\', \'羚羊角\', \'半两\', \'蔓荆子\', \'半两\', \'升麻\', \'半两\']');
