INSERT INTO `prescript` VALUES (60220, '7910', '大黄散', '川大黄1两（锉碎，微炒），柴胡3分（去苗），川升麻3分，黄芩3分，枳壳3分（麸炒微黄，去瓤），赤芍药3分，栀子仁3分，石膏1两半，知母3分，杏仁3分（汤浸，去皮尖双仁，麸炒微黄）。', '每服1钱，以水1小盏，加青竹叶2-7片，煎至5分，去滓，量儿大小，分减服之。', '《圣惠》卷八十二', '小儿周岁至3岁壮热。', '-', '-', '升麻', '[\'大黄\', \'柴胡\', \'分去\', \'苗川\', \'升麻\', \'黄芩\', \'枳壳\', \'分麸\', \'微黄\', \'芍药\', \'栀子\', \'仁分\', \'石膏\', \'两半\', \'知母\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (60221, '7901', '麻黄散', '麻黄1两（去根节），芎1两，川升麻1两，防风1两（去芦头），汉防己1两，桂心1两，羚羊角屑1两，酸枣仁1两，秦艽半两（去苗）。', '每服4钱，以水1中盏，煎至5分，去滓，入竹沥1合，更煎1-2沸，温服，不拘时候。', '《圣惠》卷十九', '中风。口面斜，筋脉拘急。', '-', '-', '升麻', '[\'麻黄\', \'两去\', \'根节\', \'两川\', \'升麻\', \'防风\', \'两去\', \'芦头汉\', \'防己\', \'两桂心\', \'羚羊角\', \'酸枣仁\', \'秦艽\', \'半两\', \'去苗\']');
INSERT INTO `prescript` VALUES (60222, '7898', '鳖甲汤', '鳖甲（炙）3两，麻黄（去节）3两，升麻3两，前胡3两，羚羊角屑3两，桑根白皮5两，薤白（切）1升，香豉1升（熬，绵别裹），黄芩3两。', '以水1斗，煮取3升，去滓，分为3服。', '《外台》卷十六引《删繁方》', '劳热。四肢肿急，少腹满痛，颜色黑黄，关格不通。', '忌苋菜。', '-', '升麻', '[\'鳖甲\', \'麻黄\', \'去节\', \'升麻\', \'两前\', \'胡两\', \'羚羊角\', \'两桑根\', \'白皮\', \'薤白\', \'切升\', \'香豉\', \'升熬\', \'绵别\', \'黄芩\']');
INSERT INTO `prescript` VALUES (60223, '7882', '大黄汤', '大黄（锉，炒）4两，栀子仁（炒）4两，升麻2两，前胡（去芦头）2两。', '每服5钱匕，水1盏半，煎至8分，去滓，食前温服。', '《圣济总录》卷九十七', '荣卫否涩，蕴热不散，腹中烦满，大便不通。', '-', '-', '升麻', '[\'大黄\', \'栀子\', \'升麻\', \'两前\', \'胡去\', \'芦头\']');
INSERT INTO `prescript` VALUES (60224, '7828', '黄连汤', '黄连（去须）1两，大黄（生用）1两，大青（去根）半两，升麻半两，黄药半两，甘草（炙）3分。', '每服5钱匕，水2盏，加黑豆1撮，同煎至1盏，去滓，分温2服。病未退，每服更加芒消末半钱匕，汤成下。以微利为度。', '《圣济总录》卷一一九', '伤寒舌肿。', '-', '-', '升麻', '[\'黄连\', \'大黄\', \'生用\', \'两大青\', \'半两\', \'升麻\', \'半两\', \'黄药\', \'半两\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (60225, '7827', '蓝青散', '蓝青5钱，知母5钱，甘草5钱，杏仁（去皮尖）5钱，黄芩4钱，升麻4钱，柴胡4钱，寒水石4钱，石膏4钱，山栀4钱，赤芍药4钱，羚羊角3钱。', '每次3钱，水煎服。', '《直指》卷二十四', '一切丹毒赤肿。', '-', '-', '升麻', '[\'蓝青\', \'知母\', \'甘草\', \'杏仁\', \'皮尖\', \'黄芩\', \'升麻\', \'柴胡\', \'寒水石\', \'石膏\', \'钱山\', \'栀钱\', \'芍药\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (60226, '7798', '黄耆汤', '黄耆3两，麦门冬3两（去心），黄芩6分，栀子14枚，芍药3两，瓜蒌2两，熟地黄2两，升麻1两。', '以水1斗，煮取3升，分3次温服。', '《鬼遗》卷三', '除热止渴。主痈肿热盛，口燥患渴。乳石发动，热渴口干。', '-', '-', '升麻', '[\'黄耆\', \'麦门冬\', \'两去\', \'黄芩\', \'栀子\', \'芍药\', \'两瓜\', \'熟地黄\', \'升麻\']');
