INSERT INTO `prescript` VALUES (60228, '7779', '麻黄膏', '猪板油2斤（熬去渣入），麻黄2两，百部2两，风子肉2两，花椒2两，升麻1两，紫草1两，枯矾1两。', '外涂。', '《饲鹤亭集方》', '一切风寒湿毒或传染而起脓窠癞疥，或湿热湿毒，坐板成疮。', '-', '-', '升麻', '[\'板油\', \'渣入\', \'麻黄\', \'百部\', \'两风子\', \'花椒\', \'升麻\', \'紫草\']');
INSERT INTO `prescript` VALUES (60229, '7759', '羚羊角丸', '羚羊角（镑）2两，白藓皮1两半，升麻1两，蔓荆实1两，升麻2两（白者），秦艽（去苗土）2两，恶实（炒）1两，枳壳（去瓤，麸炒）1两。', '每服15丸，食后煎桑根白皮汤送下，每日3次。', '《圣济总录》卷五', '肺中风，气急，背项强硬，语声嘶败。', '-', '-', '升麻', '[\'羚羊角\', \'两白藓\', \'两半\', \'升麻\', \'两蔓\', \'荆实\', \'升麻\', \'两白者\', \'秦艽\', \'苗土\', \'两恶实\', \'枳壳\']');
