INSERT INTO `prescript` VALUES (60231, '7715', '黄耆散', '黄耆半两（锉），甘草半两（生锉），栀子仁半两，黄芩3分，玄参1两，赤茯苓半两，槟榔半两，川升麻3分，紫菀半两（洗去苗土），麦门冬1两（去心，焙），牛蒡子半两。', '每服2钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十五', '咽喉内生疮疼痛。', '-', '-', '升麻', '[\'黄耆\', \'半两\', \'甘草\', \'半两\', \'栀子\', \'半两\', \'黄芩\', \'玄参\', \'两赤\', \'茯苓\', \'半两\', \'槟榔\', \'半两\', \'升麻\', \'紫菀\', \'半两\', \'苗土\', \'麦门冬\', \'两去\', \'牛蒡子\', \'半两\']');
