INSERT INTO `prescript` VALUES (60233, '7654', '黑丸子', '山茵陈半两，蜀升麻半两，常山半两，芒消半分，麻黄（去节根）1两，官桂（去粗皮）1分，附子1个（烧黑留心）。', '每服5丸，吐不止，茅根竹叶汤送下；热攻泻血，蜜炒生姜汤送下；若吐血、眼眦出血者，生油、冷酒送下；伤寒手脚心冷，冷茶清送下；失音，竹沥酒送下。', '《普济方》卷三九四引《灵苑方》', '退热，定吐逆兼除食伤。主', '-', '-', '升麻', '[\'茵陈\', \'半两\', \'升麻\', \'半两\', \'常山\', \'半两\', \'芒消\', \'半分\', \'麻黄\', \'节根\', \'官桂去\', \'粗皮分\', \'附子\', \'留心\']');
INSERT INTO `prescript` VALUES (60234, '7638', '黄芩汤', '黄芩（去黑心）1两，茵陈蒿1两，升麻1两，栀子仁半两，柴胡（去苗）半两，龙胆半两，犀角（镑）1两。', '每服5钱匕，用水1盏半，煎至1盏，去滓，加生地黄汁1合，搅令匀，不拘时候温服。', '《圣济总录》卷二十八', '内消折热。主伤寒发黄，或先服利药未愈者。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'茵陈蒿\', \'升麻\', \'栀子\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'龙胆\', \'半两\', \'犀角\']');
INSERT INTO `prescript` VALUES (60235, '7633', '解毒辟瘟丹', '苍术10两，厚朴4两（姜汁炒），陈皮3两，炙草2两，苏叶6两，藿香4两，羌活6两，防风6两，北细辛4两，川芎4两，白芷3两，石膏5两，山楂肉4两，麦芽4两（炒），枳壳2两（炒），黄芩6两，升麻3两，黑山栀3两，神曲8两。', '每服姜、葱煎汤送下。', '《医方易简》卷四', '解散。主四时感冒风寒，头痛发热，及瘟疫山岚瘴气。', '-', '-', '升麻', '[\'苍术\', \'厚朴\', \'姜汁\', \'陈皮\', \'两炙草\', \'两苏叶\', \'藿香\', \'羌活\', \'防风\', \'两北\', \'细辛\', \'川芎\', \'白芷\', \'石膏\', \'山楂\', \'麦芽\', \'枳壳\', \'黄芩\', \'升麻\', \'黑山\', \'栀两\', \'神曲\']');
INSERT INTO `prescript` VALUES (60236, '7616', '棘刺散', '棘刺半两（烧灰），青葙子3两，当归1两，干姜（炮裂，锉）1两，菖蒲1两，香附子1两，鸡舌香1两，细辛1两，川升麻1两。', '每用半钱，以绵裹于患处咬之，咽津。以愈为度。', '《圣惠》卷三十四', '齿漏疳，脓血出，齿龈宣露，气臭，不能饮食。', '-', '-', '升麻', '[\'棘刺\', \'半两\', \'烧灰\', \'青葙子\', \'当归\', \'干姜\', \'炮裂\', \'菖蒲\', \'香附子\', \'两鸡\', \'舌香\', \'细辛\', \'两川\', \'升麻\']');
