INSERT INTO `prescript` VALUES (60252, '7172', '藁本汤', '藁本（去苗土）3两，芎3两，防风（去叉）3两，蔓荆实（去皮）3两，细辛（去苗叶）3两，羌活（去芦头）3两，升麻3两，木通（锉）3两，杨白皮（细切）2两，露蜂房（炙，劈碎）半两，狼牙草（切）半两，莽草（去梗）半两，盐半两，大豆（炒令香熟）2合。', '每用5钱匕，水1盏，入生地黄汁少许，煎十余沸，去滓，热漱冷吐。', '《圣济总录》卷一一九', '牙痛。', '-', '-', '升麻', '[\'藁本\', \'苗土\', \'两芎\', \'防风\', \'两蔓\', \'荆实\', \'去皮\', \'细辛\', \'苗叶\', \'羌活\', \'芦头\', \'升麻\', \'木通\', \'白皮\', \'细切\', \'露蜂房\', \'半两\', \'狼牙\', \'草切\', \'半两\', \'莽草\', \'半两\', \'半两\', \'大豆\', \'炒令香\', \'熟合\']');
INSERT INTO `prescript` VALUES (60253, '7170', '柴胡煮散', '柴胡（去苗）1两，人参1两，白茯苓（去黑皮）1两，当归（切，焙）1两，桔梗（锉，炒）1两，青橘皮（去白，炒）1两，芍药1两，芎藭1两，麦门冬（去心，焙）1两，白术1两，升麻1两，桑根白皮（锉）1两，甘草（炙，锉）1两。', '柴胡人参汤（原书卷一七九）。', '《圣济总录》卷八十八', '虚劳潮热，肢节烦疼，肌肤枯燥，面赤咽干。小儿骨热盗汗，肌瘦减食。', '-', '-', '升麻', '[\'柴胡\', \'人参\', \'白茯苓\', \'黑皮\', \'当归\', \'切焙\', \'桔梗\', \'两青\', \'橘皮\', \'芍药\', \'麦门冬\', \'白术\', \'升麻\', \'两桑根\', \'白皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (60254, '7110', '黄矾散', '黄矾（甘锅烧通赤研入）1两，生干地黄（焙）半两，胡桐泪半两，升麻半两，干虾蟆头2枚（炙焦）。', '每用半钱匕，干贴。良久吐津，甘草水漱口。1-2服立效。', '《圣济总录》卷一二一', '齿龈宣露，及骨槽风，小儿急疳，龈肉肿烂。', '-', '-', '升麻', '[\'黄矾甘锅\', \'烧通\', \'研入\', \'两生\', \'地黄\', \'半两\', \'胡桐泪\', \'半两\', \'升麻\', \'半两\', \'虾蟆\', \'头枚\']');
INSERT INTO `prescript` VALUES (60255, '7109', '黄芩汤', '黄芩（去黑心）半两，大青半两，山栀子仁半两，甘草（炙，锉）半两，升麻3分，麦门冬（去心，焙）3分。', '每服3钱匕，水1盏，加竹叶7片，煎至6分，去滓，食后温服，1日3-5次。', '《圣济总录》卷三十二', '伤寒后毒气上攻，咽喉疮痛，口疮，烦躁头痛。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'半两\', \'大青\', \'半两\', \'栀子\', \'半两\', \'甘草\', \'半两\', \'升麻\', \'麦门冬\', \'心焙分\']');
