INSERT INTO `prescript` VALUES (60257, '7060', '羚羊角升麻汤', '羚羊角半两（镑），升麻半两，白鲜皮半两，龙齿半两，木通1分（锉），百合1分，防风（去叉）1分，石膏1两。', '每服5钱匕，以水1盏半，豉100粒，葱白5寸，煎至1盏，去滓温服，不拘时候。', '《圣济总录》卷二十八', '伤寒刚痉，仰目壮热，筋脉不舒，牙关紧急，不欲见食。', '-', '-', '升麻', '[\'羚羊角\', \'半两\', \'升麻\', \'半两\', \'白鲜皮\', \'半两\', \'龙齿\', \'半两\', \'木通\', \'百合\', \'防风\', \'去叉分\', \'石膏\']');
