INSERT INTO `prescript` VALUES (60259, '7048', '大黄汤', '大黄（细锉，微炒）3分，木通（锉）3分，木香1分，升麻半两，羚羊角屑半两，白茅根（锉）半两，黄芩（去黑心）半两。', '每服5钱匕，用水1盏半，加葱白5寸，同煎至8分，去滓温服。', '《圣济总录》卷二十八', '伤寒，狂言欲走，大小便不通，腹痛胀满。', '-', '-', '升麻', '[\'大黄\', \'炒分\', \'木通\', \'锉分\', \'木香\', \'升麻\', \'半两\', \'羚羊角\', \'半两\', \'白茅根\', \'半两\', \'黄芩\', \'黑心\', \'半两\']');
INSERT INTO `prescript` VALUES (60260, '7034', '羚羊角汤', '羚羊角半两（镑），百合半两，芎半两，木通半两（锉），葛根半两（锉），升麻半两，黄芩（去黑心）半两，石膏（碎）1两，龙齿3分，防风（去叉）3分。', '每服5钱匕，以水1盏半，煎至1盏，去滓温服，每日2次，不拘时候。', '《圣济总录》卷二十八', '伤寒刚痓，浑身壮热，头疼口噤，筋脉拘急，心神躁闷。', '-', '-', '升麻', '[\'羚羊角\', \'半两\', \'百合\', \'半两\', \'半两\', \'木通\', \'半两\', \'葛根\', \'半两\', \'升麻\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'石膏\', \'龙齿\', \'防风\', \'去叉分\']');
