INSERT INTO `prescript` VALUES (60266, '6873', '马牙消煎', '马牙消1两半，木通（锉）1两半，升麻1两半，瞿麦穗1两半，犀角屑1两半，马蔺子1两半，射干1两，玄参1两。', '每服1匙头，含化咽津。', '《圣济总录》卷一二三', '狗咽。气塞肿痛，气欲绝者。', '-', '-', '升麻', '[\'马牙消\', \'两半\', \'木通\', \'两半\', \'升麻\', \'两半\', \'麦穗\', \'两半\', \'犀角\', \'两半\', \'马蔺\', \'两半\', \'射干\', \'玄参\']');
