INSERT INTO `prescript` VALUES (60278, '6496', '大蓟散', '大蓟根（洗）1两，犀角（镑）1两，升麻1两，桑白皮（炙）1两，蒲黄（炒）1两，杏仁（去皮尖）1两，桔梗（去芦）1两，甘草（炙）半两。', '大蓟饮子（《东医宝鉴·内景篇》卷二引《丹溪心法》。', '《济生》卷二', '饮啖辛热，热邪伤肺，呕吐出血1合或半升许，名曰肺疽。', '-', '-', '升麻', '[\'大蓟\', \'根洗\', \'犀角\', \'升麻\', \'桑白皮\', \'杏仁\', \'皮尖\', \'桔梗\', \'去芦\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60279, '6456', '羚羊角汤', '羚羊角屑半两，犀角屑半两，防风半两（去叉），茯神半两（去木），黄芩半两（去黑心），玄参半两，升麻半两，龙齿（研）1两，甘草（炙）1两，竹茹3分，地骨皮3分（洗，焙），人参3分。', '本方方名，《普济方》卷一三七引作“羚羊角散”。', '《圣济总录》卷二十八', '伤寒发汗后，热毒未尽，因有所惊，狂言欲走。', '-', '-', '升麻', '[\'羚羊角\', \'半两\', \'犀角\', \'半两\', \'防风\', \'半两\', \'茯神\', \'半两\', \'黄芩\', \'半两\', \'黑心\', \'玄参\', \'半两\', \'升麻\', \'半两\', \'龙齿\', \'研两\', \'甘草\', \'竹茹\', \'地骨皮\', \'分洗\', \'人参\']');
