INSERT INTO `prescript` VALUES (60282, '6406', '款冬花散', '款冬花半两，黄耆（锉）1两半，升麻1两，赤小豆1两1分，附子（炮裂，去皮脐）1两1分，苦参1两1分。', '每服1钱匕，加至2钱匕，空心温酒调服，每日2次。', '《圣济总录》卷一二九', '瘭疽。手足累累如米起，色白刮之汁出，愈后复发。', '-', '-', '升麻', '[\'款冬花\', \'半两\', \'黄耆\', \'两半\', \'升麻\', \'赤小豆\', \'两分\', \'附子\', \'炮裂\', \'去皮\', \'两分\', \'苦参\', \'两分\']');
