INSERT INTO `prescript` VALUES (60284, '6322', '黄耆散', '黄耆1两半（锉），生干地黄1两，赤芍药半两，川大黄1两半（锉碎，微炒），赤茯苓1两，知母1两，柴胡1两（去苗），川升麻1两，当归半两，木通1两（锉），甘草半两（生锉），羚羊角屑1两。', '每服4钱，以水1中盏，加小麦100粒，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十一', '痈肿。体热烦渴，肢节拘急，肩背疼痛。', '-', '-', '升麻', '[\'黄耆\', \'两半\', \'锉生\', \'地黄\', \'两赤\', \'芍药\', \'半两\', \'大黄\', \'两半\', \'茯苓\', \'知母\', \'柴胡\', \'两去\', \'苗川\', \'升麻\', \'当归\', \'半两\', \'木通\', \'甘草\', \'半两\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (60285, '6317', '黄连清胃丸', '炒山栀2两，连翘2两，大黄2两，黄芩2两，生石膏2两，丹皮2两，薄荷2两，知母3两，朴消1两，荆芥1两，生地1两，黄连1两，升麻1两，防风1两，当归尾1两，甘草1两，白芷1两，赤芍1两，玄参1两，花粉1两。', '每服2钱，以开水送下。', '《全国中药成药处方集》（沈阳方）', '泻热解毒，清胃通便。主口燥舌干，两腮焮肿，牙齿疼痛，齿根溃烂，口流热涎，烦渴饮冷，气息臭秽，头痛目赤，尿涩便结。', '忌食五辛荤腥；孕妇忌服。', '-', '升麻', '[\'炒山\', \'连翘\', \'大黄\', \'黄芩\', \'生石膏\', \'丹皮\', \'薄荷\', \'知母\', \'两朴\', \'消两\', \'荆芥\', \'生地\', \'黄连\', \'升麻\', \'防风\', \'当归\', \'甘草\', \'白芷\', \'赤芍\', \'玄参\', \'花粉\']');
INSERT INTO `prescript` VALUES (60286, '6306', '羚羊角饮子', '羚羊角2两（1对，镑取细末），车前子2两，决明子2两，防风2两，川升麻2两，绵黄耆2两，川大黄2两，黄芩2两，芒消2两。', '每服2钱，以水2盏，煎至1盏半。凡欲服药时，先将药末以水浸1宿，次日以此水并药同煎熟，倾入瓷瓶内，用油单纸封系，悬在井中1宿，至次日取出，微暖动，临卧时徐徐呷下。不得睡枕头，至明见效。如势不可缓，急要服者，空心、日午各1服。', '《博济》卷三', '斑疮后，翳膜忽生；风毒眼目暴赤等。', '-', '-', '升麻', '[\'羚羊角\', \'两对\', \'取细末\', \'车前子\', \'决明子\', \'防风\', \'两川\', \'升麻\', \'两绵\', \'黄耆\', \'两川\', \'大黄\', \'黄芩\', \'两芒\', \'消两\']');
INSERT INTO `prescript` VALUES (60287, '6305', '大腹皮散', '大腹皮1两，前胡1两（去芦头），半夏半两（汤浸7遍，去滑），旋覆花半两，枳壳1两（麸炒微黄，去瓤），赤茯苓1两，川大黄2两（锉碎，微炒），川升麻3分，川芒消1两，陈橘皮半两（汤浸，去白瓤，焙），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，1日3-4次。', '《圣惠》卷三十八', '乳石发动，心胸痰结，头目昏闷，大小肠壅滞不通，四肢烦疼，饮食不下。', '-', '-', '升麻', '[\'大腹皮\', \'两前\', \'胡两去\', \'芦头\', \'半夏\', \'半两\', \'浸遍\', \'旋覆花\', \'半两\', \'枳壳\', \'两麸\', \'微黄\', \'茯苓\', \'两川\', \'大黄\', \'碎微\', \'炒川\', \'升麻\', \'分川芒\', \'消两\', \'橘皮\', \'半两\', \'汤浸\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60288, '6301', '羚羊角汤', '羚羊角（镑）2两，羌活1两半（去芦头），黄芩1两半（去黑心），防风1两半（去叉），玄参1两半，车前子1两，人参1两，升麻1两，决明子1两，细辛（去苗叶）半两。', '每服3钱匕，以水1盏，煎至7分，去滓，食后、临卧服，每日2次。', '《圣济总录》卷一一二', '头旋眼暗，欲成青盲。', '-', '-', '升麻', '[\'羚羊角\', \'羌活\', \'两半\', \'芦头\', \'黄芩\', \'两半\', \'黑心\', \'防风\', \'两半\', \'玄参\', \'两半\', \'车前子\', \'人参\', \'升麻\', \'决明子\', \'细辛\', \'苗叶\', \'半两\']');
