INSERT INTO `prescript` VALUES (60290, '6282', '柴胡煎丸', '柴胡1两半（去苗），犀角屑1两，知母1两，胡黄连1两，桔梗（去芦头）1两，川升麻1两，地骨皮1两，黄芩1两，诃黎勒皮1两，栝楼1枚，鳖甲2两（涂醋炙令微黄，去裙襕），甘草3分（炙微赤，锉），赤茯苓3分，人参3分（去芦头）。', '每服20丸，食后煎乌梅、小便送下。', '《圣惠》卷二十七', '虚劳骨热，肢节烦疼，心膈躁闷。', '忌苋菜。', '-', '升麻', '[\'柴胡\', \'两半\', \'去苗\', \'犀角\', \'知母\', \'胡黄连\', \'桔梗\', \'芦头\', \'两川\', \'升麻\', \'地骨皮\', \'黄芩\', \'两诃黎勒皮\', \'栝楼\', \'鳖甲\', \'两涂\', \'炙令\', \'微黄\', \'甘草\', \'分炙微\', \'茯苓\', \'人参\', \'分去\', \'芦头\']');
INSERT INTO `prescript` VALUES (60291, '6279', '慈云散', '番木鳖2两，川乌2两，土鳖虫2两，鹿角（煅）2两，穿山甲1两6钱，明天麻1两，草乌1两，川芎1两，升麻1两，当归尾1两，闹羊花1两，生香附1两，僵蚕1两，蜈蚣4钱，斑蝥4钱。', '重者服1钱，轻者用6分或8分，俱用无灰酒调服。盖被取汗，不可见风，必须汗干，然后出帏幕。', '《伤科汇纂》卷七', '接骨回生。主跌打损伤，及痈疽疔肿大毒，初起即消，已成即溃。', '-', '-', '升麻', '[\'川乌\', \'土鳖虫\', \'鹿角\', \'穿山甲\', \'两钱明\', \'天麻\', \'草乌\', \'川芎\', \'升麻\', \'当归\', \'闹羊花\', \'两生\', \'香附\', \'僵蚕\', \'蜈蚣\', \'斑蝥\']');
INSERT INTO `prescript` VALUES (60292, '6267', '黄连八味散', '黄连1斤，黄芩1斤，干姜1斤，蜀升麻1斤，知母1斤，干地黄1斤，栀子仁半斤，大青半斤。', '每于食后饮服1方寸匕，日再服，稍加至2匕。', '《外台》卷四引《许仁则方》', '热病急黄贼风，服干葛散后，虽觉热气少退，热未能顿除者。', '忌猪肉、冷水、芜荑。', '-', '升麻', '[\'黄连\', \'黄芩\', \'干姜\', \'升麻\', \'知母\', \'地黄\', \'栀子\', \'半斤\', \'大青\', \'半斤\']');
INSERT INTO `prescript` VALUES (60293, '6210', '蔓荆子散', '蔓荆子1钱，粉干葛1钱，赤芍药1钱，信前胡1钱，桑白皮1钱，淮木通1钱，怀生地1钱，杭麦冬1钱，赤茯苓1钱，绿升麻5分，小甘草5分。', '加灯心10茎，水煎服。', '《幼幼集成》卷四', '小儿肾气上冲，灌为聤耳。', '-', '-', '升麻', '[\'蔓荆子\', \'钱粉\', \'干葛钱\', \'芍药\', \'信前\', \'胡钱\', \'桑白皮\', \'钱淮\', \'木通\', \'钱怀\', \'生地\', \'麦冬\', \'茯苓\', \'升麻\', \'分小\', \'甘草\']');
INSERT INTO `prescript` VALUES (60294, '6184', '解毒散', '犀角屑3分（麸炒），川升麻半两，栀子仁2分，木通1两（锉），麦门冬3分（去心），枳壳3分（麸炒微黄，去瓤），甘草3分（生锉），葛根3分（锉），地骨皮1两。', '每服4钱，以水1中盏，入生地黄1分，豉半合，煎至6分，去滓，不拘时候温服。', '《圣惠》卷六十二', '热毒发背，焮肿疼痛，烦热，渴。', '-', '-', '升麻', '[\'犀角\', \'分麸\', \'炒川\', \'升麻\', \'半两\', \'栀子\', \'仁分\', \'木通\', \'麦门冬\', \'分去\', \'枳壳\', \'分麸\', \'微黄\', \'甘草\', \'分生\', \'葛根\', \'地骨皮\']');
INSERT INTO `prescript` VALUES (60295, '6173', '大黑膏', '乌头半两，芎半两，雄黄半两，胡粉半两，木防己半两，升麻半两，黄连半两，雌黄半两，藜芦半两，矾石半两，杏仁（去皮尖）40枚，巴豆40枚，黄柏1分，松脂如鸡子大，乱发如鸡子大。', '用涂疮上，每日3敷，先以盐汤洗，然后涂之；若患人眉睫坠落不生者，服药后经100日外，即以铁浆洗其眉睫处所，1日3度洗之。生毛则速出1大彻，眉睫如本，与不患时同也。', '《千金翼》卷二十一引《耆婆恶病论》', '疾风，遍体生疮，脓血溃坏。', '-', '-', '升麻', '[\'乌头\', \'半两\', \'半两\', \'雄黄\', \'半两\', \'胡粉\', \'半两\', \'防己\', \'半两\', \'升麻\', \'半两\', \'黄连\', \'半两\', \'雌黄\', \'半两\', \'藜芦\', \'半两\', \'矾石\', \'半两\', \'杏仁\', \'皮尖\', \'巴豆\', \'黄柏\', \'松脂\', \'鸡子\', \'乱发\', \'鸡子\']');
INSERT INTO `prescript` VALUES (60296, '6156', '麻黄白术汤', '青皮（去腐）1分，酒黄连1分，酒黄柏2分，橘红2分，甘草（炙，末）2分，升麻2分，黄耆3分，人参3分，桂枝3分，白术3分，厚朴3分，柴胡3分，苍术3分，猪苓3分，吴茱萸4分，白茯苓4分，泽泻4分，白豆蔻5分，炒曲5分，麻黄（不去节）5钱，杏仁4个。', '麻黄白术散（《东垣试效方》卷七）。', '《兰室秘藏》卷下', '大便不通，五日一遍，小便黄赤，浑身肿，面上及腹尤甚，色黄，麻木，身重如山，沉困无力，四肢痿软，不能举动，喘促唾清水，吐哕，痰唾白沫如胶，时躁热发欲去衣，须臾而过则振寒，项额有时如冰，额寒尤甚，头旋眼黑，目中溜火，冷泪，鼻不闻香臭，少腹急痛，当脐有时动气，按之坚硬而痛。', '-', '-', '升麻', '[\'青皮\', \'腐分\', \'黄连\', \'分酒\', \'黄柏\', \'橘红\', \'甘草\', \'炙末分\', \'升麻\', \'分黄\', \'耆分\', \'人参\', \'桂枝\', \'白术\', \'厚朴\', \'柴胡\', \'苍术\', \'猪苓\', \'吴茱萸\', \'白茯苓\', \'泽泻\', \'白豆蔻\', \'曲分\', \'麻黄\', \'不去\', \'节钱\', \'杏仁\']');
INSERT INTO `prescript` VALUES (60297, '6137', '黄芩散', '黄芩1两，川升麻1两，黄连3分（去须），石膏1两，栀子仁1分，麻黄1两（去根节），甘草1分（炙微赤，锉）。', '每服4钱，以水1中盏煎至6分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病后，余热不解，身体沉重。', '-', '-', '升麻', '[\'黄芩\', \'两川\', \'升麻\', \'黄连\', \'分去\', \'石膏\', \'栀子\', \'仁分\', \'麻黄\', \'两去\', \'根节\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (60298, '6114', '大黄散', '川大黄2两（锉碎，微炒），黄芩1两，泽泻1两，川升麻1两，羚羊角屑1两，栀子仁1两，玄参1两，川芒消2两。', '每服5钱，以水1大盏，煎至5分，去滓，下生地黄汁半合，温温频服。', '《圣惠》卷四十七', '泻热，开隔绝。主中焦壅热，闭塞隔绝，上下不通，不吐不下，肠胃膨膨，喘息常急。', '-', '-', '升麻', '[\'大黄\', \'黄芩\', \'泽泻\', \'两川\', \'升麻\', \'羚羊角\', \'栀子\', \'玄参\', \'两川芒\', \'消两\']');
INSERT INTO `prescript` VALUES (60299, '6109', '羚羊角汤', '羚羊角1两半（镑），犀角（镑）1两半，升麻1两，旋复花1两，木香1两，大腹5颗（连皮子锉），枳壳1两1分（去瓤，麸炒），麦门冬1两1分（去心，焙），前胡（去芦头）1两1分。', '每服5钱匕，以水1盏半，煎至1盏，去滓，空腹服之。以快利为度。', '《圣济总录》卷八十四', '胸膈妨闷，噫气不畅，烦热口干，眼暗不识人。', '-', '-', '升麻', '[\'羚羊角\', \'两半\', \'犀角\', \'两半\', \'升麻\', \'旋复花\', \'木香\', \'两大腹\', \'皮子\', \'枳壳\', \'两分\', \'麦门冬\', \'两分\', \'心焙前\', \'胡去\', \'芦头\', \'两分\']');
INSERT INTO `prescript` VALUES (60300, '6105', '羚羊角汤', '羚羊角3分（镑），黄芩3分（去黑心），柴胡3分（去苗），升麻3分，甘草（生锉）1两。', '本方方名，《普济方》引作“羚羊角散”。', '《圣济总录》卷一○九', '心肺风热，冲目生胬肉。', '-', '-', '升麻', '[\'羚羊角\', \'分镑\', \'黄芩\', \'黑心\', \'柴胡\', \'分去\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (60301, '6039', '黄耆散', '黄耆1两（锉），甘草1两（炙微赤，锉），川升麻1两，黄芩1两，前胡1两（去芦头），栝楼根1两，知母1两，麦门冬1两（去心），赤芍药1两，生干地黄2两。', '每服4钱，以水1中盏，加竹叶2-7片，小麦100粒，煎至6分，去滓温服，日3-4次。', '《圣惠》卷五十三', '渴利后，皮肤生热毒疮疼痛，寒热，口干心烦。', '-', '-', '升麻', '[\'黄耆\', \'甘草\', \'两炙微\', \'锉川\', \'升麻\', \'黄芩\', \'两前\', \'胡两去\', \'芦头\', \'栝楼\', \'根两\', \'知母\', \'麦门冬\', \'两去\', \'芍药\', \'两生\', \'地黄\']');
INSERT INTO `prescript` VALUES (60302, '5993', '擦牙至宝散', '雄鼠骨1付（其鼠要8两以上者，越大越好，用草纸包7层，再用稻草包紧，黄泥封固，用谷糠火煨熟去肉，拣出全骨，酥油炙黄，研为细末），北细辛1钱5分（洗净土，晒），破故纸5钱（青盐水炒），香白芷3钱（青盐水炒），白石膏5钱（青盐水炒），全当归5钱（酒炒），怀生地3钱（酒炒），绿升麻2分（焙），没石子雌雄1对（酒煮，火烘），真沉香1钱5分，骨碎补5钱（去毛净，蜜水炒），旱莲草5钱（酒炒）。', '每日擦牙漱咽，久而不断。', '《冯氏锦囊·杂症》卷六', '牙齿动摇者，仍可坚固；不动者，永保不动；甚至少年有去牙一二，在三年以内者，竟可复生。主', '-', '-', '升麻', '[\'雄鼠\', \'骨付\', \'其鼠\', \'以上者\', \'越大越\', \'草纸\', \'包层\', \'再用\', \'稻草\', \'包紧\', \'黄泥\', \'封固\', \'谷糠\', \'拣出\', \'全骨\', \'酥油\', \'研为\', \'细末北\', \'细辛\', \'钱分\', \'洗净\', \'土晒\', \'纸钱\', \'盐水\', \'炒香\', \'白芷\', \'钱青\', \'盐水\', \'炒白\', \'石膏\', \'钱青\', \'盐水\', \'炒全\', \'当归\', \'钱酒\', \'炒怀\', \'生地\', \'钱酒\', \'升麻\', \'分焙\', \'石子\', \'雌雄\', \'火烘真\', \'沉香\', \'钱分\', \'骨碎补\', \'毛净\', \'蜜水\', \'旱莲草\', \'钱酒\']');
INSERT INTO `prescript` VALUES (60303, '5982', '黄连丸', '黄连（去须）1两1分，艾叶（炒）3分，升麻3分，防风（去叉）半两，朴消2两，大黄（锉，炒）3分。', '每服3-5丸，食后临卧以温水送下。', '《圣济总录》卷一八○', '小儿齆鼻。', '-', '-', '升麻', '[\'黄连\', \'两分\', \'艾叶\', \'炒分\', \'升麻\', \'防风\', \'半两\', \'朴消\', \'大黄\', \'炒分\']');
INSERT INTO `prescript` VALUES (60304, '5961', '揩齿散', '猪牙皂荚半两（烧过），夜合枝1尺（烧成灰），槐枝1尺（烧成灰），皂荚枝1尺（烧成灰），寒水石半两，石膏1两（2味煅过，细研），升麻半两，芎1分，甘松1分，藿香1分，丁香10个。', '每日依常法揩齿后用盐汤漱口。', '《医方类聚》卷二三四引《王岳产书》', '产后齿脚尚虚，不宜用牙刷刷齿者。', '-', '-', '升麻', '[\'猪牙皂\', \'半两\', \'过夜\', \'合枝尺\', \'烧成灰\', \'槐枝尺\', \'烧成灰\', \'皂荚\', \'枝尺\', \'烧成灰\', \'寒水石\', \'半两\', \'石膏\', \'两味\', \'过细\', \'升麻\', \'半两\', \'分甘松分\', \'藿香\', \'丁香\']');
