INSERT INTO `prescript` VALUES (60306, '5889', '黄耆散', '黄耆1两（锉），川升麻1两，川大黄1两（锉碎，微炒），黄芩1两，远志1两（去心），赤茯苓1两，赤芍药1两，生干地黄2两，当归半两，麦门冬1两半（去心），人参半两（去芦头），甘草半两（生，锉）。', '黄耆茯苓汤（《仙传外科集验方》）。', '《圣惠》卷六十一', '痈肿。热气大盛，寒热进退。', '-', '-', '升麻', '[\'黄耆\', \'锉川\', \'升麻\', \'两川\', \'大黄\', \'黄芩\', \'远志\', \'两去\', \'茯苓\', \'两赤\', \'芍药\', \'两生\', \'地黄\', \'当归\', \'半两\', \'麦门冬\', \'两半\', \'去心\', \'人参\', \'半两\', \'芦头\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60307, '5785', '黄耆丸', '黄耆1两（锉），栀子仁1两（炒），犀角1两（镑），木通1两（锉，炒），升麻1两，人参1两，玄参1两，木香1两，干蓝1两，黄芩1两（去黑心），芍药（锉）1两。', '每服20丸，食后煎枸杞根汤送下。加至30丸。', '《圣济总录》卷一一四', '耳聋。', '-', '-', '升麻', '[\'黄耆\', \'栀子\', \'犀角\', \'两镑\', \'木通\', \'升麻\', \'人参\', \'玄参\', \'木香\', \'两干\', \'黄芩\', \'两去\', \'黑心\', \'芍药\']');
INSERT INTO `prescript` VALUES (60308, '5702', '马兜铃汤', '马兜铃7个，桑根白皮（锉）3两，升麻1两，甘草（炙，锉）2两。', '每服5钱匕，水2盏，煎至1盏，去滓温服。', '《圣济总录》卷四十八', '肺热实卒嗽，气促急妨闷，喘息不安。', '-', '-', '升麻', '[\'马兜铃\', \'个桑根\', \'白皮\', \'升麻\', \'甘草\']');
INSERT INTO `prescript` VALUES (60309, '5678', '解毒济生汤', '川芎1钱，当归1钱，黄柏1钱，知母1钱，天花粉1钱，金银花1钱，麦门冬1钱，远志1钱，柴胡1钱，黄芩1钱，犀角1钱，茯神1钱，甘草5分，红花5分，升麻（手指加）5分，牛膝（足指加）5分。', '水2钟，煎8分，临服入童便1杯，随病上下服。', '《外科正宗》卷二', '脱疽初起，恶寒体倦，发热作渴，或肿或紫，或麻或痛，四肢倦怠，心志恍惚不宁者。', '-', '-', '升麻', '[\'川芎\', \'当归\', \'黄柏\', \'知母\', \'天花粉\', \'金银\', \'花钱\', \'麦门冬\', \'远志\', \'柴胡\', \'黄芩\', \'犀角\', \'茯神\', \'甘草\', \'红花\', \'升麻\', \'手指\', \'牛膝\', \'足指\']');
INSERT INTO `prescript` VALUES (60310, '5655', '黄芩六一丸', '条芩6两，升麻1两。', '口服。', '《济阳纲目》卷九十六', '积热脱肛。', '-', '-', '升麻', '[\'条芩\', \'升麻\']');
INSERT INTO `prescript` VALUES (60311, '5629', '葛根汤', '葛根8两，生姜3两，龙胆半两，大青半两，桂心2两，甘草（炙）2两，麻黄（去节）2两，萎蕤1两，芍药2两，黄芩2两，石膏（碎）1两，升麻1两。', '葛根龙胆汤（《千金》卷九）。', '《外台》卷一引《小品方》', '伤寒3-4日不愈，身体热毒；阳毒伤寒，头痛壮热未解，身体疼痛。', '忌海藻、菘菜、生葱。', '-', '升麻', '[\'葛根\', \'生姜\', \'龙胆\', \'半两\', \'大青\', \'半两\', \'桂心\', \'甘草\', \'麻黄\', \'去节\', \'萎蕤\', \'芍药\', \'黄芩\', \'石膏\', \'升麻\']');
INSERT INTO `prescript` VALUES (60312, '5626', '马蔺子散', '马蔺子半两（微炒），地骨皮半两，川升麻半两，黄芩半两，马牙消2两，犀角屑半两，甘草半两（生用），大青半两，苦竹叶2两（锉）。', '以水2大盏，煎至1盏，滤去滓，入蜜2合，同熬令稠，取1茶匙含咽津，不拘时候。', '《圣惠》卷十', '伤寒毒气攻咽喉，窄窒痛疼不可忍。', '-', '-', '升麻', '[\'马蔺\', \'半两\', \'地骨皮\', \'半两\', \'升麻\', \'半两\', \'黄芩\', \'半两\', \'马牙消\', \'犀角\', \'半两\', \'甘草\', \'半两\', \'生用\', \'大青\', \'半两\', \'苦竹叶\']');
INSERT INTO `prescript` VALUES (60313, '5611', '麻黄汤', '麻黄4两，黄芩3两，甘草（炙）3两，石膏3两（碎），升麻2两，栀子仁1两。', '以水1斗，煮取3升半，分3次服。', '《外台》卷三十八', '下气，解肌，折热。主乳不发动，热气上冲。', '-', '-', '升麻', '[\'麻黄\', \'黄芩\', \'甘草\', \'石膏\', \'两碎\', \'升麻\', \'栀子\']');
INSERT INTO `prescript` VALUES (60314, '5602', '解表消毒饮', '黄耆（上部酒拌炒，中部米泔拌炒，下部盐水炒）1钱半，葛根5分，升麻5分，赤芍药5分，玄参5分，牛蒡子（炒研）5分，麻黄（去根节）5分，甘草5分，连翘1钱。', '水1钟，加生姜3片，葱白1根，煎至7分，温服，不拘时候。病在手少阴分野，加细辛3分；足少阴，加独活7分；手太阴，加桔梗、白芷各5分；足太阴，加苍术7分；手厥阴，加柴胡7分；足厥阴，加柴胡、青皮各5分；手太阳，加藁本5分；足太阳，加羌活7分；手阳明，加白芷5分；足阳明，加升麻、葛根各7分；手足少阳，加柴胡7分。', '《准绳·幼科》卷三', '小儿疮疡，高肿焮痛，便利调和，脉浮而洪，有表证者。', '-', '-', '升麻', '[\'黄耆\', \'上部\', \'酒拌\', \'中部\', \'泔拌\', \'下部\', \'盐水\', \'葛根\', \'升麻\', \'分赤\', \'芍药\', \'玄参\', \'牛蒡子\', \'研分\', \'麻黄\', \'根节\', \'甘草\', \'连翘\']');
INSERT INTO `prescript` VALUES (60315, '5595', '干蓝汤', '干蓝1两，车前子1两，秦皮（去粗皮）1两，细辛（去苗叶）1两，决明子（炒）1两，山栀子仁1两，升麻1两，芍药1两，甘草（炙，锉）1两，蕤仁1两半。', '每服5钱匕，水1盏半，加苦竹叶10片，煎至1盏，去滓，食后、临卧温服。', '《圣济总录》卷一○九', '眼眦生赤脉息肉，涩痛不开，热势不歇，及目睛昏黄。', '-', '-', '升麻', '[\'干蓝\', \'车前子\', \'秦皮\', \'粗皮\', \'细辛\', \'苗叶\', \'决明子\', \'两山\', \'栀子\', \'升麻\', \'芍药\', \'甘草\', \'蕤仁\', \'两半\']');
INSERT INTO `prescript` VALUES (60316, '5579', '川升麻散', '川升麻1两，白附子（炮裂）1两。', '以生地黄汁调，贴在齿根。', '《圣惠》卷三十四', '齿风宣露。牙齿不生。', '-', '-', '升麻', '[\'升麻\', \'白附子\', \'炮裂\']');
INSERT INTO `prescript` VALUES (60317, '5523', '大黄散', '川大黄1两（锉碎，微炒），犀角屑3分，射干3分，川升麻3分，玄参2分，大青3分，络石3分，木通3分（锉），甘草3分（炙微赤，锉）。', '每服5钱，以水1大盏，煎至3分，去滓温服，不拘时候。', '《圣惠》卷三十六', '心脾热毒，生木舌，肿涩妨闷。', '-', '-', '升麻', '[\'大黄\', \'犀角\', \'射干\', \'分川\', \'升麻\', \'玄参\', \'分大青分\', \'络石\', \'木通\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (60318, '5507', '救苦丹', '麻黄（去根节，洗净晒干）4两（研极细末，温水浸，用细布取汁，余滓再捣再浸再取汁，必要洁净细腻为上），甘草（炙，去皮净，研极细末）4两（凉水浸，照前取汁），赤芍1两（研极细末，温水浸，照前取汁），升麻（微炒）1两（研极细末，凉水浸取汁），朱砂1两5钱（研极细，水飞），雄黄1两5钱（研极细，水飞），当归身2两（研极细，温酒浸取汁），人参（去芦尽，研极细末）1两（温酒浸取汁），柴胡（去芦，研极细末）1两（温水浸取汁），细辛5钱（研极细末，温水浸取汁），枳实（去心，研极细末）5钱（温水浸取汁）。', '每服1丸，雄黄5分（研极细），新汲水半盏，调雄黄连药送下。用厚被盖暖处，香烧3寸，汗出即愈。春、夏，加石膏5钱（水飞）；秋、冬，加桂皮5钱（研极细末，温水浸取汁）。', '《万氏家抄方》卷一引通真子方', '大人、小儿感冒伤寒。', '-', '-', '升麻', '[\'麻黄\', \'根节\', \'洗净\', \'晒干\', \'两研极\', \'细末温\', \'水浸\', \'细布\', \'取汁\', \'余滓\', \'取汁\', \'必要\', \'洁净\', \'细腻\', \'甘草\', \'去皮\', \'研极\', \'细末\', \'凉水\', \'浸照\', \'取汁\', \'赤芍\', \'两研极\', \'细末温\', \'水浸\', \'照前\', \'取汁\', \'升麻\', \'研极\', \'细末\', \'凉水\', \'浸取\', \'朱砂\', \'研极\', \'细水\', \'雄黄\', \'研极\', \'细水\', \'当归\', \'身两\', \'研极细\', \'温酒\', \'浸取\', \'人参\', \'芦尽\', \'研极\', \'细末\', \'温酒\', \'浸取\', \'柴胡\', \'去芦\', \'研极\', \'细末\', \'温水\', \'浸取\', \'细辛\', \'研极\', \'细末\', \'温水\', \'浸取\', \'枳实\', \'去心\', \'研极\', \'细末\', \'温水\', \'浸取\']');
