INSERT INTO `prescript` VALUES (60358, '4490', '麻黄汤', '麻黄（去根节）1分，杏仁（去皮尖双仁，炒，研）1分，桔梗（去芦头，炒）1分，秦艽（去苗土）1分，薄荷叶1分，牡丹（去心）1分，防风（去叉）1分，芍药1分，升麻1分，黄芩（去黑心）1分，紫菀（去苗土）1分，半夏（汤洗去滑）半分，羌活（去芦头）半两。', '每服2钱匕，以水1盏，加生姜3片，煎至7分，去滓，食后、临卧热服。', '《圣济总录》卷十七', '头面风。', '-', '-', '升麻', '[\'麻黄\', \'根节\', \'杏仁\', \'皮尖\', \'双仁\', \'研分\', \'桔梗\', \'芦头\', \'炒分\', \'秦艽\', \'苗土\', \'薄荷叶\', \'牡丹\', \'心分\', \'防风\', \'去叉分\', \'芍药\', \'升麻\', \'黄芩\', \'黑心\', \'紫菀\', \'苗土\', \'半夏\', \'汤洗\', \'半分\', \'羌活\', \'芦头\', \'半两\']');
