INSERT INTO `prescript` VALUES (60360, '4459', '漏芦汤', '漏芦（去芦头）1两，乌蛇（去皮骨，酒炙）1两，独活（去芦头）1两，黄耆（炙）1两，白蔹1两，白茯苓（去黑皮）1两，生姜（切，炒）1两，大黄（锉碎，醋炒）1两，升麻（生用）3分，麻黄（去根节，煎去沫，焙）3分，枳实（去瓤，麸炒）3分，芍药3分，防己3分，玄参3分，甘草（炙）3分，附子（炮裂，去皮脐）3分，栀子仁1两1分，石膏（碎）1两半。', '每服5钱匕，以水1盏半，煎取1盏，去滓温服；要利，空心、临卧各1服。', '《圣济总录》卷十八', '大风癞，身体成疮，眉鬓堕落，（疒咅）（疒畾）瘙痒，搔之黄水出者。', '-', '-', '升麻', '[\'漏芦\', \'芦头\', \'乌蛇\', \'去皮\', \'骨酒\', \'独活\', \'芦头\', \'黄耆\', \'白蔹\', \'白茯苓\', \'黑皮\', \'生姜\', \'大黄\', \'升麻\', \'生用\', \'麻黄\', \'根节\', \'焙分\', \'枳实\', \'炒分\', \'芍药\', \'防己\', \'玄参\', \'甘草\', \'炙分\', \'附子\', \'炮裂\', \'去皮\', \'脐分\', \'栀子\', \'两分\', \'石膏\', \'两半\']');
