INSERT INTO `prescript` VALUES (60362, '4439', '羚羊角散', '羚羊角屑3分，麦门冬3分（去心），甘草（炙微赤，锉）3分，茯神半两，白鲜皮半两，川升麻半两，人参半两（去芦头），黄耆（锉）半两。', '羚羊角汤（《圣济总录》卷一六八）。', '《圣惠》卷八十三', '小儿风热，心膈烦闷，身体壮热，嗜睡多渴。', '-', '-', '升麻', '[\'羚羊角\', \'麦门冬\', \'分去\', \'甘草\', \'炙微\', \'茯神\', \'半两\', \'白鲜皮\', \'半两\', \'升麻\', \'半两\', \'人参\', \'半两\', \'芦头\', \'黄耆\', \'半两\']');
INSERT INTO `prescript` VALUES (60363, '4430', '葛根散', '葛根1两（锉），川升麻1两，犀角肩1两，知母1两，黄芩1两，甘草1两（炙微赤，锉），郁金1两，川大黄1两（锉碎，微炒）。', '每服2钱，用鸡子1枚取清，以新汲水半盏，同调药，不拘时候服。', '《圣惠》卷十八', '热病，累经发汗，毒气不尽，攻于头面及身体，发疮如豆，头白根紫，为毒气盛。', '-', '-', '升麻', '[\'葛根\', \'锉川\', \'升麻\', \'犀角\', \'知母\', \'黄芩\', \'甘草\', \'两炙微\', \'郁金\', \'两川\', \'大黄\']');
INSERT INTO `prescript` VALUES (60364, '4428', '羚羊角散', '羚羊角屑半两，旋覆花半两，枳壳半两（麸炒微黄，去瓤），前胡半两（去芦头），川升麻半两，玄参半两，赤芍药半两，黄芩半两，地骨皮半两，茯神3分，麦门冬1两（去心，焙），甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气七日，心神恍惚，烦躁壮热，不得眠卧。', '-', '-', '升麻', '[\'羚羊角\', \'半两\', \'旋覆花\', \'半两\', \'枳壳\', \'半两\', \'微黄\', \'半两\', \'芦头川\', \'升麻\', \'半两\', \'玄参\', \'半两\', \'芍药\', \'半两\', \'黄芩\', \'半两\', \'地骨皮\', \'半两\', \'茯神\', \'麦门冬\', \'两去\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60365, '4368', '漏芦汤', '漏芦2两，白及2两，黄芩2两，麻黄2两，白薇2两，枳实2两，升麻2两，芍药2两，甘草2两，大黄2两。', '千金漏芦汤（《局方》卷八宝庆新增方）。方中白薇、枳实，《局方》作白蔹、枳壳。', '《千金》卷二十二', '痈疽发背，丹毒恶肿，时行热毒，发作赤色，瘰病初发，头目赤痛，暴生障翳，吹奶肿痛，一切无名恶疮。', '-', '-', '升麻', '[\'漏芦\', \'两白及\', \'黄芩\', \'麻黄\', \'两白薇\', \'枳实\', \'升麻\', \'芍药\', \'甘草\', \'大黄\']');
INSERT INTO `prescript` VALUES (60366, '4356', '露蜂房散', '露蜂房半两（微炙），甘草半两（炙微赤，锉），射干半两，川升麻半两，川朴消半两，玄参半两。', '每服3钱，以水1中盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十八', '热病，喉中热毒，闭塞肿痛。', '-', '-', '升麻', '[\'露蜂房\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'射干\', \'半两\', \'升麻\', \'半两\', \'川朴消\', \'半两\', \'玄参\', \'半两\']');
INSERT INTO `prescript` VALUES (60367, '4311', '寒水石散', '寒水石2两，白石英2两，石膏2两，细辛半两，川升麻1两，朱砂半两（细研），麝香1分（细研），丁香1分，沉香半两，钟乳1两（细研）。', '每早晨、夜间常用揩齿。', '《圣惠》卷三十四', '令齿光白。主', '-', '-', '升麻', '[\'寒水石\', \'白石英\', \'石膏\', \'细辛\', \'半两\', \'升麻\', \'朱砂\', \'半两\', \'细研\', \'麝香\', \'分细研\', \'丁香\', \'沉香\', \'半两\', \'钟乳\', \'两细研\']');
INSERT INTO `prescript` VALUES (60368, '4302', '黄耆煮散', '黄耆1分（锉），桑根白皮1分（锉），杏仁1分（去皮尖双仁，炒），紫菀1分（去苗土），黄芩1分（去黑心），麻黄1分（去根节），麦门冬1分（去心，焙），升麻1分，贝母1分（去心），羌活1分（去芦头），蛤蚧（酥炙）1分，胡黄连1钱。', '每服3钱匕，水1盏，生姜1枣大（拍碎），煎至9分，去姜，食后、临卧温服。', '《圣济总录》卷四十八', '肺气盛实，其气上蒸，发嗽多痰，心胸烦躁，往往咯血。', '-', '-', '升麻', '[\'黄耆\', \'桑根\', \'白皮\', \'杏仁\', \'皮尖\', \'双仁\', \'紫菀\', \'苗土\', \'黄芩\', \'黑心\', \'麻黄\', \'分去\', \'根节\', \'麦门冬\', \'分去\', \'升麻\', \'贝母\', \'分去\', \'羌活\', \'分去\', \'芦头\', \'蛤蚧\', \'酥炙分\', \'胡黄连\']');
INSERT INTO `prescript` VALUES (60369, '4252', '鳖甲酒', '常山3两，鳖甲1两（炙），升麻1两，附子1两，乌贼骨1两。', '《圣惠》本方用法：上细锉，以绢袋盛。用酒六升，浸3-5日，每服一中盏，暖令温，空心服之,或吐即愈，未吐再服。', '方出《肘后方》卷三，名见《圣惠》卷五十二', '老疟久不断者。劳疟痰滞，发歇不定。', '-', '-', '升麻', '[\'常山\', \'鳖甲\', \'升麻\', \'附子\', \'乌贼骨\']');
INSERT INTO `prescript` VALUES (60370, '4232', '揩齿龙脑散', '龙脑1分（细研），寒水石1两，盐花半两，石膏1两（细研），藁本半两，白芷半两，芎半两，川升麻1两，细辛半两，龙花蕊半两。', '每日早晨及临卧揩齿。', '《圣惠》卷三十四', '益牙齿。主', '-', '-', '升麻', '[\'龙脑\', \'分细研\', \'寒水石\', \'盐花\', \'半两\', \'石膏\', \'两细研\', \'藁本\', \'半两\', \'白芷\', \'半两\', \'半两\', \'升麻\', \'细辛\', \'半两\', \'花蕊\', \'半两\']');
INSERT INTO `prescript` VALUES (60371, '4212', '羚羊角散', '羚羊角屑1两，木通1两（锉），射干1两，川升麻1两，地骨皮1两，芦根3两（锉）。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十', '伤寒咽喉疼痛，心神烦闷。', '-', '-', '升麻', '[\'羚羊角\', \'木通\', \'射干\', \'两川\', \'升麻\', \'地骨皮\', \'芦根\']');
INSERT INTO `prescript` VALUES (60372, '4195', '黄耆散', '黄耆1两（锉），川升麻1两，犀角屑1两，赤茯苓3分，麦门冬3分（去心），人参3分（去芦头），赤芍药3分，生干地黄3分，石膏2两，蓝叶半两。', '每服4钱，以水1中盏，入竹叶2-7片，煎至6分，去滓温服，不拘时候。', '《圣惠》卷六十二', '发背。热毒气盛，作寒热往来，疼痛不止。', '-', '-', '升麻', '[\'黄耆\', \'锉川\', \'升麻\', \'犀角\', \'茯苓\', \'麦门冬\', \'分去\', \'人参\', \'分去\', \'芦头\', \'芍药\', \'分生\', \'地黄\', \'石膏\', \'两蓝叶\', \'半两\']');
INSERT INTO `prescript` VALUES (60373, '4134', '麻黄散', '防风3分，藁本3分，羊胫骨灰6分，当归身6分，熟地黄6分，草豆蔻仁1钱，升麻1钱，黄连1钱，羌活1钱5分，麻黄（不去节）2钱，草龙胆（酒洗）2钱，生地黄2钱，细辛少许。', '本方方名，《医学纲目》引作“黄连散”。', '《兰室秘藏》卷中', '冬寒时分，寒湿脑痛，项筋急，牙齿动摇疼痛。', '-', '-', '升麻', '[\'防风\', \'藁本\', \'分羊\', \'胫骨\', \'灰分\', \'当归\', \'身分\', \'熟地黄\', \'草豆蔻\', \'仁钱\', \'升麻\', \'黄连\', \'羌活\', \'钱分\', \'麻黄\', \'不去\', \'节钱\', \'龙胆\', \'洗钱\', \'生地黄\', \'细辛\', \'少许\']');
INSERT INTO `prescript` VALUES (60374, '4116', '槐角丸', '槐角3两（炒），防风1两半，黄芩1两半（炒），当归3两，枳壳8钱（炒），升麻5钱，地榆3两（炒），生草5钱。', '每服3钱，米饮送下。', '《医略六书》卷二十五', '肠风痔血，脉浮数者。', '-', '-', '升麻', '[\'槐角\', \'防风\', \'两半\', \'黄芩\', \'两半\', \'当归\', \'枳壳\', \'升麻\', \'地榆\', \'生草\']');
INSERT INTO `prescript` VALUES (60375, '4034', '黄连泻火汤', '黄连8分，黄芩1钱（俱酒炒），生地1钱，升麻5分，柴胡7分。', '水煎服。', '《医学六要》卷八', '目暴发赤肿疼痛。', '-', '-', '升麻', '[\'黄连\', \'黄芩\', \'钱俱酒\', \'生地\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (60376, '4030', '脚气渫洗法', '威灵仙1两，防风（去芦）1两，荆芥穗1两，当归（去芦）1两，地骨皮1两，蒴藋叶1两，升麻（去腐）1两，白芍药（去皮）1两。', '本方方名，《普济方》引作“导气除湿汤”。', '《医学发明》卷八', '开导，泄越其邪。主脚气，内受湿邪，不能外达。', '-', '-', '升麻', '[\'威灵仙\', \'防风\', \'去芦\', \'荆芥\', \'当归\', \'去芦\', \'地骨皮\', \'升麻\', \'去腐\', \'两白\', \'芍药\', \'去皮\']');
INSERT INTO `prescript` VALUES (60377, '4026', '薄荷粥', '薄荷1握，阿胶1两（杵碎，炒令黄燥），川升麻1两，豉心1合。', '以水2碗，煮取1碗，去滓，以粳米作稀粥服，厚覆取汗。', '《圣惠》卷十六', '时气劳复，四肢烦疼。', '-', '-', '升麻', '[\'薄荷\', \'阿胶\', \'两杵碎\', \'炒令\', \'黄燥川\', \'升麻\', \'两豉\', \'心合\']');
