INSERT INTO `prescript` VALUES (60401, '3387', '槟榔散', '槟榔半两，荆芥穗半两，菵草半两，升麻半两，羌活半两，藁本半两，木香半两，细辛半两。', '每用半钱，敷在肿痛处，吐津，误咽无妨，不拘时候。', '《御药院方》卷九', '祛风热，清肿痛。主牙齿疼痛，连龈下颊俱肿。', '-', '-', '升麻', '[\'槟榔\', \'半两\', \'荆芥\', \'半两\', \'半两\', \'升麻\', \'半两\', \'羌活\', \'半两\', \'藁本\', \'半两\', \'木香\', \'半两\', \'细辛\', \'半两\']');
