INSERT INTO `prescript` VALUES (60403, '3360', '鳖甲汤', '鳖甲1两，当归1分，炙草1分，升麻1分，椒50粒。', '以水1升，煮8合，分为3服，每服相去如人行6-7里再服。觉身上润，衣盖取汗，微汗勿深。每日1剂，便能食。', '方出《幼幼新书》卷二十一引《婴孺方》，名见《医部全录》卷四四二', '3-7岁儿不能食，或呕，或头热，或下痢，或渴，或手脚热，有时冷。', '-', '-', '升麻', '[\'鳖甲\', \'当归\', \'分炙草分\', \'升麻\', \'分椒粒\']');
INSERT INTO `prescript` VALUES (60404, '3340', '覆盆子丸', '覆盆子半斤，五粒松半斤，枸杞子6两，秦皮4两，川升麻3两，巨胜5两，楮实5两（水淘去浮者，晒干，微炒）。', '每服30丸，食后温酒送下；如不饮酒，以浆水下。', '《圣惠》卷九十八', '补暖下元，变白发。主诸风虚。', '忌白蒿、青蒿、荏子、萝卜、蒜。', '-', '升麻', '[\'覆盆子\', \'半斤\', \'五粒\', \'半斤\', \'枸杞子\', \'秦皮\', \'两川\', \'升麻\', \'两巨胜\', \'实两水\', \'淘去\', \'浮者\', \'晒干\']');
INSERT INTO `prescript` VALUES (60405, '3294', '葛根汤', '葛根2钱，石膏2钱，升麻3分，前胡8分，青黛8分。', '如有痰，加竹沥、姜汁。', '《女科秘旨》卷四', '孕妇热病，骨节疼痛。', '-', '-', '升麻', '[\'葛根\', \'石膏\', \'升麻\', \'分前\', \'胡分\', \'青黛分\']');
INSERT INTO `prescript` VALUES (60406, '3277', '丹砂丸', '朱砂（细研，水飞过）半两，薯蓣半两，犀角屑半两，虎头骨（涂酥，炙微令黄）半两，肉苁蓉（酒浸1宿，刮去皱皮，炙令干）半两，安息香半两，川升麻半两，牡蛎（烧为粉）半两，槟榔半两，人参半峡谷，白茯苓半两，牛黄1钱（细研），麝香1钱（细研），甘草1分（炙微赤，锉），麦门冬3分（去心，焙），龟甲3分（涂醋，炙微黄，去裙襕），鼓心3分。', '每服30丸，以粥饮送下，不拘时候。', '《圣惠》卷二十七', '虚劳骨热，四肢烦闷，及心躁虚汗。', '忌苋菜。', '-', '升麻', '[\'朱砂\', \'细研水\', \'飞过\', \'半两\', \'薯蓣\', \'半两\', \'犀角\', \'半两\', \'头骨\', \'微令\', \'半两\', \'肉苁蓉\', \'浸宿\', \'刮去\', \'皱皮炙令\', \'半两\', \'安息香\', \'半两\', \'升麻\', \'半两\', \'牡蛎\', \'烧为\', \'半两\', \'槟榔\', \'半两\', \'参半\', \'峡谷\', \'白茯苓\', \'半两\', \'牛黄\', \'钱细研\', \'麝香\', \'钱细研\', \'甘草\', \'分炙微\', \'麦门冬\', \'分去\', \'龟甲\', \'分涂\', \'微黄\', \'鼓心分\']');
INSERT INTO `prescript` VALUES (60407, '3263', '漏芦汤', '漏芦1两，连翘1两，黄芩1两，白蔹1两，枳壳1两，升麻1两，麻黄（去根节）1两，朴消1两（另研），大黄半两，紫花地丁半两，金银花半两。', '每服3钱，水1盏，加生姜3片，薄荷3叶，煎至7分，空心温服。利下恶物，止药。', '《医学正传》卷六引《千金》', '疔肿。', '-', '-', '升麻', '[\'漏芦\', \'连翘\', \'黄芩\', \'白蔹\', \'枳壳\', \'升麻\', \'麻黄\', \'根节\', \'两朴\', \'消两\', \'另研\', \'大黄\', \'半两\', \'紫花地丁\', \'半两\', \'金银花\', \'半两\']');
INSERT INTO `prescript` VALUES (60408, '3224', '黑髭揩齿散', '莲子草1两，鸡肠草1两，熟干地黄1两，马齿苋1两，酸石榴皮1两，海盐1两，青胡桃皮1两，没石子1两，丁香半两，川升麻半两（末），麝香1分（细研）。', '每日早晨及夜卧时常用揩齿，1月后甚有功效。', '《普济方》卷四十九', '乌髭发。主', '-', '-', '升麻', '[\'莲子\', \'两鸡肠草\', \'两熟\', \'地黄\', \'马齿苋\', \'两酸\', \'石榴皮\', \'海盐\', \'两青\', \'胡桃\', \'石子\', \'丁香\', \'半两\', \'升麻\', \'半两\', \'麝香\', \'分细研\']');
INSERT INTO `prescript` VALUES (60409, '3178', '斑蝥白芷丸', '斑蝥2分，白芷2分，绿青2分，大黄2分，人参3两，当归3两，桂心3两，麦门冬1两，白术1两，升麻1分，钟乳1分，甘草1分，防风1分，地胆1分，续断1分，麝香1分，礜石1分。', '每服10丸，酒送下，1日3次。', '方出《千金》卷二十三，名见《普济方》卷二九三', '因惊卧失枕，致患转脉漏。始发于颈，濯濯脉转，苦惊惕，身振，寒热。', '勿食菜，慎房室百日。', '-', '升麻', '[\'斑蝥\', \'白芷\', \'分绿青分\', \'大黄\', \'人参\', \'当归\', \'两桂心\', \'麦门冬\', \'白术\', \'升麻\', \'分钟\', \'乳分\', \'甘草\', \'防风\', \'分地\', \'胆分\', \'续断\', \'麝香\', \'石分\']');
INSERT INTO `prescript` VALUES (60410, '3147', '款冬白薇茹(艹虑)丸', '款冬花（去向里裹花蕊壳及向里实并枝叶，用甘草水浸1宿，再取款冬叶相伴蒸1夜，晒干）1两5钱，白薇（糯米泔浸1宿，取出，槐砧上锉细蒸之，从申至巳晒干）1两1钱，百合（用怀生地黄汁拌润透，蒸半炷香，取出晒干）6钱，知母（槐砧上锉碎，干木臼杵捣）6钱，地骨皮（东流水洗去土，捶去心，用甘草汤浸1宿，焙干）1两，桃仁（汤润，去皮，用白术、乌豆同东流水煮至中心黄金色为度，取出捣）81枚，玄参（入甑内用蒲草重重相隔，蒸两伏时，晒干；再拌菟丝子末蒸3炷香，去菟丝子，晒干，入木臼内杵捣）6钱，沙参（真者，多出辽地，形似人参', '每服30丸，空心煎细茶送下，每日3次。', '《痎疟论疏》', '温疟。', '-', '-', '升麻', '[\'款冬花\', \'去向\', \'花蕊\', \'枝叶\', \'甘草\', \'水浸\', \'宿再\', \'取款\', \'冬叶\', \'相伴\', \'蒸夜\', \'晒干\', \'白薇\', \'糯米\', \'泔浸\', \'取出\', \'槐砧\', \'细蒸\', \'申至\', \'晒干\', \'百合\', \'用怀\', \'生地黄\', \'透蒸\', \'取出\', \'晒干\', \'知母\', \'槐砧\', \'干木\', \'臼杵\', \'捣钱\', \'地骨皮\', \'流水\', \'洗去\', \'心用\', \'甘草\', \'浸宿\', \'焙干\', \'桃仁\', \'汤润\', \'去皮\', \'白术\', \'乌豆\', \'同东\', \'流水\', \'中心\', \'黄金\', \'色为度\', \'取出\', \'捣枚\', \'玄参\', \'内用\', \'蒲草\', \'重重\', \'相隔\', \'两伏时\', \'晒干\', \'菟丝子\', \'末蒸\', \'香去\', \'菟丝子\', \'晒干\', \'入木臼\', \'沙参\', \'真者\', \'多出\', \'辽地\', \'形似\', \'人参\']');
INSERT INTO `prescript` VALUES (60411, '3131', '丹砂育', '丹砂末2两，犀角2两，夜干2两，大黄2两，芎2两，麝香末2两，黄芩2两，生地黄10两（切），升麻3两，前胡3两，沉香3两，青木香1两。', '稍稍服之。熛疽始发未曾治者，温酒服如枣核大，1日3次。', '《鬼遗》卷五', '熛疽。', '-', '-', '升麻', '[\'丹砂\', \'犀角\', \'两夜\', \'大黄\', \'两芎\', \'麝香\', \'黄芩\', \'生地黄\', \'两切\', \'升麻\', \'两前\', \'胡两\', \'沉香\', \'青木香\']');
INSERT INTO `prescript` VALUES (60412, '3115', '葛根散', '葛根2两（锉），龙胆半两（去芦头），大青半两，桂心半两，甘草半两（炙微赤，锉），麻黄1两（去根节），葳蕤1两，赤芍药1两，黄芩1两，石膏2两，川升麻1两。', '每服4钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病4日，发汗不愈，身体壮热，心膈烦闷，不得睡卧。', '-', '-', '升麻', '[\'葛根\', \'龙胆\', \'半两\', \'芦头\', \'大青\', \'半两\', \'桂心\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'麻黄\', \'两去\', \'根节\', \'葳蕤\', \'两赤\', \'芍药\', \'黄芩\', \'石膏\', \'两川\', \'升麻\']');
INSERT INTO `prescript` VALUES (60413, '3096', '黄柏升麻汤', '黄柏半两，升麻半两，甘草（生）半两。', '上（口父）咀，水1升半，煮半升，入地黄汁1合，煎半升。分2服，细呷之。', '《伤寒总病论》卷三', '天行口疮。', '-', '-', '升麻', '[\'黄柏\', \'半两\', \'升麻\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60414, '3051', '黄连散', '黄连1两（去须），麦门冬1两（去心），川升麻1两，大青半两，黄柏1两（锉），射干1两，玄在半两，黄芩半两，甘草半两（生，锉）。', '每服4钱，以水1中盏，入竹叶2-7片，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十八', '乳石发动，口舌生疮，咽喉不利。', '-', '-', '升麻', '[\'黄连\', \'两去\', \'麦门冬\', \'两去\', \'心川\', \'升麻\', \'两大青\', \'半两\', \'黄柏\', \'射干\', \'两玄\', \'半两\', \'黄芩\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60415, '3038', '黄芩汤', '黄芩（去黑心）1两半，升麻1两半，黄连（去须）1两，芎1两，大黄1两，甘草（炙，锉）半两，当归（切，焙）半两，羚羊角（镑）半两。', '每用1两，以水5盏，煎至3盏，去滓，下芒消半两搅匀，以故帛3两，重浸药汁，温搨患处数10遍，早、晚用之。以愈为度。', '《圣济总录》卷一三八', '丹毒痈疽始发，焮热浸淫长大。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'两半\', \'升麻\', \'两半\', \'黄连\', \'两芎\', \'大黄\', \'甘草\', \'半两\', \'当归\', \'切焙\', \'半两\', \'羚羊角\', \'半两\']');
INSERT INTO `prescript` VALUES (60416, '2975', '漏芦汤', '漏芦（去芦头）1两，升麻1两，大黄（锉，炒）1两，黄芩（去黑心）1两，玄参3分。', '每服5钱匕，水1盏半。加竹叶2-7片，同煎至1盏，下芒消末1钱匕，再煎沸，去滓，空心温服。如已得利，即去芒消。', '《圣济总录》卷一三七', '代指。筋骨脏腑中热，焮赤肿痛。', '-', '-', '升麻', '[\'漏芦\', \'芦头\', \'升麻\', \'大黄\', \'黄芩\', \'黑心\', \'玄参\']');
INSERT INTO `prescript` VALUES (60417, '2924', '黄芩汤', '黄芩（去黑心）3分，升麻3分，射干3分，木通（锉）3分，甘草（炙，锉）半两，犀角（镑）半两。', '每服5钱匕，以水2盏，煎至1盏，去滓，下芒消1钱匕，细细温呷。', '《圣济总录》卷一二四', '喉痹，胸满，噎塞不通。', '-', '-', '升麻', '[\'黄芩\', \'黑心\', \'升麻\', \'射干\', \'木通\', \'锉分\', \'甘草\', \'半两\', \'犀角\', \'半两\']');
INSERT INTO `prescript` VALUES (60418, '2890', '麻黄升麻汤', '麻黄（去节）2两半，升麻1两1分，黄芩1两，芍药1两，甘草（生）1两，石膏（煅）1两，茯苓1两。', '每服4大钱，以水1盏半，加生姜3片，煎至7分，去滓热服。微汗解。', '《三因》卷九', '伤寒表未解，热郁作衄；风邪内缩，久泄不止。', '-', '-', '升麻', '[\'麻黄\', \'去节\', \'两半\', \'升麻\', \'两分\', \'黄芩\', \'芍药\', \'甘草\', \'生两\', \'石膏\', \'茯苓\']');
INSERT INTO `prescript` VALUES (60419, '2879', '葛根汤', '葛根1钱，升麻1钱，甘草5分，赤芍1钱5分。', '水煎服。风胜，加荆芥、防风、薄荷；火胜，加连翘、丹皮、生地、蒡子。', '《医学心悟》卷六', '牙痛。', '-', '-', '升麻', '[\'葛根\', \'升麻\', \'甘草\', \'赤芍\', \'钱分\']');
INSERT INTO `prescript` VALUES (60420, '2866', '葛根散', '葛根1分（锉），麦门冬3分（去心），黄芩半两，赤芍药半两，人参半两（去芦头），犀角屑半两，甘草半两（炙微赤，锉），石膏1两（细研），川升麻半两。', '每服1钱，以水1小盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷八十四', '小儿时气头痛，体热烦渴。', '-', '-', '升麻', '[\'葛根\', \'麦门冬\', \'分去\', \'黄芩\', \'半两\', \'芍药\', \'半两\', \'人参\', \'半两\', \'芦头\', \'犀角\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'石膏\', \'两细\', \'研川\', \'升麻\', \'半两\']');
INSERT INTO `prescript` VALUES (60421, '2824', '解毒汤', '连翘1钱，牛蒡1钱，枳壳6分，木通6分，防风5分，桔梗5分，紫草7分，川芎4分，升麻4分，虫退4分，黄芩4分，黄连4分，前胡1钱，麦冬8分，甘草3分。', '-', '《种痘新书》卷四', '解毒。主痘疮外感风寒，毒气壅盛，憎寒壮热，咳嗽流涕，服加减升麻汤、扶元宣解汤后，依然大热熏蒸，眼红唇紫，舌有黄苔，口中气臭，狂言谵语，二便不通，恶风恶寒，嘎齿咬牙，腹中隐隐作痛者。', '-', '-', '升麻', '[\'连翘\', \'牛蒡\', \'枳壳\', \'木通\', \'防风\', \'桔梗\', \'紫草\', \'川芎\', \'升麻\', \'分虫\', \'退分\', \'黄芩\', \'黄连\', \'分前\', \'胡钱\', \'麦冬\', \'甘草\']');
INSERT INTO `prescript` VALUES (60422, '2793', '薏苡仁酒', '薏苡仁半两，羚羊角屑3两，防风3两（去芦头），川升麻2两，秦艽2两（去苗），黄芩2两，地骨皮1两，枳壳1两（麸炒微黄，去瓤），羌活2两，牛膝5两（去苗），五加皮3两，独活2两，牛蒡子2两（微炒），桂心2两，大麻仁5合，生干地黄5两。', '用生绢袋盛，以酒3斗，浸6-7日后，食前随性暖服之。', '《圣惠》卷四十五', '脚气。风毒发歇疼痛，四肢拘急，背项强直，言语謇涩。', '-', '-', '升麻', '[\'薏苡仁\', \'半两\', \'羚羊角\', \'防风\', \'两去\', \'芦头川\', \'升麻\', \'秦艽\', \'两去\', \'黄芩\', \'地骨皮\', \'枳壳\', \'两麸\', \'微黄\', \'羌活\', \'牛膝\', \'两去\', \'五加皮\', \'独活\', \'牛蒡子\', \'两微\', \'炒桂心\', \'两大\', \'麻仁\', \'合生\', \'地黄\']');
INSERT INTO `prescript` VALUES (60423, '2784', '槐枝汤', '槐枝5握（锉），升麻1两，莽草1两，胡桐泪1两。', '每服用水3盏，煎2-3沸，通口漱渫。', '《圣济总录》卷一一九', '牙齿疼痛。', '-', '-', '升麻', '[\'槐枝握\', \'升麻\', \'莽草\', \'两胡桐\']');
INSERT INTO `prescript` VALUES (60424, '2777', '揩齿散', '细辛半两，白蒺藜（微炒，去刺）半两，露蜂房（微炙）半两，川升麻半两，白矾（一半炒令汗尽，一半生用，研令细）半两，黄柏（锉）半两，槐枝3-7茎，柳枝3-7茎（各用粗者，长2寸，烧勿令过火）。', '用时先以热盐水漱口3-5度后，取药揩齿。觉微痛即止，有津吐之。', '《圣惠》卷三十四', '牙齿风疳，血出疼痛，牙齿虚浮。', '-', '-', '升麻', '[\'细辛\', \'半两\', \'蒺藜\', \'半两\', \'露蜂房\', \'半两\', \'升麻\', \'半两\', \'白矾\', \'一半\', \'炒令\', \'一半\', \'生用\', \'研令细\', \'半两\', \'黄柏\', \'半两\', \'槐枝\', \'柳枝\', \'各用\', \'粗者\', \'长寸\', \'烧勿令\', \'过火\']');
INSERT INTO `prescript` VALUES (60425, '2761', '漏芦汤', '漏芦2两，白蔹2两，黄芩2两，白薇2两，枳实（炙）2两，升麻2两，甘草（炙）2两，芍药2两，麻黄（去节）2两，大黄3两。', '漏芦散（《圣惠》卷六十四）、漏芦煮散（《圣济总录》卷一三○）。《圣惠》本方用法：上为散。每服二钱，以水一中盏，煎至五分，去滓温服，不拘时候。', '《肘后方》卷五', '痈疽、丹疹、毒肿、恶肉。', '-', '-', '升麻', '[\'漏芦\', \'白蔹\', \'黄芩\', \'两白薇\', \'枳实\', \'升麻\', \'甘草\', \'芍药\', \'麻黄\', \'去节\', \'大黄\']');
