INSERT INTO `prescript` VALUES (60427, '2744', '鹳骨丸', '鹳骨（炙）4分，狸骨（炙）4分，射干4分，玄参4分，升麻（炙）4分，青木香4分，沉香4分，犀角屑4分，丁香4分，羚羊角屑4分，丹参4分，甘草（炙）4分，人参3两，沙参3两，獭肝6分，连翘6分，光明砂2分（研）。', '《圣惠》有麝香半两。', '方出《外台》卷二十三引《广济方》，名见《圣惠》卷六十六', '瘰疬。', '忌生冷、油腻、血食酢、热肉、海藻、菘菜、粘食、陈臭生血物。', '-', '升麻', '[\'分狸\', \'骨炙分\', \'射干\', \'玄参\', \'升麻\', \'炙分\', \'青木香\', \'沉香\', \'犀角\', \'丁香\', \'羚羊角\', \'丹参\', \'甘草\', \'炙分\', \'人参\', \'沙参\', \'两獭\', \'肝分\', \'连翘\', \'光明\', \'砂分研\']');
INSERT INTO `prescript` VALUES (60428, '2740', '鳖甲散', '鳖甲1两（涂醋，炙令黄，去裙襕），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），柴胡1两（去苗），麦门冬1两半（去心，焙），赤茯苓1两，川升麻半两，木通3分，前胡3分（去芦头），贝母半两（煨，炙微黄），大腹3分（锉），甘草半两（炙微赤，锉），子芩3分。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '骨蒸劳。烦热，胸背疼痛，咳嗽气促，小便赤黄，不思饮食。', '-', '-', '升麻', '[\'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'柴胡\', \'两去\', \'麦门冬\', \'两半\', \'茯苓\', \'两川\', \'升麻\', \'半两\', \'木通\', \'分前\', \'胡分\', \'芦头\', \'贝母\', \'半两\', \'微黄\', \'大腹分\', \'甘草\', \'半两\', \'炙微\', \'锉子\']');
INSERT INTO `prescript` VALUES (60429, '2693', '麻黄汤', '麻黄（去节）4两，杏仁4两（去尖皮两仁，碎），栀子仁3两，黄芩3两，防风3两，紫菀3两，升麻3两，桂心3两，茯神3两，人参3两，大枣20枚（擘），石膏6两（碎，绵裹），桑根白皮1升。', '以水1斗，先者麻黄3沸，去沫，下诸药，煮取3升，去滓，分3次服。', '《外台》卷十六引《删繁方》', '消虚热极，止汗。主心风，伤风损脉，脉极热，多汗，无滋润。', '忌生葱、酢物。', '-', '升麻', '[\'麻黄\', \'去节\', \'杏仁\', \'两去\', \'尖皮\', \'两仁碎\', \'栀子\', \'黄芩\', \'防风\', \'紫菀\', \'升麻\', \'两桂心\', \'茯神\', \'人参\', \'大枣\', \'石膏\', \'两碎\', \'绵裹\', \'桑根\', \'白皮\']');
INSERT INTO `prescript` VALUES (60430, '2671', '葶苈散', '甜葶苈（炒）1两，桔梗（去芦）1两，瓜蒌子1两，川升麻1两，薏苡仁1两，桑白皮1两，葛根1两，甘草（炙）半两。', '每服4钱，水1盏半，加生姜5片，煎至8分，去滓，食后温服。', '《济生》卷二', '过食煎煿，或饮酒过度，致肺壅喘不得卧；及肺痈，咽燥不渴，浊唾腥臭。', '-', '-', '升麻', '[\'甜葶\', \'桔梗\', \'去芦\', \'瓜蒌子\', \'两川\', \'升麻\', \'薏苡仁\', \'桑白皮\', \'葛根\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60431, '2614', '葛根猪苓汤', '葛根（用雪水或秋露润透，阴干，锉碎）1两，猪苓（削去黑皮，切作薄片，在流水浸1宿，取出锉片，用升麻叶拌蒸1日，去叶晒干；如无叶，升麻亦可用）5钱，泽泻（锉碎，酒浸1宿，取出晒干）5钱，茯苓（捣细，水飞，去膜，澄清，晒干）5钱，滑石（取洁白者，以竹刀刮净，研如粉，每两用牡丹皮2两同煮3炷香，去牡丹，以东流水淘过，晒干）7钱，石膏（煅赤，研细，甘草水飞过，澄，晒，再研）1两，阿胶5钱，地骨皮（东流水洗净，刷去土，捶去心，甘草水浸1宿，焙干）、栀子（去壳取仁，用甘草水浸1宿，取出晒干，捣筛为末）5钱。', '方中地骨皮用量原缺。', '《痎疟论疏》', '瘅疟。', '-', '-', '升麻', '[\'葛根\', \'雪水\', \'秋露\', \'润透\', \'阴干\', \'猪苓\', \'削去\', \'黑皮\', \'切作\', \'薄片\', \'水浸\', \'取出\', \'锉片\', \'升麻\', \'叶拌\', \'蒸日\', \'晒干\', \'如无叶\', \'升麻\', \'亦可\', \'用钱\', \'泽泻\', \'浸宿\', \'取出\', \'晒干\', \'茯苓\', \'细水\', \'飞去\', \'澄清\', \'晒干\', \'滑石\', \'洁白\', \'竹刀\', \'刮净\', \'研如粉\', \'两用\', \'牡丹皮\', \'两同\', \'香去\', \'牡丹\', \'以东\', \'流水\', \'淘过\', \'晒干\', \'石膏\', \'研细\', \'甘草\', \'飞过\', \'研两\', \'阿胶\', \'地骨皮\', \'流水\', \'洗净\', \'甘草\', \'水浸\', \'焙干\', \'栀子\', \'取仁用\', \'甘草\', \'水浸\', \'取出\', \'晒干\', \'筛为\']');
