INSERT INTO `prescript` VALUES (60434, '2437', '鳖甲散', '升麻半两，前胡（去芦）半两，乌梅（去核）半两，枳实（麸炒，去白）半两，犀角（镑）半两，黄芩半两，生地黄（切）2合，甘草1分（炙），鳖甲（去裙，米醋炙赤黄，杵碎用）半两。', '每服5钱匕，水1盏半，煎至8分，去滓温服。', '《活人书》卷十七', '伤寒8-9日不愈，名曰坏伤寒，诸药不能治者。', '-', '-', '升麻', '[\'升麻\', \'半两\', \'胡去\', \'半两\', \'乌梅\', \'去核\', \'半两\', \'枳实\', \'半两\', \'犀角\', \'半两\', \'黄芩\', \'半两\', \'生地黄\', \'切合\', \'甘草\', \'鳖甲\', \'米醋\', \'黄杵碎\', \'半两\']');
