INSERT INTO `prescript` VALUES (60436, '2414', '丹砂丸', '朱砂（细研，水飞过）1两，薯蓣1两，犀角屑1两，黄耆1两，肉苁蓉（酒浸1宿，刮去粗皮，令炙干）1两，川升麻1两，牡蛎（烧为粉）1两，槟榔1两，牛黄（细研）1钱，麝香1钱，麦门冬（去心，焙）3分，鳖甲（涂醋炙微黄，去裙）3分，豉心3分。', '每服30丸，以粥饮送下，不拘时候。', '《普济方》卷二三○', '虚劳骨热，四肢烦闷，及心躁虚汗。', '忌苋菜。', '-', '升麻', '[\'朱砂\', \'细研水\', \'飞过\', \'薯蓣\', \'犀角\', \'黄耆\', \'肉苁蓉\', \'浸宿\', \'刮去\', \'粗皮令\', \'两川\', \'升麻\', \'牡蛎\', \'烧为\', \'槟榔\', \'牛黄\', \'细研\', \'麝香\', \'麦门冬\', \'心焙分\', \'鳖甲\', \'涂醋\', \'微黄\', \'分豉\', \'心分\']');
INSERT INTO `prescript` VALUES (60437, '2302', '蓝叶散', '蓝叶1两，犀角屑1两半，木香1分，川升麻1两半，玄参1两，蘧麦1两，甘草半两（炙微赤，锉）。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十八', '热病，热毒盛，疱疮出，头黑者。', '-', '-', '升麻', '[\'蓝叶\', \'犀角\', \'两半\', \'木香\', \'分川\', \'升麻\', \'两半\', \'玄参\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60438, '2291', '碧霞浆', '羌活5钱，独活5钱，白芷5钱，川乌5钱，细辛5钱，菖蒲5钱，苍术5钱，风藤5钱，苦参5钱，当归5钱，防风5钱，升麻5钱，藁本5钱，蒺藜5钱，荆芥5钱，木瓜5钱，薄荷5钱，茄根5钱，防己5钱，天麻5钱，川芎5钱，射干5钱，麻黄5钱，水萍5钱，胡麻5钱，萎蕤5钱，首乌5钱，木香5钱，檀香5钱，沉香5钱，仙灵脾5钱，威灵仙5钱，蛇床子5钱，（台木）实5钱，金银花5钱，羊踯躅花5钱。', '酒浆1坛，入药5两，隔汤煮透，俟冷，每饮1杯，避风二时，朝夕饮。', '《解围元薮》卷四', '痛风寒湿痿困诸症。', '-', '-', '升麻', '[\'羌活\', \'独活\', \'白芷\', \'川乌\', \'细辛\', \'菖蒲\', \'苍术\', \'风藤钱\', \'苦参\', \'当归\', \'防风\', \'升麻\', \'藁本\', \'蒺藜\', \'荆芥\', \'木瓜\', \'薄荷\', \'钱茄\', \'根钱\', \'防己\', \'天麻\', \'川芎\', \'射干\', \'麻黄\', \'水萍\', \'胡麻\', \'萎蕤\', \'首乌\', \'木香\', \'檀香\', \'沉香\', \'仙灵脾\', \'威灵仙\', \'蛇床子\', \'钱台\', \'木实\', \'金银\', \'花钱\', \'羊踯躅\', \'花钱\']');
INSERT INTO `prescript` VALUES (60439, '2277', '马兜铃饮', '马兜铃7枚，桑根白皮（锉）3两，甘草（炙）2两，升麻1两，灯心1小束。', '每服5钱匕，水1盏半，煎至8分，去滓温服，1日3次。', '《圣济总录》卷四十九', '肺热咳嗽，气急喘促。', '-', '-', '升麻', '[\'马兜铃\', \'桑根\', \'白皮\', \'甘草\', \'升麻\', \'灯心\', \'小束\']');
INSERT INTO `prescript` VALUES (60440, '2223', '丹砂丸', '丹砂（别研）半两，雄黄（别研）半两，龙齿（去土，研）半两，羚羊角屑半两，远志（去心）半两，菖蒲（洗，锉，焙）1两，羌活（去芦头）1两，独活（去芦头）1两，升麻1两，芎1两，沙参1两，防风（去叉）1两。', '每服20丸，温水送下，1日2次。', '《圣济总录》卷一五○', '妇人心气不足，被风所乘，惊悸不己。', '-', '-', '升麻', '[\'丹砂\', \'别研\', \'半两\', \'雄黄\', \'别研\', \'半两\', \'龙齿\', \'土研\', \'半两\', \'羚羊角\', \'半两\', \'远志\', \'去心\', \'半两\', \'菖蒲\', \'羌活\', \'芦头\', \'独活\', \'芦头\', \'升麻\', \'两芎\', \'沙参\', \'防风\']');
INSERT INTO `prescript` VALUES (60441, '2211', '羚羊角汤', '羚羊角1两（镑），赤茯苓1两（去黑皮），升麻1两，槟榔1两（锉），泽泻1两（锉），甘草1两（炙，锉），芍药1两，木通1两（锉），黄芩1两（去黑心），杏仁（汤浸，去皮尖双仁，炒）1两。', '每服3钱匕，以水1盏，加淡竹叶14片，同煎至7分，去滓温服，不拘时候。', '《圣济总录》卷五十一', '肾脏实热，多怒好忘，肢体烦满，腰腹急重。', '-', '-', '升麻', '[\'羚羊角\', \'两镑\', \'茯苓\', \'两去\', \'黑皮\', \'升麻\', \'槟榔\', \'泽泻\', \'甘草\', \'芍药\', \'木通\', \'黄芩\', \'两去\', \'黑心\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\']');
INSERT INTO `prescript` VALUES (60442, '2063', '蓝花汤', '蓝花1两，漏芦（去芦头）1两，大黄（锉，炒）1两，升麻1两，黄芩（去黑心）1两。', '每服5钱匕，水1盏半，竹叶2-7片煎至1盏，下芒消末1钱匕，再煎1沸，去滓，空心温服。如已得利，去芒消。', '《圣济总录》卷一三七', '代指肿痛。', '-', '-', '升麻', '[\'蓝花\', \'漏芦\', \'芦头\', \'大黄\', \'升麻\', \'黄芩\', \'黑心\']');
INSERT INTO `prescript` VALUES (60443, '2000', '濡肠汤', '熟地1两，当归1两，升麻5分，牛膝3钱。', '水煎服。', '《辨证录》卷九', '肾虚大便闭结，口干舌躁，咽喉肿痛，头目昏晕，面红烦躁。', '-', '-', '升麻', '[\'熟地\', \'当归\', \'升麻\', \'牛膝\']');
INSERT INTO `prescript` VALUES (60444, '1984', '寒水石散', '寒水石3斤，石膏3斤，磁石3斤，滑石3斤（捣为细末，用水1石，煮至4升4斗，去滓，入后药），元参1斤（洗，焙，锉），羚羊角5两，升麻5两，丁香1两，木香半两，甘草8两（以上6味捣为末，入药汁中，再煮取1斗5升，去滓，顷入下2味药），朴消（精者）2斤，消石2斤（好者，以上2味入前药汁中微火煎，不住手将柳木篦搅，候有7-8斤许，投在木盆中半日久，候欲凝，却入下2味），朱砂2两（细研），麝香（当门子）1两2钱（乳细，以上2味入前药汁中拌匀，调令全）。', '每服1钱或2钱，冷水调下，大人小儿仔细加减，食后服。', '《普济方》卷二五五', '脚气毒遍内外，壮热不解，口中生疮，狂走毒厉；及解中请热药毒，邪热，卒黄等；及蛊毒，鬼魅，野道热毒；又治小儿惊痫热病。', '-', '-', '升麻', '[\'寒水石\', \'石膏\', \'磁石\', \'滑石\', \'细末\', \'用水\', \'石煮\', \'升斗\', \'去滓入\', \'药元\', \'参斤\', \'羚羊角\', \'升麻\', \'丁香\', \'木香\', \'半两\', \'甘草\', \'以上\', \'入药\', \'斗升\', \'去滓顷\', \'下味\', \'药朴\', \'消精者\', \'消石斤\', \'好者\', \'以上\', \'味入\', \'药汁\', \'微火\', \'不住\', \'柳木\', \'搅候\', \'斤许\', \'投在\', \'木盆\', \'半日\', \'久候\', \'下味\', \'朱砂\', \'两细研\', \'麝香\', \'门子\', \'乳细\', \'以上\', \'味入\', \'药汁\', \'拌匀\', \'调令\']');
INSERT INTO `prescript` VALUES (60445, '1982', '羚羊角汤', '羚羊角（镑屑）1分，升麻3分，射干1分，陈橘皮（汤浸，去白，焙）1分，白药半两。', '每服1钱匕，以水7分，煎至4分，去滓，分3次温服。早晨、日午、夜卧各1服。', '《圣济总录》卷一八○', '小儿喉痹痛，咽塞不利。', '-', '-', '升麻', '[\'羚羊角\', \'升麻\', \'射干\', \'分陈\', \'橘皮\', \'汤浸\', \'白焙分\', \'白药\', \'半两\']');
INSERT INTO `prescript` VALUES (60446, '1925', '黑参汤', '黑参2钱，苦参2钱，栀子（研）2钱，菊花2钱，黄连2钱，枳壳（麸炒）2钱，草决明2钱，车前子2钱，防风2钱，大黄（炒）2钱，升麻2钱。', '水煎，食后服。', '《金鉴》卷六十五', '心经实火，大眦肉色深红，时觉疼痛。', '-', '-', '升麻', '[\'参钱\', \'苦参\', \'栀子\', \'研钱\', \'菊花\', \'黄连\', \'枳壳\', \'草决明\', \'车前子\', \'防风\', \'大黄\', \'升麻\']');
INSERT INTO `prescript` VALUES (60447, '1900', '漏芦散', '漏芦1两半，木通1两，蓝叶1两，犀角屑1两，栀子仁1两，玄参1两，川升麻1两，川朴消1两，甘草半两（炙微赤，锉）。', '每服3钱，水1中盏，煎至6分，去滓，入生地黄汁半合，温服，不拘时候。', '《圣惠》卷十八', '热病，毒气攻皮肤，生疮痒痛。', '-', '-', '升麻', '[\'漏芦\', \'两半\', \'木通\', \'两蓝叶\', \'犀角\', \'栀子\', \'玄参\', \'两川\', \'升麻\', \'两川朴\', \'消两\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60448, '1878', '碧雪煎', '大青3两，吴蓝叶2两，竹茹3两，麦门冬2两（去心），子芩2两，甘草3两（生用），枳壳3两（去瓤），地骨皮3两，龙胆3两（去芦头），犀角屑2两，玄参3两，赤茯苓2两，川升麻2两，羚羊角犀2两，上细锉，以水2斗，煮至1斗，去滓澄清。龙齿2两（细研），牛黄2两（细研），麝香1两（细研），青黛5两（细研），朴消10斤（炼过者）。', '每有患者，以冷水调下半匙。', '《圣惠》卷九十五', '心神烦热，时行温病，癫痫，热毒，头痛，赤眼口疮，酒黄，大人小儿一切热病。', '-', '-', '升麻', '[\'大青\', \'吴蓝叶\', \'竹茹\', \'麦门冬\', \'两去\', \'心子\', \'甘草\', \'两生用\', \'枳壳\', \'两去\', \'地骨皮\', \'龙胆\', \'两去\', \'芦头\', \'犀角\', \'玄参\', \'两赤\', \'茯苓\', \'两川\', \'升麻\', \'羚羊角\', \'犀两上\', \'以水斗\', \'澄清\', \'龙齿\', \'两细研\', \'牛黄\', \'两细研\', \'麝香\', \'两细\', \'研青\', \'两细\', \'研朴\', \'消斤\', \'过者\']');
INSERT INTO `prescript` VALUES (60449, '1865', '槐枝煎', '槐枝（二三月采好者，锉）1斗，桑枝（锉）1斗，柳枝（锉）1斗（3味以水5斗，隔宿浸，次日入锅文武火煎，约得1斗，去滓，再入铜铛煎至5升入后药），槐蛀虫1两，细辛（去苗叶）半两，藁木（去苗土）1两，胡桐泪半两，升麻半两，莽草半两，麝香（研）1分。', '上将后7味为末，入前药汁内，更煎如饧。临卧净漱口，以药半匙敷痛处，有涎即吐之，临卧再用。', '《圣济总录》卷一一七', '口疮。', '-', '-', '升麻', '[\'槐枝\', \'二三月\', \'采好者\', \'斗桑枝\', \'柳枝\', \'锉斗味\', \'以水斗\', \'隔宿\', \'次日\', \'入锅\', \'文武\', \'再入\', \'铜铛\', \'升入\', \'后药槐\', \'蛀虫\', \'细辛\', \'苗叶\', \'半两\', \'藁木去\', \'苗土\', \'两胡桐\', \'半两\', \'升麻\', \'半两\', \'莽草\', \'半两\', \'麝香\', \'研分\']');
INSERT INTO `prescript` VALUES (60450, '1821', '黄芩散', '黄芩2两（去黑心），玄参2两，大黄1两（锉，炒），甘草1两（炙，锉），枳壳（去瓤，麸炒）1两，升麻1两半（焙）。', '每服2钱匕，以熟水调下。', '《圣济总录》卷二十三', '伤寒。头痛，大热烦燥。', '-', '-', '升麻', '[\'黄芩\', \'两去\', \'黑心\', \'玄参\', \'大黄\', \'甘草\', \'枳壳\', \'升麻\', \'两半\']');
INSERT INTO `prescript` VALUES (60451, '1815', '解肌汤', '芍药2两，麻黄（去节）3分，升麻半两，甘草（炙）半两。', '方中芍药，《医学纲目》引作“石膏”。如3-5服后犹恶风者，加麻黄半两，石膏1两。', '《伤寒微旨论》卷上', '伤寒阴阳俱有余，两手脉浮数，或紧或缓，三部俱有力，无汗恶风，时届立春以后至清明以前者。', '-', '-', '升麻', '[\'芍药\', \'麻黄\', \'节分\', \'升麻\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (60452, '1758', '渫牙散', '荆芥（去梗）1两，细辛1两，莽草1两，升麻1两，木律半两。', '每服5钱，水2盏，入槐枝10数茎，盐2钱，同煎令浓。热含冷吐。', '《博济》卷三', '齿牙浮动，宣露疼痛。', '-', '-', '升麻', '[\'荆芥\', \'细辛\', \'莽草\', \'升麻\', \'两木律\', \'半两\']');
INSERT INTO `prescript` VALUES (60453, '1749', '葵子散', '葵子1两，豉（微炒）1两，犀角屑1两，地榆（锉）1两，川升麻1两，露蜂房（微炒）1两，甘草3分（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '眼丹石毒，先面赤口干，目黄赤睛疼痛，恐变生翳障。', '忌炙煿热面。', '-', '升麻', '[\'葵子\', \'两豉\', \'犀角\', \'地榆\', \'两川\', \'升麻\', \'露蜂房\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (60454, '1709', '马兜铃散', '马兜铃1两，桑根白皮1两（锉），川升麻半两，灯心3束，甘草3分（炙微赤，锉），大腹皮1两（锉），赤茯苓1两，枳壳1两（麸炒微黄，去瓤）。', '每服5钱，以水1大盏，加生姜半分，煎至5分，去滓温服，不拘时候。', '《圣惠》卷四十六', '咳嗽喘急，胸膈烦闷。', '-', '-', '升麻', '[\'马兜铃\', \'两桑根\', \'白皮\', \'锉川\', \'升麻\', \'半两\', \'灯心\', \'甘草\', \'分炙微\', \'大腹皮\', \'茯苓\', \'枳壳\', \'两麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (60455, '1684', '蓝子散', '蓝子5合，升麻8两，甘草4两（炙），王不留行4两。', '每服2方寸匕，冷水调下，日三夜二；及以1方寸匕水和匀，涂疮上。', '《鬼遗》卷二', '解毒。主金疮，中菵药。', '-', '-', '升麻', '[\'蓝子合\', \'升麻\', \'甘草\', \'王不留行\']');
INSERT INTO `prescript` VALUES (60456, '1590', '葛根汤', '葛根（锉）2两，黄连（去须）2两，木通（锉）2两，吴蓝2两，甘草（炙，锉）2两，升麻1两半，黄芩（去黑心）1两半，大黄（锉，炒）1两半，石膏4两。', '每服5钱匕，以水1盏半，煎至6分，去滓，入消1钱匕，地黄汁半合，更煎2-3沸，放温，食后临卧服。', '《圣济总录》卷一○八', '金石发动，眼痛欲裂。', '-', '-', '升麻', '[\'葛根\', \'黄连\', \'木通\', \'甘草\', \'升麻\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'大黄\', \'两半\', \'石膏\']');
INSERT INTO `prescript` VALUES (60457, '1541', '葛根升麻加芍药汤', '升麻1钱，葛根1钱，甘草（炙）1钱，芍药2钱。', '水1盏，煎7分，温服，不拘时候。', '《医学正传》卷八', '痘疮发时身痛，若红点方见，为寒所折，而肉体有热之轻者。', '-', '-', '升麻', '[\'升麻\', \'葛根\', \'甘草\', \'芍药\']');
INSERT INTO `prescript` VALUES (60458, '1538', '葛根麦门冬散', '葛根3钱，麦门冬（去心）4钱，人参2钱，石膏半两，川升麻2钱，甘草2钱，茯苓2钱，赤芍药1钱。', '葛根麦门冬汤（《保婴撮要》卷十八）、干葛麦冬汤（《痘疹会通》卷四）。《准绳·幼科》有淡竹叶七片。', '《小儿痘疹方论》', '外除表邪，内清胃火，兼补元气。主小儿热毒斑疹，头痛壮热，心神烦闷。', '-', '-', '升麻', '[\'葛根\', \'麦门冬\', \'心钱\', \'人参\', \'石膏\', \'半两\', \'升麻\', \'甘草\', \'茯苓\', \'芍药\']');
INSERT INTO `prescript` VALUES (60459, '1536', '羚羊角汤', '羚羊角1两（镑），射干1两（去毛），麦门冬1两（去心，焙），芦根1两，升麻3分，芍药3分，木通（锉）1两半。', '每服5钱匕，以水1盏半，煎至8分，去滓，食后温服。', '《圣济总录》卷三十', '伤寒热病后，余热上冲，口舌生疮。', '-', '-', '升麻', '[\'羚羊角\', \'两镑\', \'射干\', \'两去\', \'麦门冬\', \'两去\', \'芦根\', \'升麻\', \'芍药\', \'木通\', \'两半\']');
INSERT INTO `prescript` VALUES (60460, '1499', '揩齿防风散', '防风（去叉）1分，升麻1分，细辛（去苗叶）1分，钟乳粉半两，凝水石（捣）半两，白石英（捣）半两，丹砂（研）1分，沉香（锉）1分，丁香1分，麝香（研）1分。', '每日如常揩齿。', '《圣济总录》卷一二一', '涤除腐气，令牙齿坚牢，齿槽固密，诸疾不生。主', '-', '-', '升麻', '[\'防风\', \'去叉分\', \'升麻\', \'细辛\', \'苗叶\', \'分钟\', \'乳粉\', \'半两\', \'凝水石\', \'半两\', \'白石英\', \'半两\', \'丹砂\', \'研分\', \'沉香\', \'锉分\', \'丁香\', \'麝香\', \'研分\']');
INSERT INTO `prescript` VALUES (60461, '1475', '漏芦汤', '漏芦1两，白蔹1两，黄芩（去黑心）1两，麻黄（去节）1两，枳实（麸炒，去瓤）1两，升麻1两，芍药1两，甘草（炙）1两，朴消1两，大黄2两。', '每服3钱，气实人5钱，水1盏半，文武火煎7沸，去滓，空心热服。', '《外科精义》卷下', '一切恶疮，毒肿丹瘤，瘰疬疔肿，鱼睛五发，凛疽。初觉1-2日，便如伤寒，头痛烦渴，拘急恶寒，肢体疼痛，四肢沉重，恍惚闷乱，坐卧不宁，皮肤状热，大便秘涩，小便赤黄。', '好身莫服。', '-', '升麻', '[\'漏芦\', \'白蔹\', \'黄芩\', \'黑心\', \'麻黄\', \'去节\', \'枳实\', \'升麻\', \'芍药\', \'甘草\', \'两朴\', \'消两\', \'大黄\']');
INSERT INTO `prescript` VALUES (60462, '1473', '漏芦汤', '漏芦（去芦头）1两，升麻1两，连翘1两，麻黄（去根节）1两，大黄3分，防已3分，木香3分，白蔹3分，沉香3分。', '每服5钱匕，水1盏半，加竹叶7片，煎至1盏，搅匀，去滓，空心温服。取利2-3行，未利再服。', '《圣济总录》卷一二九', '附骨疽。', '-', '-', '升麻', '[\'漏芦\', \'芦头\', \'升麻\', \'连翘\', \'麻黄\', \'根节\', \'大黄\', \'分防\', \'已分\', \'木香\', \'白蔹\', \'沉香\']');
INSERT INTO `prescript` VALUES (60463, '1427', '缓筋汤', '熟地黄1分，生甘草2分，柴胡2分，红花2分，炙甘草2分，苏木2分，独活2分，藁本3分，升麻3分，黄芩3分，草豆蔻仁3分，酒黄柏3分，生地黄3分，当归身3分，麻黄3分，羌活3钱，苍术5分。', '水2大盏，煎至1盏，去滓，食远服。', '《兰室秘藏》卷中', '两目如火肿痛，两足及伏免筋骨痛，膝少力，身重腰痛，夜恶寒，痰嗽，颈项皆急痛，目外眵目丝急，食不下。', '-', '-', '升麻', '[\'熟地黄\', \'生甘草\', \'柴胡\', \'红花\', \'甘草\', \'苏木\', \'独活\', \'藁本\', \'升麻\', \'黄芩\', \'草豆蔻\', \'仁分\', \'黄柏\', \'生地黄\', \'当归\', \'身分\', \'麻黄\', \'羌活\', \'苍术\']');
INSERT INTO `prescript` VALUES (60464, '1416', '鳖甲散', '鳖甲3分（涂醋，炙令黄，去裙襕），川升麻半两，葳蕤1斤，黄连（去须）1斤，当归（锉，微炒）1斤，赤芍药1斤，桂心1斤，犀角屑1斤，贝齿1斤，茯神1斤，秦艽（去苗）1斤，甘草（炙微赤，锉）1斤，柴胡半两（去苗），麻黄半两（去根节），人参半两（去芦头）。', '每服2钱，以粥饮调下，不拘时候。', '《圣惠》卷十三', '伤寒不解，变成狐惑，默默欲睡，卧则不安，咽喉干痛，口内生疮，恶闻食气，时时下痢。', '-', '-', '升麻', '[\'鳖甲\', \'分涂\', \'炙令\', \'黄去\', \'升麻\', \'半两\', \'葳蕤\', \'黄连\', \'须斤\', \'当归\', \'锉微\', \'炒斤\', \'芍药\', \'斤桂心斤\', \'犀角\', \'贝齿斤\', \'茯神\', \'秦艽\', \'苗斤\', \'甘草\', \'炙微\', \'锉斤\', \'柴胡\', \'半两\', \'去苗\', \'麻黄\', \'半两\', \'根节\', \'人参\', \'半两\', \'芦头\']');
INSERT INTO `prescript` VALUES (60465, '1360', '大黄散', '大黄1两，瞿麦1两，白干葛1两，牛蒡子1两，地骨皮1两，苍术1两，升麻2两，大青2两，芍药（赤者）2两，枸杞子2两，当归2两，吊藤2两，黄芩2两，黄连2两，连翘2两，羌活2两，青皮2两，郁金2两，芎2两，桑白皮2两，甘草2两，牵牛（黄者）2两，荆芥穗2两。', '每服1-2钱，食后、临卧用生姜自然汁调下。', '《鸡峰》卷二十五', '百种毒。', '-', '-', '升麻', '[\'大黄\', \'瞿麦\', \'白干\', \'葛两\', \'牛蒡子\', \'地骨皮\', \'苍术\', \'升麻\', \'两大青\', \'芍药\', \'赤者\', \'枸杞子\', \'当归\', \'两吊藤\', \'黄芩\', \'黄连\', \'连翘\', \'羌活\', \'青皮\', \'郁金\', \'两芎\', \'桑白皮\', \'甘草\', \'牵牛\', \'黄者\', \'荆芥\']');
INSERT INTO `prescript` VALUES (60466, '1339', '解肌散', '麻黄1两（去根节），川升麻1两，赤芍药1两，石膏1两，贝齿3枚，甘草半两（炙微赤，锉），杏仁1两（汤浸，去皮尖双仁，麸炒微黄）。', '每服5钱，以水1大盏，入生姜半分，大枣3枚，煎至5分，去滓，不拘时候热服。衣覆取汗。', '《圣惠》卷十五', '时气1日，头痛壮热，四肢烦疼。', '-', '-', '升麻', '[\'麻黄\', \'两去\', \'根节川\', \'升麻\', \'两赤\', \'芍药\', \'石膏\', \'两贝齿\', \'甘草\', \'半两\', \'炙微\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (60467, '1309', '漏芦散', '漏芦1两，白蔹1两，黄芩1两，麻黄1两（去根节），知母1两，枳实1两（麸炒微黄），川升麻1两，犀角屑1两，赤芍药1两，甘草1两（炙微赤，锉），川芒消2两，川大黄2两（锉碎，微炒）。', '每服3钱，水1中盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷六十四', '毒肿疼痛，心神烦热，大肠秘涩。', '-', '-', '升麻', '[\'漏芦\', \'白蔹\', \'黄芩\', \'麻黄\', \'两去\', \'根节\', \'知母\', \'枳实\', \'两麸\', \'微黄\', \'升麻\', \'犀角\', \'芍药\', \'甘草\', \'两炙微\', \'锉川芒\', \'消两川\', \'大黄\']');
INSERT INTO `prescript` VALUES (60468, '1296', '丹参煮散', '丹参3两，芎2两，杜仲2两，续断2两，地骨皮2两，当归1两18铢，通草1两18铢，干地黄1两18铢，麦门冬1两18铢，升麻1两18铢，禹余粮1两18铢，麻黄1两18铢，牛膝2两6铢，生姜（切，炒取焦干）2两，牡蛎2两，甘草1两6铢，桂心1两6铢。', '丹参散（《普济方》卷十五）。', '《千金》卷十一', '筋实极，则两脚下满而痛，不得远行，脚心如割，筋断折痛不可忍。', '忌海藻、菘菜、生葱、芜荑。', '-', '升麻', '[\'丹参\', \'两芎\', \'杜仲\', \'续断\', \'地骨皮\', \'当归\', \'两铢\', \'通草\', \'两铢干\', \'地黄\', \'两铢\', \'麦门冬\', \'两铢\', \'升麻\', \'两铢\', \'禹余粮\', \'两铢\', \'麻黄\', \'两铢\', \'牛膝\', \'两铢\', \'生姜\', \'炒取\', \'焦干\', \'牡蛎\', \'甘草\', \'两铢桂心\', \'两铢\']');
INSERT INTO `prescript` VALUES (60469, '1283', '揩齿散', '莲子草1两，鸡眼草1两，熟干地黄1两，马齿苋1两，酸石榴皮1两，海盐1两，青胡桃皮1两，没石子1两，丁香半两，川升麻半两（末），麝香1分（细研）。', '每日早晨及夜卧时，常用揩齿。', '《圣惠》卷四十一', '黑髭主', '-', '-', '升麻', '[\'莲子\', \'鸡眼\', \'两熟\', \'地黄\', \'马齿苋\', \'两酸\', \'石榴皮\', \'海盐\', \'两青\', \'胡桃\', \'石子\', \'丁香\', \'半两\', \'升麻\', \'半两\', \'麝香\', \'分细研\']');
INSERT INTO `prescript` VALUES (60470, '1244', '槟榔散', '槟榔1两，桑根白皮1两，茯神3分，地骨皮3分，黄连3分（去须），葳蕤3分，麦门冬3分（去心），甘草半两（炙微赤，锉），川大黄1两（锉碎，微炒），川升麻3分，犀角屑1两，赤芍药半两（锉），黄耆半两（锉），枳壳半两（麸炒微黄，去瓤）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷四十五', '服乳石致脏腑壅滞，发脚气肿满，四肢烦疼，口干心躁，夜卧恍惚。', '-', '-', '升麻', '[\'槟榔\', \'两桑根\', \'白皮\', \'茯神\', \'地骨皮\', \'黄连\', \'分去\', \'葳蕤\', \'麦门冬\', \'分去\', \'甘草\', \'半两\', \'炙微\', \'锉川\', \'大黄\', \'碎微\', \'炒川\', \'升麻\', \'犀角\', \'芍药\', \'半两\', \'黄耆\', \'半两\', \'枳壳\', \'半两\', \'微黄\']');
INSERT INTO `prescript` VALUES (60471, '1094', '橘皮甘草汤', '甘草1两，升麻半两，生姜3两，橘皮2两。', '水3升，煮取2升，顿服之。', '方出《肘后方》卷二，名见《外台》卷三', '伤寒呕哕不止，病源伏热在胃，令人胸满则逆，气逆则哕。若大下后，胃气虚，亦可致哕。', '忌海藻、菘菜。', '-', '升麻', '[\'甘草\', \'升麻\', \'半两\', \'生姜\', \'橘皮\']');
INSERT INTO `prescript` VALUES (60472, '1092', '黄耆丸', '黄耆2两（锉），防风2两（去叉），麦门冬2两（去心，焙），羌活（去芦头）2两，五加皮1两半，甘草1两（炙，锉），升麻1两，苦参1两，白鲜皮1两，菊花1两，枳壳1两（去瓤，麸炒），黄连1两（去须，炒），车前子1两，葶苈（隔纸炒）半两。', '每服20丸，空心食前以温酒送下，加至30丸。', '《圣济总录》卷十三', '热毒风上攻，头旋目眩，耳聋心烦，手足（疒帬）痹，皮肤瘙痒。', '-', '-', '升麻', '[\'黄耆\', \'防风\', \'两去\', \'麦门冬\', \'两去\', \'羌活\', \'芦头\', \'五加皮\', \'两半\', \'甘草\', \'升麻\', \'苦参\', \'白鲜皮\', \'菊花\', \'枳壳\', \'两去\', \'黄连\', \'两去\', \'车前子\', \'两葶\', \'隔纸\', \'半两\']');
INSERT INTO `prescript` VALUES (60473, '1022', '揩齿朱砂散', '朱砂1两（细研），海蛤2两（细研），石膏1两，细辛2两，川升麻2两，防风（去头芦）2两，寒水石3两，芎1两，槟榔2两，生干地黄2两半。', '每日早、晚各以暖水漱口3-5度，用药揩齿；或以薄纸贴药于齿上，不用漱口。', '《圣惠》卷三十四', '令黄黑齿变白净。主', '-', '-', '升麻', '[\'朱砂\', \'两细\', \'研海\', \'两细研\', \'石膏\', \'细辛\', \'两川\', \'升麻\', \'防风\', \'头芦\', \'寒水石\', \'两芎\', \'槟榔\', \'两生\', \'地黄\', \'两半\']');
INSERT INTO `prescript` VALUES (60474, '990', '鳖甲散', '鳖甲（涂醋，炙令黄，去裙襕）1两，柴胡（去苗）1两，川升麻1两，乌悔肉1两，枳实（麸炒微黄）1两，犀角屑1两，黄芩1两，甘草半两（炙微赤，锉）。', '鳖甲犀角汤（《伤寒总病论》卷五）、鳖甲汤（《圣济总录》卷二十一）。', '《圣惠》卷十三', '坏伤寒。经10日以来未解，热在胸膈，烦闷不止。', '-', '-', '升麻', '[\'鳖甲\', \'涂醋\', \'炙令\', \'黄去\', \'柴胡\', \'去苗\', \'两川\', \'升麻\', \'两乌悔\', \'枳实\', \'微黄\', \'犀角\', \'黄芩\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60475, '947', '漏芦汤', '漏芦1钱，紫花地丁1钱，荆芥1钱，当归1钱，连翘1钱，薄荷1钱，白芷1钱，升麻1钱。', '水2钟，煎8分，热服。如治便毒，加猪苓、泽泻；如在上者，加川芎、桔梗；面上，加蔓荆子；下部，加牛膝、木瓜、薏苡仁；如红肿势凶，大便秘结，加大黄3钱，麻黄3钱，甘草4分；如大便不秘，减大黄1钱半，次日红肿尽退，只用神灯照之；若红肿未退，加大黄、麻黄各1钱半，甘草1钱，服至肿消，若红肿未尽，主药及加药各1钱5分，煎服。', '《医林绳墨大全》卷九', '肿毒。', '-', '-', '升麻', '[\'漏芦\', \'紫花地丁\', \'荆芥\', \'当归\', \'连翘\', \'薄荷\', \'白芷\', \'升麻\']');
INSERT INTO `prescript` VALUES (60476, '918', '葛根汤', '葛根（锉，微炒）半两，麻黄（去节）半两，羌活（去芦头）半两，甘草（炙，锉）半两，枳壳（去瓤，麸炒）半两，杏仁（汤浸，去皮尖双仁，炒）1分，升麻1两，黄芩（去黑心）1两，大黄（锉，炒）1两，柴胡（去苗）3分，芍药3分，钩藤1分，蛇蜕（微炙）3寸，蚱蝉2枚（去翅，微炒），石膏（碎）1两半。', '每服1钱匕，以水半盏，煎至3分，入竹沥少许，更煎1-2沸，去滓，分3次温服。', '《圣济总录》卷一七二', '小儿初生，至百晬前后，惊痫连发不醒，及胎中感风，体冷面青，筋急反张。', '-', '-', '升麻', '[\'葛根\', \'半两\', \'麻黄\', \'去节\', \'半两\', \'羌活\', \'芦头\', \'半两\', \'甘草\', \'半两\', \'枳壳\', \'半两\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'炒分\', \'升麻\', \'黄芩\', \'黑心\', \'大黄\', \'柴胡\', \'苗分\', \'芍药\', \'钩藤\', \'蛇蜕\', \'微炙寸\', \'蚱蝉\', \'石膏\', \'两半\']');
INSERT INTO `prescript` VALUES (60477, '909', '漏芦汤', '漏芦6钱，连翘6钱，白蔹6钱，芒消6钱，甘草6钱，大黄1两，升麻9铢，枳实9铢，麻黄9铢，黄芩9铢。', '漏芦连翘汤（《千金》卷十）、漏芦散（《圣惠》卷九十）、千金漏芦汤（《卫生总微》卷二十）、漏芦煮散（《普济方》卷二八五）。', '《千金》卷五', '小儿热毒痈疽，赤白诸丹毒疮疖，眼赤痛，生翳障。', '-', '-', '升麻', '[\'漏芦\', \'连翘\', \'白蔹\', \'钱芒\', \'消钱\', \'甘草\', \'大黄\', \'升麻\', \'枳实\', \'麻黄\', \'黄芩\']');
INSERT INTO `prescript` VALUES (60478, '893', '丹砂膏', '丹砂5两，芎3两，大黄2两、蜀椒2两（去目，出汗），白芷2两，麝香3两，升麻2两，冶葛皮2两，麻黄5两（去节），丹参5两，巴豆2升（去皮心），桂心2两，附子12枚，皂荚2两（去皮子）。', '鼻塞，取半核大，纳鼻中、缩气令人聪里；若耳聋，取如两枣核大，烊之如水，纳其耳中，3-5年聋可愈；或寒癖腹满坚胀，及飞尸、恶毒、楚痛，温酒服；霍乱当成末成，已吐未痢，白汤服枣核大，若已痢1-2行，而腹烦痛，更服之；眼中风膜，膜或痛，常下泪，取如粟大，注眼中，自当下，止，或半日痛便愈；又胸背喉颈痛，摩足，口中亦稍稍令常闻有膏气。老小增减。', '《鬼遗》卷五', '百病，伤寒，温毒热疾，鼻塞，耳聋，寒癖腹满坚胀，及飞尸恶毒楚痛，霍乱当成末成，已吐未痢，或已痢一两行，而腹烦痛，眼中风膜，膜或痛，常下泪，胸背喉颈痛。', '当服取利为度，若不利，如人行15里可与热饮发，当预作白薄粥令冷，若过利要止者，多进冷粥，便住，若能忍，待药势尽，自止更佳。', '-', '升麻', '[\'丹砂\', \'两芎\', \'大黄\', \'两蜀椒\', \'两去\', \'出汗\', \'白芷\', \'麝香\', \'升麻\', \'两冶葛皮\', \'麻黄\', \'两去\', \'丹参\', \'巴豆\', \'去皮\', \'心桂心\', \'附子\', \'皂荚\', \'两去\', \'皮子\']');
INSERT INTO `prescript` VALUES (60479, '839', '蝉退散', '蝉退（去土）半两，蛇蜕（炙）半两，升麻（洗）半两，蒺藜（炒去角）半两，黄连（炒）半两，谷精草半两，大青叶半两，仙灵脾半两，威灵仙半两，井泉石半两，朱砂1分，螺粉1分。', '每服半钱或1钱，蜜水调下。', '《幼幼新书》卷十八引丁安中方', '斑疮翳障，眼不见光明。', '-', '-', '升麻', '[\'退去\', \'半两\', \'蛇蜕\', \'半两\', \'升麻\', \'半两\', \'蒺藜\', \'半两\', \'黄连\', \'半两\', \'谷精草\', \'半两\', \'大青叶\', \'半两\', \'仙灵脾\', \'半两\', \'威灵仙\', \'半两\', \'泉石\', \'半两\', \'朱砂\', \'分螺\', \'粉分\']');
INSERT INTO `prescript` VALUES (60480, '833', '鳖甲丸', '黄耆1两，柴胡1两，枳壳1两，知母1两，白茯苓1两，沉香1两，人参1两，附子1两，木香1两，升麻1两，肉桂1两，胡黄连1两，杏仁1两，当归1两，常山1两，羌活1两，荆三棱1两，乌梅肉1两，安息香（明者，同胡桃肉细研）1两。', '丈夫妇人15岁已上，每服20-30丸，温酒送下；妇人荆芥酒送下。所煮膏子须吃斟酌多少，勿令剩下，但少些不妨，却别熬酒，若膏剩，恐鳖不全故也。凡服此药恐热，3-5更须服八仙饮子1服解之。', '《普济方》卷三一九', '男子、妇人、童男、室女五劳七伤，传疰，伏连，飞尸，尸注，八极，骨蒸肺痿，黄瘦虚劳无肉，肌肉不生；妇人血蒸，五心烦热，血蒸劳气；室女月闭，黄瘦，气块肠痛，经脉不调，干嗽，咽膈不利，症瘕积块，脸赤口疮。', '-', '-', '升麻', '[\'黄耆\', \'柴胡\', \'枳壳\', \'知母\', \'白茯苓\', \'沉香\', \'人参\', \'附子\', \'木香\', \'升麻\', \'肉桂\', \'胡黄连\', \'杏仁\', \'当归\', \'常山\', \'羌活\', \'荆三棱\', \'乌梅\', \'安息香\', \'明者\', \'胡桃肉\', \'细研\']');
INSERT INTO `prescript` VALUES (60481, '822', '解毒香豉饮子', '香豉2两，石膏3两，栀子仁1两，大青1两，川升麻1两，川芒消1两，甘草半两（生用），川大黄1两（锉碎，微炒）。', '每服半两，用水1大盏，入生姜半分，葱白7寸，煎至5分，去滓、不拘时候温服。', '《圣惠》卷十八', '心肺脏热，毒攻于皮肤，遍生赤斑，重者其色紫黑。', '-', '-', '升麻', '[\'香豉\', \'石膏\', \'栀子\', \'两大青\', \'两川\', \'升麻\', \'两川芒\', \'消两\', \'甘草\', \'半两\', \'生用川\', \'大黄\']');
INSERT INTO `prescript` VALUES (60482, '820', '蓝叶汤', '吴蓝叶半两，犀角（镑）半两，升麻半两，黄耆（锉）半两，山栀子仁半两，连翘半两，甘草（炙）半两，黄芩（去黑心）1分，大青1分，玄参1分，大黄（锉，炒）3分。', '每服1钱匕，水7分，煎至4分，去滓，食后、临卧温服。', '《圣济总录》卷一八二', '小儿疽疮。', '-', '-', '升麻', '[\'吴蓝叶\', \'半两\', \'犀角\', \'半两\', \'升麻\', \'半两\', \'黄耆\', \'半两\', \'栀子\', \'半两\', \'连翘\', \'半两\', \'甘草\', \'半两\', \'黄芩\', \'黑心\', \'分大青分\', \'玄参\', \'大黄\', \'炒分\']');
INSERT INTO `prescript` VALUES (60483, '819', '解毒散', '犀角屑1两，木通（锉）1两，川升麻1两，赤芍药1两，川朴消1两，石膏2两，甘草（生，锉）半两，玄参半两，麦门冬（去心）半两。', '每服4钱，以水1中盏，煎至6分，去滓，不拘时候温服。', '《圣惠》卷六十一', '痈疽始觉。', '-', '-', '升麻', '[\'犀角\', \'木通\', \'两川\', \'升麻\', \'两赤\', \'芍药\', \'两川朴\', \'消两\', \'石膏\', \'甘草\', \'半两\', \'玄参\', \'半两\', \'麦门冬\', \'去心\', \'半两\']');
INSERT INTO `prescript` VALUES (60484, '781', '解毒升麻散', '川升麻、栀子仁、大青、黄芩、甘草（炙微赤，锉）1两，石膏2两。', '本方方名，《普济方》引作“升麻散”。', '《圣惠》卷十', '伤寒热毒不解，欲变成斑。', '-', '-', '升麻', '[\'升麻\', \'栀子\', \'仁大青\', \'黄芩\', \'甘草\', \'炙微\', \'石膏\']');
INSERT INTO `prescript` VALUES (60485, '698', '丹参丸', '丹参1两，苦参1两，升麻1两，黄芩（去黑心）半两，防风（去叉）半两，枳壳（去瓤，麸炒）1两，乌头（炮裂，去皮脐）1两。', '本方方名，原作“丹麦丸”，据文瑞楼本改。', '《圣济总录》卷十一', '风疮痒，搔之成疮。', '-', '-', '升麻', '[\'丹参\', \'苦参\', \'升麻\', \'黄芩\', \'黑心\', \'半两\', \'防风\', \'半两\', \'枳壳\', \'乌头\', \'炮裂\', \'去皮\']');
INSERT INTO `prescript` VALUES (60486, '688', '解毒膏', '川升麻1两，白蔹2两，漏芦1两，连翘1两，川芒消1两，蛇衔草2两半，黄芩1两半，栀子仁30枚，蒴藋根2两。', '于软帛上摊贴，日2换之。', '《圣惠》卷六十三', '一切毒肿疼痛。', '-', '-', '升麻', '[\'升麻\', \'白蔹\', \'漏芦\', \'连翘\', \'两川芒\', \'消两蛇\', \'两半\', \'黄芩\', \'两半\', \'栀子\', \'仁枚\']');
INSERT INTO `prescript` VALUES (60487, '528', '黑散子', '川大黄半两，麻黄（去根节）、川升麻、杏仁（去皮尖）、芍药、甘草。', '每服半钱至1钱，煎荆芥汤调下。', '《普济方》卷三六八', '解邪热。主小儿伤寒。', '-', '-', '升麻', '[\'大黄\', \'半两\', \'麻黄\', \'根节川\', \'升麻\', \'杏仁\', \'皮尖\', \'芍药\', \'甘草\']');
INSERT INTO `prescript` VALUES (60488, '509', '解散', '升麻3两，大黄3两，黄连3两，甘草3两，黄柏3两，芍药6两，白鸭通5合，黄芩4两，栀子仁14枚，竹叶（切）1升，豉1升。', '大黄汤（《普济方》卷二六三）。若上气者，加杏仁5合；腹满，加石膏3两。', '《千金》卷二十四', '服散后盛热实，大小便赤。', '-', '-', '升麻', '[\'升麻\', \'大黄\', \'黄连\', \'甘草\', \'黄柏\', \'芍药\', \'两白\', \'通合\', \'黄芩\', \'栀子\', \'仁枚\', \'竹叶\', \'切升豉\']');
INSERT INTO `prescript` VALUES (60489, '499', '麻前饮', '升麻2钱，车前子（炒）2钱。', '以黄酒2钟，煎8分服。', '《仙拈集》卷二', '大小便闭。', '-', '-', '升麻', '[\'升麻\', \'车前子\']');
INSERT INTO `prescript` VALUES (60490, '468', '黄耆汤', '黄耆（锉）半两，连翘半两，升麻半两，恶实（炒）半两，玄参1分，丹参1分，露蜂房（炙）1分，枳壳（去瓤，麸炒）1分，甘草（炙）1分。', '每服1钱匕，水7分，煎至4分，去滓，食后临卧温服。', '《圣济总录》卷一八二', '小儿痈疽疮疖肿毒。', '-', '-', '升麻', '[\'黄耆\', \'半两\', \'连翘\', \'半两\', \'升麻\', \'半两\', \'恶实\', \'半两\', \'玄参\', \'丹参\', \'露蜂房\', \'炙分\', \'枳壳\', \'炒分\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (60491, '465', '丹砂丸', '丹砂1两（细研，水飞），铁粉1两（细研），金箔50片（细研），银箔50片（细研），人参1两半（去芦头），获神2两，秦艽1两（去苗），川升麻1两，子芩1两，白鲜皮1两，麦门冬1两半（去心，焙），龙齿1两，木香1两，枳实1两（麸炒微黄），甘草半两（炙微赤，锉）。', '每服20丸，以荆芥汤送下，不拘时候。', '《圣惠》卷二十', '风虚惊悸，心神烦闷，睡卧不安。', '忌生血等。', '-', '升麻', '[\'丹砂\', \'两细\', \'研水飞\', \'铁粉\', \'两细研\', \'金箔\', \'片细\', \'研银\', \'箔片\', \'细研\', \'人参\', \'两半\', \'芦头\', \'获神\', \'秦艽\', \'两去\', \'苗川\', \'升麻\', \'两子\', \'白鲜皮\', \'麦门冬\', \'两半\', \'龙齿\', \'木香\', \'枳实\', \'两麸\', \'微黄\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (60492, '439', '解肌升麻汤', '升麻1两，芍药1两，石膏1两，麻黄1两，甘草1两，杏仁30枚，贝齿2枚（一作贝母18铢）。', '上(口父)咀。以水3升，煮取1升，尽服。温覆发汗便愈。', '《千金》卷九', '时气3-4日不解。', '-', '-', '升麻', '[\'升麻\', \'芍药\', \'石膏\', \'麻黄\', \'甘草\', \'杏仁\', \'贝齿\', \'一作\', \'贝母\']');
INSERT INTO `prescript` VALUES (60493, '435', '丹砂膏', '丹砂2两（末），蜀椒（去目闭口，汗）2两，大黄2两，白芷2两，甘草（炙）2两，巴豆3升（去皮心），麝香2两，芎2两，附子2枚，升麻2两，冶葛皮2两，犀角2两，当归2两，乌头2两，丹参1斤。', '四时常用，1日3次。治百病、伤寒、温毒热换。服如枣核大1枚；鼻塞，取半核大，纳外中，缩气令人聪里；若耳聋，取如两枣核大，烊之如水，纳其耳中，3-5年聋可愈；或寒癖腹满坚胀，及飞尸、恶毒、楚痛，温酒服；霍乱当成末成，已吐未痢，白汤服枣核大，若已痢1-2行，而腹烦痛，更服之；眼中风膜，膜或痛，常下泪，取如粟大，注眼中，自当下，止，或半目痛便愈；又胸背喉颈痛，摩足，口中亦稍稍令常闻有膏气，老小增减。', '《鬼遗》卷五', '百病，伤寒，温毒热疾，鼻塞，耳聋，寒癖腹满坚胀，及飞尸恶毒楚痛，霍乱当成末成，已吐末痢，或已痢一两行，而腹烦痛，眼中风膜，膜或痛，常下泪，胸背喉颈痛。', '当服取利为度。若不利，如人行15里可与热饮发。当预作白薄粥令冷，若过利要止者，多进冷粥，便住。若能忍，待药势尽，自止更佳。', '-', '升麻', '[\'丹砂\', \'两末\', \'闭口\', \'大黄\', \'白芷\', \'甘草\', \'巴豆\', \'去皮\', \'麝香\', \'两芎\', \'附子\', \'升麻\', \'两冶葛皮\', \'犀角\', \'当归\', \'乌头\', \'丹参\']');
INSERT INTO `prescript` VALUES (60494, '414', '擦牙石盐散', '白软石膏1斤，辽细辛12两5钱，川升麻2两5钱，川芎1两，白芷3两，馒头（炒成黑炭）半斤，白盐12两（入炭火煅红半日）。', '擦牙甚妙。', '《回春》卷五', '用此药久擦牙，永久坚固，再无牙痉、牙疼之症。主', '-', '-', '升麻', '[\'白软\', \'石膏\', \'细辛\', \'两钱川\', \'升麻\', \'川芎\', \'白芷\', \'馒头\', \'炒成\', \'黑炭\', \'半斤\', \'白盐\', \'两入\', \'炭火\', \'煅红\', \'半日\']');
INSERT INTO `prescript` VALUES (60495, '380', '鳖甲丸', '人参半两，牡丹皮半两，槟榔半两，吴茱萸半两，盐半两，肉豆蔻半两，赤芍药半两，泽泻半两，木香半两，远志半两，缩砂仁半两，枳壳半两，柴胡半两，麻黄半两，麝香半两，乌梅2两，知母1两，升麻1两，甘草1两，鳖甲1两，苁蓉1两，白蔹1两，葳蕤1两，虎骨1两，桃仁1两，羌活1两，防风1两，茯苓1两，附子1两，青蒿1两，秦艽1两，厚朴1两，牛膝1两，半夏1两，桂1两。', '每服30丸，空心温酒送下。', '《鸡峰》卷九', '风劳，肌瘦面黄。', '-', '-', '升麻', '[\'人参\', \'半两\', \'牡丹皮\', \'半两\', \'槟榔\', \'半两\', \'吴茱萸\', \'半两\', \'半两\', \'肉豆蔻\', \'半两\', \'芍药\', \'半两\', \'泽泻\', \'半两\', \'木香\', \'半两\', \'远志\', \'半两\', \'砂仁\', \'半两\', \'枳壳\', \'半两\', \'柴胡\', \'半两\', \'麻黄\', \'半两\', \'麝香\', \'半两\', \'乌梅\', \'知母\', \'升麻\', \'甘草\', \'鳖甲\', \'苁蓉\', \'白蔹\', \'葳蕤\', \'虎骨\', \'桃仁\', \'羌活\', \'防风\', \'茯苓\', \'附子\', \'青蒿\', \'秦艽\', \'厚朴\', \'牛膝\', \'半夏\', \'两桂\']');
INSERT INTO `prescript` VALUES (60496, '364', '黄连散', '黄连（去须）1两，升麻1两，龙胆1两。', '绵裹如弹子大，临卧以新汲水浸过，含化咽津。', '《圣济总录》卷一一八', '脾胃积热，风冷乘之，唇肿结核。', '-', '-', '升麻', '[\'黄连\', \'升麻\', \'龙胆\']');
INSERT INTO `prescript` VALUES (60497, '353', '黄柏散', '黄柏3分（锉），川升麻1两，石膏1两，犀角屑3分，玄参1两，甘草1两（生，锉），麦门冬1两半（去心，焙），牛蒡子半两（微炒）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓，温服，不拘时候。', '《圣惠》卷三十八', '乳石发动，头痛心烦，口舌生疮，干呕恶食。', '-', '-', '升麻', '[\'黄柏\', \'锉川\', \'升麻\', \'石膏\', \'犀角\', \'玄参\', \'甘草\', \'两生\', \'麦门冬\', \'两半\', \'牛蒡子\', \'半两\']');
INSERT INTO `prescript` VALUES (60498, '348', '解肌散', '麻黄1两（去根节），石膏2两，川升麻1两，甘草1两（炙微赤，锉），赤芍药1两，柴胡1两（去苗），桔梗1两（去芦头），杏仁1两（汤浸，去皮尖双仁，麸炒微黄）。', '每服5钱，以水1大盏，入生姜半分，煎至5分，去滓，不拘时候热服。衣覆取汗，未汗再服。', '《圣惠》卷十七', '热病1日，头痛身热，四肢烦疼。', '-', '-', '升麻', '[\'麻黄\', \'两去\', \'根节\', \'石膏\', \'两川\', \'升麻\', \'甘草\', \'两炙微\', \'芍药\', \'柴胡\', \'两去\', \'桔梗\', \'两去\', \'芦头\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (60499, '314', '解毒化热汤', '牛蒡1钱，赤芍1钱，连翘8分，归尾7分，桔梗6分，人中黄6分，升麻4分，干葛4分，防风4分，荆芥4分，红花5分，蝉蜕3分。', '加烧人粪末1钱调服。弱者，加人参6分。', '《种痘新书》卷十二', '痘稠密红紫者。', '-', '-', '升麻', '[\'牛蒡\', \'赤芍\', \'连翘\', \'分归尾分\', \'桔梗\', \'分人\', \'黄分\', \'升麻\', \'分干葛分\', \'防风\', \'荆芥\', \'红花\', \'蝉蜕\']');
INSERT INTO `prescript` VALUES (60500, '304', '解毒汤', '润元参1钱，净连翘1钱，绿升麻1钱，片黄芩1钱，京赤芍1钱，全当归1钱，川羌活1钱，北防风1钱，怀生地1钱，荆芥穗1钱，淮木通1钱，炙甘草5分。', '加灯心10茎，水煎，热服。大便秘，加酒大黄。', '《幼幼集成》卷四', '托毒。主小儿疮疥，误用搽洗，逼毒入腹。', '-', '-', '升麻', '[\'润元\', \'参钱净\', \'连翘\', \'升麻\', \'钱片\', \'黄芩\', \'钱京\', \'赤芍\', \'钱全\', \'当归\', \'钱川\', \'羌活\', \'钱北\', \'防风\', \'钱怀\', \'生地\', \'荆芥\', \'木通\', \'甘草\']');
INSERT INTO `prescript` VALUES (60501, '255', '蓝叶散', '蓝叶半两，人参半两（去芦头），知母半两，钩藤3分，玄参3分，川升麻3分，葛根3分（锉），子芩1分，犀角屑1分，射干1分。', '3-5岁儿，以竹沥半合，调半钱服，1日3次。', '《圣惠》卷八十五', '小儿头额体背俱热，大便秘涩，眼赤心闷，乍睡乍惊，精神昏浊，与人不相主，当欲作痫状。', '-', '-', '升麻', '[\'蓝叶\', \'半两\', \'人参\', \'半两\', \'芦头\', \'知母\', \'半两\', \'钩藤\', \'玄参\', \'分川\', \'升麻\', \'葛根\', \'锉子\', \'犀角\', \'射干\']');
INSERT INTO `prescript` VALUES (60502, '237', '葛根汤', '升麻1两，葛根2两，甘草2钱，半夏5钱，苏叶5钱，白芷5钱，丁皮5钱，川芎5钱，香附子5钱，陈皮5钱。', '每服2钱，入姜、葱煎，空心服之。', '《仙传外科集验方》卷六', '发散。主刀刃伤后发寒热，男女流注初发，潮热，红肿赤痛者。', '-', '-', '升麻', '[\'升麻\', \'葛根\', \'甘草\', \'半夏\', \'苏叶\', \'白芷\', \'钱丁皮\', \'川芎\', \'香附子\', \'陈皮\']');
INSERT INTO `prescript` VALUES (60503, '149', '漏芦汤', '漏芦2钱，升麻1钱半，大黄（酒浸，量轻重用之）、黄芩（酒洗）5分，生甘草1钱，蓝叶（如无，用青黛）1钱，黑云参1钱，牛蒡子（炒，研）1钱，苦梗1钱，连翘1钱。', '漏芦升麻汤（《景岳全书》卷六十四）。便结者，加芒消。', '《医统》卷二十五', '时毒，头面红肿，咽嗌堵塞，水药不下；及脏腑素有积热，发为肿毒疙瘩，一切红肿恶毒。', '-', '-', '升麻', '[\'漏芦\', \'升麻\', \'钱半大\', \'黄酒\', \'浸量\', \'轻重\', \'黄芩\', \'洗分\', \'生甘草\', \'蓝叶\', \'无用\', \'青黛钱\', \'黑云\', \'参钱\', \'牛蒡子\', \'研钱苦\', \'连翘\']');
INSERT INTO `prescript` VALUES (60504, '107', '干蓝汤', '干蓝叶1两，升麻1两，芍药1两，栀子仁4枚。', '百晬至二百日儿，每服1钱匕，以水半盏，加香豉7枚，薤白1茎（拍破），向煎至3分，去滓，食前分温3服。', '《圣济总录》卷一七八', '小儿百晬内下痢如鱼脑，赤白杂痢，腹痛多啼。', '-', '-', '升麻', '[\'干蓝叶\', \'升麻\', \'芍药\', \'栀子\', \'仁枚\']');
INSERT INTO `prescript` VALUES (60505, '104', '丹砂散', '朱砂3分（细研，水飞过），犀角屑半两，天竹黄半两，秦艽半两（去苗），白鲜皮半两，沙参半两（去芦头），寒水石1两，麦门冬2两（去心，焙干），马牙消半两（研入），川升麻半两，甘草半两（炙微赤，锉），龙脑1钱（研入）。', '每服1钱，以温水调下，不拘时候。', '《圣惠》卷二十三', '风热，心肺壅滞，时多烦闷。', '-', '-', '升麻', '[\'朱砂\', \'分细\', \'研水\', \'飞过\', \'犀角\', \'半两\', \'天竹黄\', \'半两\', \'秦艽\', \'半两\', \'去苗\', \'白鲜皮\', \'半两\', \'沙参\', \'半两\', \'芦头\', \'寒水石\', \'麦门冬\', \'两去\', \'焙干\', \'马牙消\', \'半两\', \'入川\', \'升麻\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'龙脑\', \'研入\']');
